    "contracts/RewardTokenContract.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.6;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

import "./interfaces/IPancakeRouter02.sol";

contract RewardTokenContract is Ownable {
    IERC20 token;
    IERC20 usdt;
    IPancakeRouter02 router;

    bool public swapping;

    address[] public shareholders;
    mapping(address => bool) public shared;
    mapping(address => uint256) public shareholderIndexes;
    uint256 public sharingIndex;
    uint256 public usdtPerUser;

    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 usdtReceived,
        uint256 tokensIntoLiqudity
    );

    constructor(address _token, address _usdt, address _router) {
        token = IERC20(_token);
        usdt = IERC20(_usdt);
        router = IPancakeRouter02(_router);
    }

    function checkSwap() external onlyOwner {
        if (swapping) {
            return;
        }

        swapping = true;

        uint256 tokenBalance = token.balanceOf(address(this));
        uint256 usdtBalance = usdt.balanceOf(address(this));

        if (tokenBalance >= 5000 * 10**18 && usdtBalance <= usdtPerUser) {
            uint256 forUBalance = tokenBalance * 2 / 8;
            swapTokensForUsdt(forUBalance);
            swapAndLiquify(tokenBalance - forUBalance);
            usdtPerUser = usdt.balanceOf(address(this)) / shareholders.length;
            sharingIndex = 0;
        }

        swapping = false;
    }

    function checkSharing() external onlyOwner {
        if (swapping) {
            return;
        }

        uint256 usdtBalance = usdt.balanceOf(address(this));
        uint8 counts = 5;
        while(counts > 0 && usdtBalance >= usdtPerUser) {
            if (sharingIndex >= shareholders.length) {
                sharingIndex = 0;
            }
            usdt.transfer(shareholders[sharingIndex], usdtPerUser);
            usdtBalance = usdt.balanceOf(address(this));
            counts--;
            sharingIndex++;
        }
    }

    function addShareholder(address shareholder) public onlyOwner {
        shareholderIndexes[shareholder] = shareholders.length;
        shareholders.push(shareholder);
        shared[shareholder] = true;
    }
    function removeShareholder(address shareholder) public onlyOwner {
        shareholders[shareholderIndexes[shareholder]] = shareholders[shareholders.length-1];
        shareholderIndexes[shareholders[shareholders.length-1]] = shareholderIndexes[shareholder];
        shareholders.pop();
        shared[shareholder] = false;
    }

    function swapAndLiquify(uint256 contractTokenBalance) private {
        // split the contract balance into halves
        uint256 half = contractTokenBalance / 2;
        uint256 otherHalf = contractTokenBalance - half;

        // capture the contract's current USDT balance.
        // this is so that we can capture exactly the amount of USDT that the
        // swap creates, and not make the liquidity event include any USDT that
        // has been manually sent to the contract
        uint256 initialBalance = usdt.balanceOf(address(this));

        // swap tokens for USDT
        swapTokensForUsdt(half); // <- this breaks the USDT -> HATE swap when swap+liquify is triggered

        // how much USDT did we just swap into?
        uint256 newBalance = usdt.balanceOf(address(this)) - initialBalance;

        // add liquidity to uniswap
        addLiquidity(otherHalf, newBalance);

        emit SwapAndLiquify(half, newBalance, otherHalf);
    }

    function swapTokensForUsdt(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> usdt
        address[] memory path = new address[](2);
        path[0] = address(token);
        path[1] = address(usdt);

        token.approve(address(router), tokenAmount);

        // make the swap
        router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of USDT
            path,
            address(this),
            block.timestamp
        );
    }

    function addLiquidity(uint256 tokenAmount, uint256 usdtAmount) private {
        // approve token transfer to cover all possible scenarios
        token.approve(address(router), tokenAmount);
        usdt.approve(address(router), usdtAmount);

        // add the liquidity
        router.addLiquidity(
            address(token),
            address(usdt),
            tokenAmount,
            usdtAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(this),
            block.timestamp
        );
    }

    function withdrawToken(IERC20 _token, address receipt, uint256 amount) external onlyOwner {
        _token.transfer(receipt, amount);
    }
}
