    "contracts/TokenSeller.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.6;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Enumerable.sol";

import "./UserRegistry.sol";

interface MintableToken is IERC20 {
    function mint(address account, uint256 amount) external;
}

interface MintableERC721 is IERC721Enumerable {
    function mint(address account) external;
}

contract TokenSeller is Ownable, UserRegistry {
    struct User {
        uint256 joinBalance; // usdt
        uint256 receiveBalance;
        uint256 inviteBalance;
        uint256 inviteTotal;
        uint256 groupTotal;

        bool isFans;
        bool isPlayer;
        bool isJudge;

        uint256 playerClaimCounts;
    }

    struct RankUser {
        address account;
        uint256 amount;
    }

    uint256 public constant TA_PER_USDT = 100;
    uint256 public constant FANS_USDT = 32 * 1e18;
    uint256 public constant PLAYER_USDT = 320 * 1e18;
    uint256 public constant JUDGE_USDT = 64000 * 1e18;
    address public constant BURN_ADDRESS = address(0x000000000000000000000000000000000000dEaD);

    IERC20 public ta;
    MintableERC721 public player;
    MintableERC721 public judge;
    IERC20 public usdt;

    mapping(address => User) public users;
    address public receiveAddr1;
    address public receiveAddr2;
    address public receiveAddr3;
    address public receiveAddr4;
    uint256 public initialSupply;
    uint256 public totalJoin;

    address[] _ranks;
    mapping(address => bool) _ranked;
    mapping(address => uint256) _ranksIndexes;

    bool isStarted = true;

    mapping(address => bool) public isLeader;

    event BecomeFans(address indexed account, address indexed recommender, uint256 usdtAmount, uint256 tokenAmount);
    event BecomePlayer(address indexed account, address indexed recommender, uint256 usdtAmount, uint256 tokenAmount);
    event BecomeJudge(address indexed account, uint256 usdtAmount);
    event Claim(address indexed account, uint256 amount);
    event ClaimPlayer(address indexed account);

    constructor(IERC20 _usdt,
        MintableERC721 _player,
        MintableERC721 _judge,
        address _receiveAddr1,
        address _receiveAddr2,
        address _receiveAddr3,
        address _receiveAddr4
    ) {
        usdt = _usdt;
        player = _player;
        judge = _judge;
        receiveAddr1 = _receiveAddr1;
        receiveAddr2 = _receiveAddr2;
        receiveAddr3 = _receiveAddr3;
        receiveAddr4 = _receiveAddr4;
    }

    function setReceiveAddr1(address account) external onlyOwner {
        receiveAddr1 = account;
    }
    function setReceiveAddr2(address account) external onlyOwner {
        receiveAddr2 = account;
    }
    function setReceiveAddr3(address account) external onlyOwner {
        receiveAddr3 = account;
    }
    function setReceiveAddr4(address account) external onlyOwner {
        receiveAddr4 = account;
    }

    function setStarted(bool state) external onlyOwner {
        isStarted = state;
    }

    function setTa(IERC20 addr) external onlyOwner {
        ta = addr;
    }

    function addLeaders(address[] calldata addrs) external onlyOwner {
        for (uint256 i = 0; i < addrs.length; i++) {
            isLeader[addrs[i]] = true;
        }
    }

    function removeLeaders(address[] calldata addrs) external onlyOwner {
        for (uint256 i = 0; i < addrs.length; i++) {
            isLeader[addrs[i]] = false;
        }
    }

    function withdrawToken(IERC20 token, address receipt, uint256 amount) external onlyOwner {
        token.transfer(receipt, amount);
    }

    function canInvite(address account) public view returns(bool) {
        return isLeader[account] || (account != address(0) && users[account].joinBalance > 0);
    }

    function join(address recommender_) public returns(address) {
        recommender_ = _join(msg.sender, recommender_);
        require(canInvite(recommender_), "Not invite");
        return recommender_;
    }

    function becomeFans(address recommender_) external {
        require(isStarted, "Not started");
        require(!users[msg.sender].isFans , "already buyed");
        users[msg.sender].isFans = true;

        recommender_ = join(recommender_);

        uint256 receiveAmount = _swapUsdt(msg.sender, FANS_USDT);

        _updateRecommender(msg.sender, receiveAmount);
        _updateGroup(msg.sender, receiveAmount, 10);

        if (users[msg.sender].isPlayer && users[msg.sender].isFans) {
            player.mint(msg.sender);
        }

        emit BecomeFans(msg.sender, recommender_, FANS_USDT, receiveAmount);
    }

    function becomePlayer(address recommender_) external {
        require(isStarted, "Not started");
        require(!users[msg.sender].isPlayer, "already buyed");
        users[msg.sender].isPlayer = true;

        recommender_ = join(recommender_);

        uint256 receiveAmount = _swapUsdt(msg.sender, PLAYER_USDT);

        _updateRecommender(msg.sender, receiveAmount);
        _updateGroup(msg.sender, receiveAmount, 10);

        if (users[msg.sender].isPlayer && users[msg.sender].isFans) {
            player.mint(msg.sender);
        }

        emit BecomePlayer(msg.sender, recommender_, FANS_USDT, receiveAmount);
    }

    function becomeJudge() external {
        require(isStarted, "Not started");
        require(judge.totalSupply() < 10, "Sold out");
        require(!users[msg.sender].isJudge, "already buyed");
        users[msg.sender].isJudge = true;

        judge.mint(msg.sender);

        emit BecomeJudge(msg.sender, JUDGE_USDT);
    }

    function claim() external {
        require(!isStarted, "require end");

        uint256 receiveBalance = users[msg.sender].receiveBalance;
        uint256 inviteBalance = users[msg.sender].inviteBalance;
        users[msg.sender].receiveBalance = 0;
        users[msg.sender].inviteBalance = 0;

        ta.transfer(msg.sender, receiveBalance + inviteBalance);

        emit Claim(msg.sender, receiveBalance + inviteBalance);
    }

    function claimPlayer(uint256 counts) external {
        require(users[msg.sender].joinBalance > 0, "Not joined");
        require(counts < 10, "max counts is 10");
        require(childrenLength(msg.sender) > users[msg.sender].playerClaimCounts + counts, "no chance");

        for (uint256 i = 0; i < counts; i++) {
            uint256 random = uint256(keccak256(abi.encodePacked(childrenLength(msg.sender), block.timestamp, msg.sender, users[msg.sender].playerClaimCounts, block.difficulty, blockhash(block.number - 32 + i)))) % 100;
            users[msg.sender].playerClaimCounts++;

            if (random < 2) {
                player.mint(msg.sender);
                emit ClaimPlayer(msg.sender);
            }
        }
    }

    function getRank() public view returns(RankUser[] memory rankUser) {
        rankUser = new RankUser[](_ranks.length);

        for (uint256 i = 0; i < _ranks.length; i++) {
            rankUser[i] = RankUser(_ranks[i], users[_ranks[i]].inviteTotal);
        }
    }

    function _swapUsdt(address account, uint256 usdtAmount) private returns (uint256 tokenAmount) {
        tokenAmount = usdtAmount * TA_PER_USDT;

        User storage user = users[msg.sender];

        user.joinBalance += usdtAmount;
        user.receiveBalance += tokenAmount;
        usdt.transferFrom(
            account,
            address(this),
            usdtAmount
        );
        usdt.transfer(receiveAddr1, usdtAmount * 5 / 100);
        usdt.transfer(receiveAddr2, usdtAmount * 10 / 100);
        usdt.transfer(receiveAddr3, usdtAmount * 30 / 100);
        usdt.transfer(receiveAddr4, usdtAmount * 50 / 100);
        totalJoin += usdtAmount;
        initialSupply += tokenAmount;
    }

    function _addRank(address addr) private {
        _ranksIndexes[addr] = _ranks.length;
        _ranks.push(addr);
        _ranked[addr] = true;
    }
    function _removeRank(address addr) private {
        _ranks[_ranksIndexes[addr]] = _ranks[_ranks.length-1];
        _ranksIndexes[_ranks[_ranks.length-1]] = _ranksIndexes[addr];
        _ranks.pop();
        _ranked[addr] = false;
    }

    function _updateRecommender(address account, uint256 amount) private {
        address recommender_ = _recommender[account];
        if (recommender_ == address(0)) return;
        User storage recommenderUser = users[recommender_];
        recommenderUser.inviteTotal += amount;

        if (!recommenderUser.isPlayer) return;

        recommenderUser.inviteBalance += amount / 10;
        initialSupply += amount / 10;

        if (_ranked[recommender_]) return;
        if (_ranks.length < 50) {
            _addRank(recommender_);
        } else {
            address minAddr = _ranks[0];
            for (uint256 i = 1; i < _ranks.length; i++) {
                if (users[_ranks[i]].inviteTotal < users[minAddr].inviteTotal) {
                    minAddr = _ranks[i];
                }
            }
            if (users[minAddr].inviteTotal < recommenderUser.inviteTotal) {
                _removeRank(minAddr);
                _addRank(recommender_);
            }
        }
    }

    function _updateGroup(address account, uint256 amount, uint8 step) private {
        if (step == 0) return;
        address recommender_ = _recommender[account];
        if (recommender_ == address(0)) return;
        User storage recommenderUser = users[recommender_];
        recommenderUser.groupTotal += amount;
        _updateGroup(recommender_, amount, step - 1);
    }
}
"
    },
