    "contracts/Judge.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.6;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract Judge is ERC721Enumerable, Ownable {
    mapping(address => bool) private _operators;
    string private baseURI;
    IERC20 private _ta;

    uint256 public rewardPerToken;
    mapping(uint256 => uint256) public claimed; // tokenId => claimed

    modifier onlyOperator() {
        require(_operators[msg.sender] || msg.sender == owner(), "UserRegistry: caller is not the operator or owner");
        _;
    }

    constructor() ERC721("TA Judge", "TAJ") {
    }


    function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }

    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        return super.tokenURI(tokenId);
    }

    function claim() external {
        for (uint256 i = 0; i < balanceOf(msg.sender); i++) {
            uint256 tokenId = tokenOfOwnerByIndex(msg.sender, i);
            if (claimed[tokenId] < rewardPerToken) {
                _ta.transfer(msg.sender, rewardPerToken - claimed[tokenId]);
                claimed[tokenId] = rewardPerToken;
            }
        }
    }

    function mint(address account) external onlyOperator {
        uint256 tokenId = totalSupply();
        _safeMint(account, tokenId);
        claimed[tokenId] = rewardPerToken;
    }

    function notifyRewardAmount(uint256 amount) external onlyOperator {
        rewardPerToken += amount / totalSupply();
    }

    function setBaseURI(string calldata uri) external onlyOwner {
        baseURI = uri;
    }

    function addOperators(address[] calldata accounts) external onlyOwner {
        for (uint256 i = 0; i < accounts.length; i++) {
            _operators[accounts[i]] = true;
        }
    }

    function removeOperators(address[] calldata accounts) external onlyOwner {
        for (uint256 i = 0; i < accounts.length; i++) {
            _operators[accounts[i]] = false;
        }
    }

    function setToken(IERC20 token) external onlyOwner {
        _ta = token;
    }
}
"
    },
