// File contracts/dkv2.sol

// Original license: SPDX_License_Identifier: MIT
pragma solidity ^0.8.19;







interface IPancakePair {
    function totalSupply() external view returns (uint256);

    function balanceOf(address owner) external view returns (uint256);

    function getReserves()
        external
        view
        returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
}

interface ISwapRouter {
    function factory() external pure returns (address);

    function WETH() external pure returns (address);

    function swapExactTokensForTokensSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
}

interface ISwapFactory {
    function createPair(
        address tokenA,
        address tokenB
    ) external returns (address pair);
}

interface NFTDividend {
    function process() external;
}

interface LPDividend is NFTDividend {
    function add(address tokenA) external;
}

contract TokenDistributor {
    constructor(address token) {
        ERC20(token).approve(msg.sender, uint(~uint256(0)));
    }
}

contract DragonKing is ERC20, Ownable {
    using SafeMath for uint256;
    using Address for address;
    using EnumerableMap for EnumerableMap.AddressToUintMap;

    address DEAD;
    LPDividend public _LPDividend;
    NFTDividend public _NFTDividend;
    bool public DividendFlag;
    uint256 public startTradeBlock;

    mapping(address => bool) public _feeWhiteList;
    mapping(address => address) public _invitor;
    mapping(address => EnumerableMap.AddressToUintMap) private _children;
    uint256 public _tTotal;

    ISwapRouter public _swapRouter;
    bool public inSwap;
    uint256 public numTokensSellToFund;

    uint256 private constant MAX = ~uint256(0);
    ERC20 public USDT;
    TokenDistributor public _tokenDistributor;
    uint256 public _buyFee;
    uint256 public _nftFee;
    uint256 public _lpFee;

    IPancakePair public _usdtPair;
    uint256 public _invitorAmount;
    uint256 public _invitorLimitAmount;
    uint256 public percentageLimit;

    modifier lockTheSwap() {
        inSwap = true;
        _;
        inSwap = false;
    }

    constructor() ERC20("DK", "DK") {
        DEAD = 0x000000000000000000000000000000000000dEaD;
        _swapRouter = ISwapRouter(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        USDT = ERC20(0x55d398326f99059fF775485246999027B3197955);
        super._approve(address(this), address(_swapRouter), MAX);

         _mint(_msgSender(), 300000000000 * 10 ** decimals());
        _tTotal = balanceOf(_msgSender());

        _tokenDistributor = new TokenDistributor(address(USDT));
        super._transfer(
            _msgSender(),
            address(_tokenDistributor),
            _tTotal.mul(10).div(100)
        ); //10%trade
        super._approve(address(_tokenDistributor), address(this), MAX);
        _feeWhiteList[address(this)] = true;
        _feeWhiteList[address(_tokenDistributor)] = true;
        _feeWhiteList[address(_swapRouter)] = true;
        _feeWhiteList[_msgSender()] = true;

        numTokensSellToFund = 200 * 10 ** USDT.decimals(); //200U
        _buyFee = 4; //4%
        _nftFee = 4;
        _lpFee = 2;
        _invitorAmount = 1 * 10 ** (decimals() - 1); //0.1
        _invitorLimitAmount = 200 * 10 ** USDT.decimals(); //200u
        percentageLimit = 15; //15%
    }

    function checkAddressActive(address from, address to) public {
        address addr = from;
        for (uint256 index = 0; index < 2; index++) {
            if (index == 1) {
                addr = to;
            }
            address parent = _invitor[addr];
            if (parent == address(0)) {
                continue;
            }
            if (balanceValueOfUsdt(addr) < _invitorLimitAmount) {
                _children[parent].remove(addr);
                continue;
            }
            _children[parent].set(addr, 0);
        }
    }

    function checkTradeAmount(uint256 amount) public view returns (bool) {
        (uint256 res0, uint256 res1, ) = _usdtPair.getReserves();
        uint256 k = res0.mul(res1);
        uint256 res0Amount = k.div((res1.add(amount)));
        uint256 pct = ((res0.sub(res0Amount)).mul(100)).div(res0);
        return pct < percentageLimit;
    }

    function setPercentageLimit(uint256 percentage) external onlyOwner {
        percentageLimit = percentage;
    }

    function transfer(
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        address spender = _msgSender();
        _spendAllowance(sender, spender, amount);
        _transfer(sender, recipient, amount);
        return true;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        if (address(_usdtPair) == from || address(_usdtPair) == to) {
            if (0 == startTradeBlock) {
                require(_feeWhiteList[from] || _feeWhiteList[to], "!Trading");
                startTradeBlock = block.number;
            }
            if (_feeWhiteList[from]) {
                super._transfer(from, to, amount);
                return;
            }
            if (!_feeWhiteList[from] && address(_usdtPair) == to && !inSwap) {
                require(checkTradeAmount(amount), "price impact high");
            }
            if (
                address(_usdtPair) == to &&
                balanceValueOfUsdt(address(this)) >= numTokensSellToFund &&
                !inSwap
            ) {
                swapTokenForFund(balanceOf(address(this)));
            }
        } else {
            if (
                amount == _invitorAmount &&
                balanceValueOfUsdt(to) >= _invitorLimitAmount &&
                address(0) == _invitor[from] &&
                !_feeWhiteList[to] &&
                _invitor[to] != from
            ) {
                _invitor[from] = to;
            }
            super._transfer(from, to, amount);
            checkAddressActive(from, to);
            return;
        }

        if (address(_usdtPair) == from) {
            _LPDividend.add(to);
        } else if (_usdtPair.balanceOf(from) > 0) {
            _LPDividend.add(from);
        }

        _tokenTransfer(from, to, amount);
        checkAddressActive(from, to);

        if (from != address(this) && startTradeBlock > 0) {
            if (!DividendFlag) {
                _NFTDividend.process();
            } else {
                _LPDividend.process();
            }
            DividendFlag = !DividendFlag;
        }
    }

    function _tokenTransfer(
        address sender,
        address recipient,
        uint256 tAmount
    ) private {
        uint256 taxAmount;
        //交易
        //上下级提成
        address current;
        if (address(address(_usdtPair)) == sender) {
            //买入，
            current = recipient;
            taxAmount = tAmount.mul(_buyFee).div(100); //4%
            super._transfer(sender, DEAD, taxAmount);

                if (super.balanceOf(address(_tokenDistributor)) > 0) {
                uint256 inviterAmount;
                uint256 currentLevel = 0;
                for (uint256 i; i < 5; ++i) {
                    address inviter = _invitor[current];
                    if (address(0) == inviter) {
                        break;
                    }
                    current = inviter;
                    if (
                        balanceValueOfUsdt(inviter) < _invitorLimitAmount ||
                        _children[inviter].length() < i + 1
                    ) {
                        continue;
                    }
                    if (0 == currentLevel) {
                        inviterAmount = tAmount.mul(4).div(100);
                    } else if (1 == currentLevel) {
                        inviterAmount = tAmount.mul(2).div(100);
                    } else {
                        inviterAmount = tAmount.mul(1).div(100);
                    }
                    super._transfer(
                        address(_tokenDistributor),
                        inviter,
                        inviterAmount
                    );
                    current = inviter;
                    currentLevel++;
                }
                }

        } else {
            //卖出
            current = sender;
            taxAmount = tAmount.mul(_nftFee + _lpFee).div(100); //6%
            super._transfer(sender, address(this), taxAmount);
        }

        //接收者增加余额
        super._transfer(sender, recipient, tAmount - taxAmount);
    }

    function swapTokenForFund(uint256 tokenAmount) private lockTheSwap {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = address(USDT);
        _swapRouter.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(_tokenDistributor),
            block.timestamp
        );

        //按照比例分配
        uint256 usdtBalance = USDT.balanceOf(address(_tokenDistributor));
        USDT.transferFrom(
            address(_tokenDistributor),
            address(_NFTDividend),
            usdtBalance.mul(_nftFee).div(_nftFee.add(_lpFee))
        );
        USDT.transferFrom(
            address(_tokenDistributor),
            address(_LPDividend),
            usdtBalance.mul(_lpFee).div(_nftFee.add(_lpFee))
        );
    }

    function setTradeAddress(address addr) external onlyOwner {
        _tokenDistributor = TokenDistributor(addr);
        _feeWhiteList[addr] = true;
    }

    function setLPDividend(address addr) external onlyOwner {
        _LPDividend = LPDividend(addr);
    }

    function setSwapPair(address addr) external onlyOwner {
        _usdtPair = IPancakePair(addr);
    }

    function removeSwapPair() external onlyOwner {
        _usdtPair = IPancakePair(address(0x0));
    }

    function setNFTDividend(address addr) external onlyOwner {
        _NFTDividend = NFTDividend(addr);
    }

    function setFundSellAmount(uint256 amount) external onlyOwner {
        numTokensSellToFund = amount;
    }

    function balanceValueOfUsdt(address account) public view returns (uint256) {
        if (
            address(0) == address(address(_usdtPair)) ||
            _usdtPair.totalSupply() == 0
        ) {
            return 0;
        }
        (uint256 reserve0, uint256 reserve1, ) = _usdtPair.getReserves();
        return balanceOf(account).mul(reserve0).div(reserve1);
    }

    receive() external payable {}

    function getInviter(address account) external view returns (address) {
        return _invitor[account];
    }

    function setDividendFlag(bool flag) external onlyOwner {
        DividendFlag = flag;
    }

    function getMyDirectChildren(
        address addr
    ) public view returns (address[] memory) {
        uint256 count = _children[addr].length();
        address[] memory data = new address[](count);
        for (uint256 index = 0; index < count; index++) {
            (address tmpAddress, ) = _children[addr].at(index);
            data[index] = tmpAddress;
        }
        return data;
    }
}
