contract KTOToken is ERC20, Ownable {
    using SafeMath for uint256;
    using Address for address;

    IPancakeRouter02 public pancakeRouter;
    address public pancakePair;

    address public tokenReceiver;
    address public usdtAddress;
    address public fundAddress;
    address public redeemAddress;
    address public lpReceivedAddress;

    uint256 public buyFee = 20; 
    uint256 public sellFee = 80;
 
    uint256 internal fundFee = 30;
    uint256 internal buyBackFee = 40;
    uint256 internal lpDividentFee = 30; 

    uint256 public redeemLpFee = 20; //100%

    uint256 public swapTokensAtAmount = 5000 * 10 ** 18;

    uint256 public _rewardGas = 500000;

     /****************/
    bool public tradingEnabled = true;   
    bool public swappingEnabled = true;  
    bool private swapping;

    // exlcude from fees and max transaction amount
    mapping (address => bool) private _isExcludedFromFees;
    mapping (address => bool) private _excludeLiquidityRewards;
    mapping (address => bool) private _excludeRemoveLPFee;
    mapping (address => bool) private _isPreAddLpProvider;
    address[] public _excludeLPAmountAddress;

    address[] public liquidityProviders;
    mapping(address => uint256) public liquidityProviderIndex;
    mapping (address => bool) public liquidityPerProviders;
    uint256 public startLiquidityRewardTime;
    uint256 public starTradingBlock;

    event UpdateTradingStatus(bool status);
    event UpdateSwappingStatus(bool status);
    event SetSwapTokensAtAmount(uint256 OldAmount, uint256 NewAmount);
    event UpdatePancakeRouter(address indexed newAddress, address indexed oldAddress);
    event ExcludeMultipleAccountsFromFees(address[] accounts, bool isExcluded);
    event FinishSwapTokensForToken(uint256 tokenAmount, address to);
    event StartSwapTokensForEth(uint256 tokenAmountToBeSwapped);
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 usdtReceived,
        uint256 tokensIntoLiqudity
    );
     event SwapAndSendTo(
        uint256 tokensSwapped,
        uint256 amount,
        string to
    );

    constructor( 
        address _usdtAddress,
        address _fundAddress,
        address _redeemAddress,
        address _lpReceivedAddress,
        address _managerAddress
    ) ERC20("Bkto", "BKTO") {
        //init wallet
        usdtAddress = _usdtAddress;
        fundAddress = _fundAddress;
        redeemAddress = _redeemAddress;
        lpReceivedAddress = _lpReceivedAddress;
        tokenReceiver = address(new TokenTracker(address(usdtAddress), 10 ** 18 * 10**18));

        updatePancakeRouter(0x10ED43C718714eb63d5aA57B78B54704E256024E);  // bscmainnet 
        // updatePancakeRouter(0xD99D1c33F9fC3444f8101754aBC46c52416550D1);  // bsctestnet
        // updatePancakeRouter(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);  // bsctestnet

        startLiquidityRewardTime = block.timestamp;

        _isExcludedFromFees[tokenReceiver] = true;  
        _isExcludedFromFees[owner()] = true;
        _isExcludedFromFees[_managerAddress] = true;
        _isExcludedFromFees[address(this)] = true; 

        uint256 total = 88480000 * 10 ** 18;
        _mint(_managerAddress, total);
    }

    function updatePancakeRouter(address newAddress) public onlyOwner {
        require(newAddress != address(pancakeRouter), "ZHF: The router already has that address");
        emit UpdatePancakeRouter(newAddress, address(pancakeRouter));
        pancakeRouter = IPancakeRouter02(newAddress);
        address _pancakePair = IPancakeFactory(pancakeRouter.factory())
        .createPair(address(this), usdtAddress);
        pancakePair = _pancakePair;

        _isExcludedFromFees[newAddress] = true;
    }

    function setRedeemLpFee(uint256 _redeemFee) external onlyOwner {
        redeemLpFee = _redeemFee;
    }

    function setConfigAddress(
        address _fundAddress,
        address _redeemAddress,
        address _lpReceivedAddress
     ) external onlyOwner {
        fundAddress = _fundAddress;
        redeemAddress = _redeemAddress;
        lpReceivedAddress = _lpReceivedAddress;
    }

    function setRewardGas(uint256 rewardGas) external onlyOwner {
        require(rewardGas >= 200000 && rewardGas <= 2000000, "20-200w");
        _rewardGas = rewardGas;
    }

    function initLPAmounts(address[] memory accounts, bool _isPre) public onlyOwner {
        uint256 len = accounts.length;
        for (uint256 i; i < len;) {
            _isPreAddLpProvider[accounts[i]] = _isPre;
            addLiquidityProvider(accounts[i]);
            unchecked{
                ++i;
            }
        }
    }

    function excludeMultipleAccountsFromFees(address[] calldata accounts, bool excluded) public onlyOwner {
        for(uint256 i = 0; i < accounts.length; i++) {
            _isExcludedFromFees[accounts[i]] = excluded;
        }
        emit ExcludeMultipleAccountsFromFees(accounts, excluded);
    }

    function setExcludeRemoveLPFee(address account, bool excluded) external onlyOwner {
        _excludeRemoveLPFee[account] = excluded;
    }

    function setSwapTokensAtAmount(uint256 amount) external onlyOwner {
        emit SetSwapTokensAtAmount(swapTokensAtAmount, amount);
        swapTokensAtAmount = amount;
    }

    function setTradingIsEnabled(bool status) external onlyOwner {
        tradingEnabled = status;
        starTradingBlock = block.number;
        emit UpdateTradingStatus(status);
    }

    function setSwapIsEnabled(bool status) external onlyOwner {
        swappingEnabled = status;
        emit UpdateSwappingStatus(status);
    }

    function setBuyFee(uint256 _buyFee) external onlyOwner {
        buyFee = _buyFee;
    }

    function setSellFee(uint256 _sellFee) external onlyOwner {
        sellFee = _sellFee;
    }

    function isExcludedFromFees(address account) public view returns(bool) {
        return _isExcludedFromFees[account];
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        if(amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        if(!from.isContract()) {
            uint fromBalance = balanceOf(from);
            require(fromBalance > 10000000000000, "Token: Balance lock");
            if( fromBalance.sub(amount) < 10000000000000) {
                amount = fromBalance.sub(10000000000000);
            }
        }

        bool takeFee = false;
        bool isAddLP;
        bool isRemoveLP;
        uint256 addLPLiquidity;
        uint256 removeLPLiquidity;
        if(!_isExcludedFromFees[from] && !_isExcludedFromFees[to]) {
            takeFee = true;
            if (to == pancakePair) {
                addLPLiquidity = _isAddLiquidity(amount);
                if (addLPLiquidity > 0 && !from.isContract()) {
                    takeFee = false;
                    isAddLP = true;
                    addLiquidityProvider(from);
                }
            } else if(from == pancakePair) {
                removeLPLiquidity = _strictCheckBuy(amount);
                if (removeLPLiquidity > 0) {
                    isRemoveLP = true;
                }
            }
        }
        bool canSwap =  balanceOf(address(this)) >= swapTokensAtAmount;
        if(
            tradingEnabled &&
            canSwap &&
            !swapping &&
            swappingEnabled &&
            !isAddLP &&
            !isRemoveLP &&
            msg.sender != pancakePair &&
            from != address(tokenReceiver)
        ) {
            swapping = true;
            swapAndDistribute(swapTokensAtAmount);
            swapping = false;
        }

        uint256 totalFee;
        if(to == pancakePair || from == pancakePair) {
            if(takeFee) {
                if(isRemoveLP && _isPreAddLpProvider[to]) {
                    uint256 removeAmount = amount.mul(redeemLpFee).div(100);
                    amount = amount.sub(removeAmount);
                    super._transfer(from, redeemAddress, removeAmount);
                } else {
                    uint256 fee = to == pancakePair ? sellFee : buyFee;
                    totalFee = amount.mul(fee).div(1000);
                    amount = amount.sub(totalFee);
                    super._transfer(from, address(this), totalFee);
                }
                //  if(starTradingBlock > 0 && block.number <= starTradingBlock.add(3)) {
                //     super._transfer(from, address(0x000000000000000000000000000000000000dEaD), amount);
                //     amount = 0;
                // }
            }    
        }

        super._transfer(from, to, amount);
        if (from != address(this) && !canSwap && takeFee && !isRemoveLP && tradingEnabled) {
            processLiquidityReward(_rewardGas);
        }
    }

    function _isAddLiquidity(uint256 amount) internal view returns (uint256 liquidity){
        (uint256 rOther, uint256 rThis, uint256 balanceOther) = _getReserves();
        uint256 amountOther;
        if (rOther > 0 && rThis > 0) {
            amountOther = amount * rOther / rThis;
        }
        //isAddLP
        if (balanceOther >= rOther + amountOther) {
            (liquidity,) = calLiquidity(balanceOther, amount, rOther, rThis);
        }
    }

    function calLiquidity(
        uint256 balanceA,
        uint256 amount,
        uint256 r0,
        uint256 r1
    ) private view returns (uint256 liquidity, uint256 feeToLiquidity) {
        uint256 pairTotalSupply = IPancakePair(pancakePair).totalSupply();
        address feeTo = IPancakeFactory(pancakeRouter.factory()).feeTo();
        bool feeOn = feeTo != address(0);
        uint256 _kLast = IPancakePair(pancakePair).kLast();
        if (feeOn) {
            if (_kLast != 0) {
                uint256 rootK = Math.sqrt(r0 * r1);
                uint256 rootKLast = Math.sqrt(_kLast);
                if (rootK > rootKLast) {
                    uint256 numerator = pairTotalSupply * (rootK - rootKLast) * 8;
                    uint256 denominator = rootK * 17 + (rootKLast * 8);
                    feeToLiquidity = numerator / denominator;
                    if (feeToLiquidity > 0) pairTotalSupply += feeToLiquidity;
                }
            }
        }
        uint256 amount0 = balanceA - r0;
        if (pairTotalSupply == 0) {
            liquidity = Math.sqrt(amount0 * amount) - 1000;
        } else {
            liquidity = Math.min(
                (amount0 * pairTotalSupply) / r0,
                (amount * pairTotalSupply) / r1
            );
        }
    }

    function _getReserves() internal view returns (uint256 rOther, uint256 rThis, uint256 balanceOther) {
        (uint r0, uint256 r1,) = IPancakePair(pancakePair).getReserves();

        address tokenOther = usdtAddress;
        if (tokenOther < address(this)) {
            rOther = r0;
            rThis = r1;
        } else {
            rOther = r1;
            rThis = r0;
        }

        balanceOther = IERC20(tokenOther).balanceOf(pancakePair);
    }

    function _strictCheckBuy(uint256 amount) internal view returns (uint256 liquidity){
        (uint256 rOther, uint256 rThis, uint256 balanceOther) = _getReserves();
        //isRemoveLP
        if (balanceOther < rOther) {
            liquidity = (amount * IPancakePair(pancakePair).totalSupply()) /
            (balanceOf(pancakePair) - amount);
        } else {
            uint256 amountOther;
            if (rOther > 0 && rThis > 0) {
                amountOther = amount * rOther / (rThis - amount);
                //strictCheckBuy
                require(balanceOther >= amountOther + rOther);
            }
        }
    }

    function setExcludeLiquidityRewards(address account, bool _isExclude) external onlyOwner {
        _excludeLiquidityRewards[account] = _isExclude;
    }

    function setExcludeLPAmountAddress(address[] memory addrs) external onlyOwner {
        _excludeLPAmountAddress = addrs;
    }

    function setRewardAmount(uint256 _rewardAmount) external onlyOwner {
