    uint256 public _currentRewardLiquidityIndex;
    uint256 public _progressBlock;
    uint256 public _progressBlockDebt = 10;
    mapping(address => uint256) public _lastRewardLiquidityRewardTimes;
    uint256 public rewardAmount = 500 * 10 ** 18;
    uint256 public minLPAmountRequired = 1 * 10 ** 18;

    function processLiquidityReward(uint256 gas) public {
        if (block.timestamp <= startLiquidityRewardTime) {
            return;
        }
        if (_progressBlock + _progressBlockDebt > block.number) {
            return;
        }

        uint totalSupplyCirculation= totalSupplyLPCirculation();
        if (totalSupplyCirculation == 0) {
            return;
        }

        uint256 rewardCondition = rewardAmount;
        if(rewardCondition <= 0) {
            return;
        }
        uint256 thisBalance = IERC20(usdtAddress).balanceOf(address(this)); 
        if (thisBalance < rewardCondition) {
            return;
        }

        address shareHolder;
        uint256 liquidityBalance;
        uint256 perRewardAmount;

        uint256 shareholderCount = getLiquidityProviderLength();

        uint256 gasUsed = 0;
        uint256 iterations = 0;
        uint256 gasLeft = gasleft();
        uint256 holdMinRequiredAmount = minLPAmountRequired;
        uint256 blockTime = block.timestamp;

        while (gasUsed < gas && iterations < shareholderCount) {
            if (_currentRewardLiquidityIndex >= shareholderCount) {
                _currentRewardLiquidityIndex = 0;
            }
            shareHolder = liquidityProviders[_currentRewardLiquidityIndex];
            if (!_excludeLiquidityRewards[shareHolder]) {
                liquidityBalance = IERC20(pancakePair).balanceOf(shareHolder);
                if (liquidityBalance >= holdMinRequiredAmount) {
                    perRewardAmount = rewardCondition * liquidityBalance / totalSupplyCirculation;
                    if (perRewardAmount > 0) {
                        IERC20(usdtAddress).transfer(shareHolder, perRewardAmount);
                        _lastRewardLiquidityRewardTimes[shareHolder] = blockTime;
                    }
                }
            }

            gasUsed = gasUsed + (gasLeft - gasleft());
            gasLeft = gasleft();
            _currentRewardLiquidityIndex++;
            iterations++;
        }
        _progressBlock = block.number;
    }

    function totalSupplyLPCirculation() public view returns(uint256) {
        uint256 excludeLPAmount;
        if(_excludeLPAmountAddress.length > 0) {
            for(uint256 i = 0; i < _excludeLPAmountAddress.length; i++) {
                excludeLPAmount += IERC20(pancakePair).balanceOf(_excludeLPAmountAddress[i]);
            }
        }
        uint256 totalSupplyLPAmount = IERC20(pancakePair).totalSupply();
        return totalSupplyLPAmount.sub(excludeLPAmount);
    }

    function getLiquidityProviderLength() public view returns (uint256){
        return liquidityProviders.length;
    }

    function addLiquidityProvider(address account) private  {
        if (liquidityProviderIndex[account] == 0) {
            if (liquidityProviders.length == 0 || liquidityProviders[0] != account) {
                uint256 size;
                assembly {size := extcodesize(account)}
                if (size > 0) {
                    return;
                }
                liquidityProviderIndex[account] = getLiquidityProviderLength();
                liquidityProviders.push(account);
            }
        }
    }
    receive() external payable { }
    function swapAndDistribute(uint256 amount) private  {
        uint256 buybackAmount = amount.mul(buyBackFee).div(100);
        uint256 swapAmount = amount.sub(buybackAmount);
        swapTokensToToken(swapAmount,tokenReceiver);
        uint256 swapBalance = IERC20(usdtAddress).balanceOf(address(tokenReceiver));
        IERC20(usdtAddress).transferFrom(address(tokenReceiver), address(this), swapBalance);
        uint256 lpDividentAmount = swapBalance.mul(lpDividentFee).div(lpDividentFee.add(buyBackFee));
        uint256 fundAmount = swapBalance.sub(lpDividentAmount);
        IERC20(usdtAddress).transfer(fundAddress, fundAmount);
        swapAndLiquify(buybackAmount);
    }

    function swapTokensToToken(uint256 tokenAmount, address to) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = usdtAddress;

        _approve(address(this), address(pancakeRouter), tokenAmount);
        pancakeRouter.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            to,
            block.timestamp
        );
        emit FinishSwapTokensForToken(tokenAmount, to);
    }

    function swapAndLiquify(uint256 contractTokenBalance) private  {
        
        uint256 half = contractTokenBalance.div(2);
        uint256 otherHalf = contractTokenBalance.sub(half);

        swapTokensToToken(half,tokenReceiver); 
        uint256 newBalance = IERC20(usdtAddress).balanceOf(tokenReceiver);
        IERC20(usdtAddress).transferFrom(tokenReceiver,address(this), newBalance);

        addLiquidity(otherHalf, newBalance);
        emit SwapAndLiquify(half, newBalance, otherHalf);
    }

    function addLiquidity(uint256 tokenAmount, uint256 usdtAmount) private {
        _approve(address(this), address(pancakeRouter), tokenAmount);
        IERC20(usdtAddress).approve(address(pancakeRouter), usdtAmount);
        pancakeRouter.addLiquidity(
            address(this),
            usdtAddress,
            tokenAmount,
            usdtAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(lpReceivedAddress),
            block.timestamp
        );
    }
}
