contract sevenBc_Token is IERC20, Ownable {
    using SafeMath for uint256;

    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;
    mapping(address => bool) private _isExcludedFromFee;

    uint256 private _FeeTotal;

    string private _name = "7BC-Token";
    string private _symbol = "7BC";
    uint8 private _decimals = 18;

    uint256 public _Fee = 100;
    uint256 private _previousFee;

    uint256 currentIndex;
    uint256 private _totalSupply = 21000000 * 10 ** 18;

    constructor() {
        _balances[msg.sender] = _totalSupply;
        //exclude owner and this contract from fee
        _isExcludedFromFee[msg.sender] = true;
        _isExcludedFromFee[address(this)] = true;
        operator[msg.sender] = true;
        emit Transfer(address(0), msg.sender, _totalSupply);
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint256) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function transfer(
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function allowance(
        address owner,
        address spender
    ) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(
        address spender,
        uint256 amount
    ) public override returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            msg.sender,
            _allowances[sender][msg.sender].sub(
                amount,
                "ERC20: transfer amount exceeds allowance"
            )
        );
        return true;
    }

    function increaseAllowance(
        address spender,
        uint256 addedValue
    ) public virtual returns (bool) {
        _approve(
            msg.sender,
            spender,
            _allowances[msg.sender][spender].add(addedValue)
        );
        return true;
    }

    function decreaseAllowance(
        address spender,
        uint256 subtractedValue
    ) public virtual returns (bool) {
        _approve(
            msg.sender,
            spender,
            _allowances[msg.sender][spender].sub(
                subtractedValue,
                "ERC20: decreased allowance below zero"
            )
        );
        return true;
    }

    function totalFees() public view returns (uint256) {
        return _FeeTotal;
    }

    function isExcludedFromFee(address account) public view returns (bool) {
        return _isExcludedFromFee[account];
    }

    function _mint(address account, uint256 amount) internal {
        require(account != address(0), "BEP20: mint to the zero address");
        require(
            amount.add(_totalSupply) >= 36000000 * 10 ** 6,
            "The upper limit of the total amount of mint"
        );
        _totalSupply = _totalSupply.add(amount);
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }

    function burn(uint256 amount) public returns (bool) {
        _burn(msg.sender, amount);
        return true;
    }

    function _burn(address account, uint256 amount) internal {
        require(account != address(0), "BEP20: burn from the zero address");

        _balances[account] = _balances[account].sub(
            amount,
            "BEP20: burn amount exceeds balance"
        );
        _totalSupply = _totalSupply.sub(amount);
        emit Transfer(account, address(0), amount);
    }

    function _burnFrom(address account, uint256 amount) internal {
        _burn(account, amount);
        _approve(
            account,
            msg.sender,
            _allowances[account][msg.sender].sub(
                amount,
                "BEP20: burn amount exceeds allowance"
            )
        );
    }

    function removeAllFee() private {
        _previousFee = _Fee;
        _Fee = 0;
    }

    function restoreAllFee() private {
        _Fee = _previousFee;
    }

    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(address from, address to, uint256 amount) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");

        //indicates if fee should be deducted from transfer
        bool takeFee = true;

        //if any account belongs to _isExcludedFromFee account then remove the fee
        if (_isExcludedFromFee[from] || _isExcludedFromFee[to]) {
            takeFee = false;
        }

        //transfer amount, it will take tax, burn, liquidity fee
        _tokenTransfer(from, to, amount, takeFee);
    }

    //this method is responsible for taking all fee, if takeFee is true
    function _tokenTransfer(
        address sender,
        address recipient,
        uint256 amount,
        bool takeFee
    ) private {
        if (!takeFee) removeAllFee();

        _transferStandard(sender, recipient, amount);

        if (!takeFee) restoreAllFee();
    }

    function _takeFee(address sender, uint256 Amount) private {
        if (_Fee == 0) return;
        _FeeTotal = _FeeTotal.add(Amount);
        uint256 _BurnFee = Amount;

        _balances[address(0)] = _balances[address(0)].add(_BurnFee);
        emit Transfer(sender, address(0), _BurnFee);
        _totalSupply = _totalSupply.sub(_BurnFee);
    }

    function _transferStandard(
        address sender,
        address recipient,
        uint256 Amount
    ) private {
        _balances[sender] = _balances[sender].sub(Amount);

        uint256 rete;
        rete = _Fee;
        uint256 recipientRate = 10000 - rete;
        _takeFee(sender, Amount.div(10000).mul(_Fee));
        _balances[recipient] = _balances[recipient].add(
            Amount.div(10000).mul(recipientRate)
        );
        emit Transfer(sender, recipient, Amount.div(10000).mul(recipientRate));
    }

    mapping(address => uint256) public nonce;
    mapping(address => bool) public operator;

    function getnonce(address _address) public view returns (uint256) {
        return nonce[_address];
    }

    function getMsg(
        uint256 _nonce,
        uint256 _timestamp,
        uint256 _amount,
        address _account,
        address _address
    ) public view returns (bytes32) {
        require(_timestamp.add(300) > block.timestamp, "overtime");
        bytes32 message = keccak256(
            abi.encodePacked(_nonce, _timestamp, _amount, _account, _address)
        );
        return
            keccak256(
                abi.encodePacked("\x19Ethereum Signed Message:\n32", message)
            );
    }

    function check(
        uint256 _nonce,
        uint256 _timestamp,
        uint256 _amount,
        address _account,
        bytes32 r,
        uint8 v,
        bytes32 s
    ) public view returns (address) {
        bytes32 message = getMsg(
            _nonce,
            _timestamp,
            _amount,
            _account,
            address(this)
        );
        return ecrecover(message, v, r, s);
    }

    function mintTokenWithPermit(
        address _to,
        uint256 _amount,
        uint256 _timestamp,
        bytes32 _r,
        uint8 _v,
        bytes32 _s
    ) public {
        require(
            operator[check(nonce[_to], _timestamp, _amount, _to, _r, _v, _s)] ==
                true,
            "transfer Failed to sign"
        );
        _mint(_to, _amount);
        nonce[_to] += 1;
    }

    function setoperator(address _operator) public onlyOwner {
        operator[_operator] = true;
    }

    function removeoperator(address _operator) public onlyOwner {
        operator[_operator] = false;
    }

    function excludeFromFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = true;
    }

    function includeInFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = false;
    }

    //to recieve ETH from uniswapV2Router when swaping
    receive() external payable {}

    function claimBNB() public onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }

    function claimTokens(address token) public onlyOwner {
        IERC20(token).transfer(owner(), IERC20(token).balanceOf(address(this)));
    }

    function mint(uint256 amount) public onlyOwner returns (bool) {
        _mint(msg.sender, amount);
        return true;
    }
}
