    function mintArtwork() external nonReentrant returns (uint256 mintedId) {
        require(!hasMinted[msg.sender], "You have already minted an artwork");
        require(mintable);

        mintedId = nextMintableId;
        uint256 _uriIndex = randomURIIndex(msg.sender);
        string memory _uri = string(
            abi.encodePacked(_baseURI(), _uriIndex.toString(), ".json")
        );
        _safeMint(msg.sender, nextMintableId);
        tokenURIS[nextMintableId] = _uri;
        hasMinted[msg.sender] = true;
        mintedToken[msg.sender] = mintedId; 
        nextMintableId += 1;
        emit ArtworkMinted(mintedId);
    }

    function randomURIIndex(address user) internal view returns (uint256) {
        uint256 rawRand = uint256(
            keccak256(abi.encodePacked(user, blockhash(block.number - 1)))
        );
        uint256 rand = rawRand % MAX_ARTWORKS;

        return rand;
    }
}
