pragma solidity ^0.8.0;
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

interface IERC20 {
    function transfer(address recipient, uint256 amount)
        external
        returns (bool);

    function approve(address spender, uint256 value) external returns (bool);

    function transferFrom(
        address from,
        address to,
        uint256 value
    ) external returns (bool);

    function balanceOf(address account) external view returns (uint256);
}

contract WrappedNovoos is Initializable {
    string public name;
    string public symbol;
    uint8 public decimals;
    uint256 public _totalSupply;
    address public  buybackToken;
    IERC20 public Token;

    event Approval(address indexed src, address indexed guy, uint256 wad);
    event Transfer(address indexed src, address indexed dst, uint256 wad);
    event Deposit(address indexed dst, uint256 wad);
    event Withdrawal(address indexed src, uint256 wad);

    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;

    function initialize(address _token) public initializer {
        name = "Wrapped Novoos";
        symbol = "WNOVO";
        decimals = 18;
        buybackToken = _token;
        Token = IERC20(buybackToken);
    }

    function deposit(uint256 wad) public {
        require(Token.balanceOf(msg.sender) >= wad, "Insufficient balance.");
        Token.transferFrom(msg.sender, address(this), wad);
        balanceOf[msg.sender] += wad;
        _totalSupply += wad;
        emit Deposit(msg.sender, wad);
    }

    function withdraw(uint256 wad) public {
        require(balanceOf[msg.sender] >= wad, "Insufficient balance.");
        balanceOf[msg.sender] -= wad;
        _totalSupply -= wad;
        Token.transfer(msg.sender, wad);
        emit Withdrawal(msg.sender, wad);
    }

    function totalSupply() public view returns (uint256) {
        return _totalSupply;
    }

    function approve(address guy, uint256 wad) public returns (bool) {
        allowance[msg.sender][guy] = wad;
        emit Approval(msg.sender, guy, wad);
        return true;
    }

    function transfer(address dst, uint256 wad) public returns (bool) {
        return transferFrom(msg.sender, dst, wad);
    }

    function transferFrom(
        address src,
        address dst,
        uint256 wad
    ) public returns (bool) {
        require(balanceOf[src] >= wad, "Insufficient balance.");

        if (src != msg.sender && allowance[src][msg.sender] != uint256(0)) {
            require(allowance[src][msg.sender] >= wad);
            allowance[src][msg.sender] -= wad;
        }

        balanceOf[src] -= wad;
        balanceOf[dst] += wad;

        emit Transfer(src, dst, wad);

        return true;
    }
}
