    "contracts/prize-pots/PixelSweeperStakerPotV2.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "./PixelSweeperPot.sol";
import "../erc721/ERC721Staked.sol";
import "../sweeper/utils/EmergencyWithdrawable.sol";
import "./utils/TokenWeights.sol";
import "./IPixelSweeperStakerPotV2Listener.sol";
import "./PixelSweeperStakerPotV2Migrator.sol";

/**
 * @title PixelSweeper Staker Pot - Version 2.0
 *
 * The staker pot allows holders of PixelSweeper NFTs to stake their tokens 
 * for a chance to win NFTs swept by the PixelSweeper project.
 * Chances per sweeper, the weight, is dependent on their RS-S ranking.
 * When staking, users receive a staked version of their staked NFT (this contract).
 * Chances to win in the staker pot the are tracked in the {ticket} ERC20 token.
 *
 * @author Fab
 */
contract PixelSweeperStakerPotV2 is
    PixelSweeperPot,
    ERC721Staked,
    TokenWeights,
    EmergencyWithdrawable,
    PixelSweeperStakerPotV2Migrator
{
    using ERC165Checker for address;

    IERC721Enumerable public token;
    IPixelSweeperStakerPotV2Listener public stakingListener;

    string private baseURI;

    event StakingListenerSet(address indexed stakingListener);

    constructor(
        string memory _name,
        string memory _symbol,
        IERC721Enumerable _token,
        uint256 _maxSupply,
        address _stakerPotV1
    ) 
        ERC721Staked(_token) 
        ERC721(_name, _symbol) 
        TokenWeights(_maxSupply)
        PixelSweeperStakerPotV2Migrator(_stakerPotV1, _token)
    {
        token = _token;
        _pause();
    }


    // --- User methods ---

    /**
     * @notice Stake `tokenIds`
     * SWPR tokens will be send to this contract.
     * Mints corresponding SWPR-S tokens to senders.
     * Mints staker pot tickets to sender, according to weight of staked tokens.
     * Expects this contract is approved to transfer SWPR tokens for the sender.
     * Contract needs to not be {paused}.
     */
    function stake(uint256[] memory tokenIds) external nonReentrant whenNotPaused {
        address user = _msgSender();
        _stakeTokens(user, user, tokenIds);
    }

    /**
     * @notice Unstake `tokenIds`
     * SWPR tokens will be send back from this contract to the sender.
     * Burns the corresponding SWPR-S tokens from sender.
     * Burns staker pot tickets from sender, according to the weight of the unstaked tokens.
     * Burns player pot tickets from sender, when not enough staked tokens remain for the 100 tickets per staked token requirement.
     * Contract needs to not be {paused}.
     */
    function unstake(uint256[] memory tokenIds, bool allowTicketLoss)
        external
        nonReentrant
        whenNotPaused
    {
        address user = _msgSender();
        _unstakeTokens(user, user, tokenIds, allowTicketLoss);
    }


    // --- Admin methods ---

    /**
     * @notice Stake `tokenIds` for `to`, transfering tokens from `from`, see {stake}.
     * @dev Callable by owner, used for migration purposes to safe the user gas costs.
     */
    function stakeFor(
        address from,
        address to,
        uint256[] memory tokenIds
    ) external onlyOwner nonReentrant {
        require(from == address(this) || from == to, "Invalid 'to' address");
        _stakeTokens(from, to, tokenIds);
    }

    /**
     * @notice Unstake `tokenIds` for `user`, see {unstake}.
     * @dev Callable by owner, used for migration purposes to safe the user gas costs.
     */
    function unstakeFor(
        address user,
        uint256[] memory tokenIds,
        bool allowTicketLoss
    ) external onlyOwner nonReentrant {
        _unstakeTokens(user, user, tokenIds, allowTicketLoss);
    }

    /**
     * @notice Sets the staking listener to act on staking and unstaking.
     * @dev Callable by owner
     */
    function setStakingListener(
        IPixelSweeperStakerPotV2Listener _stakingListener
    ) external onlyOwner {
        require(
            address(_stakingListener).supportsInterface(
                type(IPixelSweeperStakerPotV2Listener).interfaceId
            ),
            "stakingListener-invalid"
        );
        stakingListener = _stakingListener;

        emit StakingListenerSet(address(_stakingListener));
    }

    /**
     * @notice Allows the owner to set the base URI to be used for all not revealed token IDs
     * @param _uri: base URI
     * @dev Callable by owner
     */
    function setBaseURI(string memory _uri) external onlyOwner {
        baseURI = _uri;
    }

    /**
     * @notice Pauses staking and unstaking
     * @dev Callable by owner, pausing the contract is used to complete the setup and for migration purposes.
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Resume normal operations
     * @dev Callable by owner
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    // -- Internal methods ---

    /**
     * @dev Stake `tokenIds`
     * SWPR tokens will be send to this contract from `from`.
     * Mints corresponding SWPR-S tokens to `to`.
     * Mints staker pot tickets to `to`, according to weight of staked tokens.
     * Expects this contract is approved to transfer SWPR tokens for `from`.
     */
    function _stakeTokens(
        address from,
        address to,
        uint256[] memory tokenIds
    ) internal override {
        require(tokenIds.length > 0, "Cannot stake 0 tokens");

        uint256 weight;
        for (uint256 i = 0; i < tokenIds.length; ++i) {
            weight += _stakeToken(from, to, tokenIds[i]);
        }

        _enterPot(to, weight);

        if (address(stakingListener) != address(0)) {
            stakingListener.afterTokenStake(from, to, tokenIds);
        }
    }

    /**
     * @dev Unstake `tokenIds`
     * SWPR tokens will be send back from this contract to `to`.
     * Burns the corresponding SWPR-S tokens from `from`.
     * Burns staker pot tickets from `from` according to the weight of the unstaked tokens.
     * Burns player pot tickets from `from` when not enough staked tokens remain for the 100 tickets per staked token requirement.
     */
    function _unstakeTokens(
        address from,
        address to,
        uint256[] memory tokenIds,
        bool allowTicketLoss
    ) internal {
        require(tokenIds.length > 0, "Cannot unstake 0 tokens");
        uint256 weight;
        for (uint256 i = 0; i < tokenIds.length; ++i) {
            weight += _unstakeToken(from, to, tokenIds[i]);
        }

        _leavePot(from, weight, allowTicketLoss);

        if (address(stakingListener) != address(0)) {
            stakingListener.afterTokenUnstake(
                from,
                to,
                tokenIds,
                allowTicketLoss
            );
        }
    }

    /**
     * @dev Stake `tokenId` and return its weights.
     * Staking transfers the token to this contract and mints the corresponding staking-receipt token.
     * @param from the address to transfer the staked token from
     * @param to the address to mint the staking-receipt token to
     * @param tokenId the tokenId to stake
     */
    function _stakeToken(
        address from,
        address to,
        uint256 tokenId
    ) internal returns (uint256) {
        super._stake(from, to, tokenId);
        return _weightOfToken(tokenId);
    }

    /**
     * @dev Untake `tokenId` and return its weights.
     * Unstaking transfers the token back to the user and burns the corresponding staking-receipt token.
     * @param from the address to burn the staking-receipt token from
     * @param to the address to transfer the staked token to
     * @param tokenId the tokenId to stake
     */
    function _unstakeToken(
        address from,
        address to,
        uint256 tokenId
    ) internal returns (uint256) {
        _unstake(from, to, tokenId);
        return _weightOfToken(tokenId);
    }

    /**
     * @dev See {ERC721-_beforeTokenTransfer}
     * Only allow burning and minting of this token, no transfers.
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal virtual override {
        super._beforeTokenTransfer(from, to, tokenId);
        require(
            from == address(0) || to == address(0),
            "Staking Receipt not transferable"
        );
    }

    /**
     * @dev See {ERC721-_baseURI}
     */
    function _baseURI() internal view override returns (string memory) {
        return baseURI;
    }

    /**
     * @dev See {PixelSweeperStakerPotV2Migrator-_stakerOf}
     */
    function _stakerOf(uint256 tokenId) internal view override returns (address) {
        return ownerOf(tokenId);
    }
}
"
    },
    "contracts/prize-pots/PixelSweeperPot.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/introspection/ERC165Checker.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

import "./token/IControlledToken.sol";
import "./token/ITokenController.sol";
import "./prize-strategy/IPeriodicPrizeStrategy.sol";

/**
 * @title Base contract for a PixelSweeper Pot
 * 
 * Controls the chances to win the pot by entering the pot with a given weight.
 * Chance to win is relative to the weight the user owns.
 * Weight is tracked as a ERC20 token in {controlledToken}, the ticket.
 * To benefit long term users, there is a time bonus or locked weight system in place.
 * When a user enters the pot after {gracePeriod}, a part of his weight is locked and
 * can be unlocked for the next drawing using the {unlock} method.
 *
 * Unlocking is done automatically on every enter/leave transaction.
 * 
 * @author Fab
 */
abstract contract PixelSweeperPot is Ownable, Pausable, ReentrancyGuard, ITokenController {
    using ERC165Checker for address;

    struct LockedWeight {
        uint256 amount;
        uint256 lockedUntil;
    }

    event LockedWeigthUpdate(
        address indexed user,
        uint256 amount,
        uint256 lockedUntil
    );

    event PrizeStrategySet(address indexed prizeStrategy);


    // Ticket tracking the chances to win a drawing
    IControlledToken public controlledToken;

    // Locked weight of user, when entered after {gracePeriod}
    mapping(address => LockedWeight) public lockedWeight;

    // Price strategy handling the drawing and prize distribution
    IPeriodicPrizeStrategy public prizeStrategy;

    // Time users can stake, after the current round started, to still get 100% weight / time bonus.
    uint256 public gracePeriod = 7 days;


    // --- User methods ---

    /** 
     * @notice Unlock all unlockable weight / activate time bonus for `user`
     * Unlocking is done automatically on every enter/leave transaction.
     */
    function unlock(address user) external nonReentrant {
        _unlock(user);
    }


    // --- Admin methods ---

    /**
     * @dev Set after constructor since we need to set this contract as controller in the Ticket constructor
     * Callable by owner.
     */
    function setTicket(IControlledToken _ticket) external onlyOwner {
        require(address(controlledToken) == address(0), "Already set");
        controlledToken = _ticket;
    }

    /**
     * @dev Sets grace period after which part of the weight is locked when entering the pot.
     * Callable by owner.
     */
    function setGracePeriod(uint256 _gracePeriod) external onlyOwner {
        gracePeriod = _gracePeriod;
    }

    /**
     * @dev Sets the prize strategy of the pot.
     * Callable by owner.
     */
    function setPrizeStrategy(IPeriodicPrizeStrategy _prizeStrategy) external onlyOwner {
        _setPrizeStrategy(_prizeStrategy);
    }


    // --- Internal methods ---

    /**
     * @dev Enter the pot for `user` with given `weight`.
     * Locks part of the weight when entering after {gracePeriod}.
     * Unlocks any unlockable weight first.
     */
    function _enterPot(address user, uint256 weight) internal virtual {
        _unlock(user);
        if (address(prizeStrategy) != address(0)) {
            uint256 enterAfter = block.timestamp - prizeStrategy.prizePeriodStartedAt();
            if (enterAfter > gracePeriod) {
                uint256 remainingSeconds = prizeStrategy.prizePeriodRemainingSeconds();
                uint256 weightUnlocked = (weight * remainingSeconds) / (prizeStrategy.prizePeriodSeconds() - gracePeriod);

                lockedWeight[user].amount += weight - weightUnlocked;
                lockedWeight[user].lockedUntil = prizeStrategy.prizePeriodEndAt();
                emit LockedWeigthUpdate(
                    user,
                    lockedWeight[user].amount,
                    lockedWeight[user].lockedUntil
                );

                weight = weightUnlocked;
            }
        }

        controlledToken.controllerMint(user, weight);
    }

    /**
     * @dev Leave the pot for `user` with given `weight`.
     * Unlocks any unlockable weight first.
     */
    function _leavePot(
        address user,
        uint256 weight,
        bool
    ) internal virtual {
        LockedWeight memory locked = lockedWeight[user];
        if (locked.amount > 0) {
            if (weight < locked.amount) {
                lockedWeight[user].amount -= weight;
                emit LockedWeigthUpdate(
                    user,
                    lockedWeight[user].amount,
                    lockedWeight[user].lockedUntil
                );
                weight = 0;
                _unlock(user);
            } else {
                weight -= locked.amount;
                delete lockedWeight[user];
                emit LockedWeigthUpdate(user, 0, 0);
            }
        }

        if (weight > 0) {
            controlledToken.controllerBurnFrom(_msgSender(), user, weight);
        }
    }

    /**
     * @dev Unlock any unlockable weight for `user`
     */
    function _unlock(address user) internal {
        LockedWeight memory locked = lockedWeight[user];
        if (locked.amount > 0 && block.timestamp > locked.lockedUntil) {
            controlledToken.controllerMint(user, locked.amount);
            delete lockedWeight[user];
            emit LockedWeigthUpdate(user, 0, 0);
        }
    }

    /**
     * @dev Sets the prize strategy of the pot.
     */
    function _setPrizeStrategy(IPeriodicPrizeStrategy _prizeStrategy) internal {
        require(
            address(_prizeStrategy) != address(0),
            "prizeStrategy-not-zero"
        );
        require(
            address(_prizeStrategy).supportsInterface(
                type(IPeriodicPrizeStrategy).interfaceId
            ),
            "prizeStrategy-invalid"
        );
        prizeStrategy = _prizeStrategy;

        emit PrizeStrategySet(address(_prizeStrategy));
    }


    // --- ITokenController interface ---

    /**
     * @dev Hook of {controlledTicket} to avoid buying and selling tickets when award is in progress.
     */
    function beforeTokenTransfer(
        address,
        address,
        uint256
    ) external view override {
        require(
            address(prizeStrategy) == address(0) ||
                prizeStrategy.awardNotInProgress(),
            "Award in progress"
        );
    }
}
"
    },
    "contracts/erc721/ERC721Staked.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Enumerable.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";

/**
 * @title Implementation of IERC721Enumerable, meant to be used as staked version of a NFT. 
 *
 * @dev Reduces gas cost for minting and burning by using staked tokens enumerator for {tokenByIndex} and {totalSupply} 
 * and making {tokenOfOwnerByIndex} and {tokensOfOwner} very inefficient.
 * Only use {tokenOfOwnerByIndex} and {tokensOfOwner} these methods in view methods!
 * 
 * This contract assumes to be used with {_stake} and {_unstake} method, do not use {_burn} or {_mint}, {_safeMint}
 * methods directly, as they will break the enumeration assumptions.
 *
 * @author Fab
 */
abstract contract ERC721Staked is ERC721, IERC721Enumerable, IERC721Receiver {

    IERC721Enumerable private stakedToken;

    constructor(IERC721Enumerable _stakedToken) {
        stakedToken = _stakedToken;
    }

    /**
     * @dev Returns the total amount of tokens stored by the contract.
     */
    function totalSupply() public view override returns (uint256) {
        return stakedToken.balanceOf(address(this));
    }

    /**
     * @dev Returns a token ID owned by `owner` at a given `index` of its token list.
     * Use along with {balanceOf} to enumerate all of ``owner``'s tokens.
     * @dev Warning: This function is very inefficient and is meant to be accessed in view read methods only.
     */
    function tokenOfOwnerByIndex(address owner, uint256 index) public view override returns (uint256 tokenId) {
        require(index < balanceOf(owner), "ERC721Enumerable: owner index out of bounds");

        uint256 indexOfOwner;
        uint256 _totalSupply = totalSupply();
        for (uint256 i = 0; i < _totalSupply; ++i) {
            tokenId = tokenByIndex(i);
            if (ownerOf(tokenId) == owner) {
                if (indexOfOwner == index) return tokenId;
                indexOfOwner++;
            }
        }
    }

    /**
     * @dev Returns a token ID at a given `index` of all the tokens stored by the contract.
     * Use along with {totalSupply} to enumerate all tokens.
     */
    function tokenByIndex(uint256 index) public view override returns (uint256) {
        return stakedToken.tokenOfOwnerByIndex(address(this), index);
    }

    /**
     * @notice Returns all tokenIds owned by `_owner`
     * @param _owner: owner
     * @dev Warning: This function is very inefficient and is meant to be accessed in view read methods only.
     */
    function tokensOfOwner(address _owner) external view returns (uint256[] memory) {
        uint256 tokenCount = balanceOf(_owner);
        uint256[] memory result = new uint256[](tokenCount);
        uint256 index;

        uint256 _totalSupply = totalSupply();
        for (uint256 i = 0; i < _totalSupply && index < tokenCount; ++i) {
            uint256 tokenId = tokenByIndex(i);
            if (ownerOf(tokenId) == _owner) {
                result[index++] = tokenId;
            }
        }
        return result;
    }

    /**
     * @dev See {IERC721Receiver-onERC721Received} 
     */
    function onERC721Received(address, address, uint256, bytes memory) public virtual override returns (bytes4) {
        require(msg.sender == address(stakedToken), "Only staked token");
        return this.onERC721Received.selector;
    }

    /** 
     * @dev Stake `tokenId` by transfering them from `from' to this contract 
     * and minting equivalent tokenId of this token. 
     */
    function _stake(address from, address to, uint256 tokenId) internal {
        if (from != address(this)) {
            stakedToken.safeTransferFrom(from, address(this), tokenId);
        } else {
            require(stakedToken.ownerOf(tokenId) == address(this), "Not owner");
        }
        super._safeMint(to, tokenId);
    }

    /** 
     * @dev Stake `tokenId` by transfering them from `from' to this contract 
     * and minting equivalent tokenId of this token. 
     */
    function _unstake(address from, address to, uint256 tokenId) internal {
        require(ownerOf(tokenId) == from, "Not owner");
        super._burn(tokenId);
        stakedToken.safeTransferFrom(address(this), to, tokenId);
    }
}
"
    },
    "contracts/sweeper/utils/EmergencyWithdrawable.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/Address.sol";

contract EmergencyWithdrawable is Ownable {
    using SafeERC20 for IERC20;
    using Address for address;

    event EmergencyWithdrawERC721(address indexed owner, address indexed token, uint256[] tokenIds);
    event EmergencyWithdrawERC20(address indexed owner, address indexed token, uint256 balance);
    event EmergencyWithdraw(address indexed owner, uint256 amount);

    /**
     * @notice Allows the owner to withdraw non-fungible tokens for emergencies
     * @param _token: NFT address
     * @param _tokenIds: tokenIds
     * @dev Callable by owner
     */
    function emergencyWithdraw(address _token, uint256[] memory _tokenIds) external onlyOwner {
        require(_tokenIds.length != 0, "Cannot recover zero balance");

        emit EmergencyWithdrawERC721(msg.sender, _token, _tokenIds);
        for (uint256 i = 0; i < _tokenIds.length; ++i) {
            IERC721(_token).transferFrom(address(this), address(msg.sender), _tokenIds[i]);
        }
    }

    /**
     * @notice Allows the owner to withdraw tokens for emergencies
     * @param _token: token address
     * @dev Callable by owner
     */
    function emergencyWithdraw(address _token) external onlyOwner {
        uint256 balance = IERC20(_token).balanceOf(address(this));
        require(balance != 0, "Cannot recover zero balance");

        emit EmergencyWithdrawERC20(msg.sender, _token, balance);
        IERC20(_token).safeTransfer(address(msg.sender), balance);
    }

    /**
     * @notice Allows the owner to withdraw Ether for emergencies
     * @dev Callable by owner
     */
    function emergencyWithdraw() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance != 0, "Cannot recover zero balance");

        emit EmergencyWithdraw(msg.sender, balance);
        Address.sendValue(payable(msg.sender), balance);
    }
}"
