    "contracts/prize-pots/utils/TokenWeights.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";

import "./Uint16Array.sol";

/** 
 * @title Contract to track uint16 weights per tokenId
 *
 * @dev TokenIds are expected to be in [1..maxSupply]. 
 * To work around block gas limit, {addWeight} can be used to feed the weights incrementally.
 */
contract TokenWeights is Ownable {
    using Uint16Array for bytes;

    uint256 private maxSupply;
    bytes private weights;

    constructor(uint256 _maxSupply) {
        maxSupply = _maxSupply;
    }

    function addWeights(bytes memory _weights) external onlyOwner {
        weights.append(_weights);
    }

    function weightOfToken(uint256 _tokenId) external view returns (uint256) {
        require(_tokenId > 0 && _tokenId <= maxSupply, "Invalid tokenId");
        return _weightOfToken(_tokenId);
    }

    function _weightOfToken(uint256 _tokenId) internal view returns (uint256 weight) {
        return weights.at(_tokenId);
    }
}
"
