    "contracts/prize-pots/PixelSweeperStakerPotV2Migrator.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Enumerable.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

import "../interfaces/IERC721TokensOfOwner.sol";
import "../sweeper/utils/EmergencyWithdrawable.sol";


/**
 * @title PixelSweeper Staker Pot V2 Migrator
 * 
 * Handles the migration of PixelSweeper StakerPot V1 to V2 contract. 
 *
 * @author Fab
 */
abstract contract PixelSweeperStakerPotV2Migrator is Ownable, IERC721Enumerable {

    address private immutable stakerPotV1;
    IERC721Enumerable private immutable token;

    constructor(address _stakerPotV1, IERC721Enumerable _token) {
        stakerPotV1 = _stakerPotV1;
        token = _token;
    }

    function _stakeTokens(address from, address to, uint256[] memory tokenIds) internal virtual; 
    function _stakerOf(uint256 tokenId) internal virtual returns (address); 

    /**
     * @dev Migrate SWPR tokens of `user` to v2 staker pot (this contract).
     * Callable by owner
     */
    function migrateUser(address user, uint256[] memory tokenIds) external onlyOwner {
        // 1. withdraw SWPR tokens to this contract
        EmergencyWithdrawable(stakerPotV1).emergencyWithdraw(address(token), tokenIds);

        // 2. stake SWPR tokens and mint v2 receipts to user
        _stakeTokens(address(this), user, tokenIds);

        // 3. verify that v1 and v2 receipt is owned by user
        for (uint256 i = 0; i < tokenIds.length; ++i) {
            require(IERC721Enumerable(stakerPotV1).ownerOf(tokenIds[i]) == user, "Not staker");
            require(_stakerOf(tokenIds[i]) == user, "Migration failed");
        }
    }

    /**
     * @dev Transfer ownership of staker pot v1 back to {owner}
     * Callable by owner
     */
    function migrateRevertOwnershowStakerPotV1() external onlyOwner {
        Ownable(stakerPotV1).transferOwnership(owner());
    }


    /**
     * @dev Iterator over all users to migrate.
     * Returns null address when migration done
     */
    function migrationNextUser() external view returns (address user, uint256[] memory tokenIds) {
        if (token.balanceOf(stakerPotV1) != 0) {
            uint256 tokenId = token.tokenOfOwnerByIndex(stakerPotV1, 0);
            user = IERC721Enumerable(stakerPotV1).ownerOf(tokenId);
