    "contracts/OnchainLiquidityProvider.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.13;

import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import {SafeERC20Upgradeable, IERC20Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import {IVault} from "./interface/IVault.sol";
import {DexBase, QuoteParameters} from "./DexBase.sol";

contract OnchainLiquidityProvider is DexBase, ReentrancyGuardUpgradeable {
    using ECDSA for bytes32;
    using SafeERC20Upgradeable for IERC20Upgradeable;

    event QuoteAccepted(address indexed user, string quoteId, QuoteParameters quoteInfo);

    mapping(string => bytes32) private quoteHash;

    IVault public vault;

    /**
     * @notice
     * The current chain id
     */
    function blockchain() external view returns (uint256) {
        return block.chainid;
    }

    /**
     * @notice
     * Mark as contract version
     */
    function version() external pure returns (uint256) {
        return 10000;
    }

    /**
     * @notice
     * Query the history quote by quote id
     *
     * @param quoteId The quote id.
     */
    function quoteHashQuery(string memory quoteId) external view returns (bytes32) {
        return quoteHash[quoteId];
    }


    function initialize(address owner_, address vault_) public payable initializer {
        _init(owner_, vault_);
    }

    function _init(address owner_, address vault_) internal onlyInitializing {
        __ReentrancyGuard_init_unchained();
        _init_unchained(owner_, vault_);
    }

    function _init_unchained(address owner_, address vault_) internal onlyInitializing {
        require(owner_ != address(0), "QuickLiquidityProvider: owner is the zero address");
        _owner = owner_;
        vault = IVault(payable(vault_));
        circuitBreaker = false;
        allowDiffReceiver = false;
    }

    receive() external payable {
    }

    /**
     * @notice
     * Swap request.
     *
     * @param message Encoded quote info.
     * @param signature The signatures. 0: Signature signed by liquidity provider. 1: Signature signed by platform signer.
     */
    function swapRequest(bytes calldata message, bytes calldata signature) external payable nonReentrant isCircuitBreaker {
        address signer = _source(message, signature);
        QuoteParameters memory params = abi.decode(message, (QuoteParameters));
        string memory quoteId = params.quoteId;
        require(params.chainid == block.chainid, 'Onchain: blockchain id');
        require(params.fromAsset != params.toAsset, "Onchain: fromAsset should not the same with toAsset");
        require(params.expireTime >= block.timestamp, 'Onchain: EXPIRED');
        require(signer == liquiditySigner, "Onchain: invalid signer");
        require(quoteHash[quoteId] == 0, "Onchain: duplicate quoteId");
        // check bnb fromAsset
        if (params.fromAsset == BNB) {
            require(msg.value == params.fromAmount, "Onchain: value not equal fromAmount");
        } 
        // diff receiver
        if (!allowDiffReceiver) {
            require(params.toAddress == msg.sender, "Onchain: different receiver");
        }

        // settlement
        _settlement(msg.sender, params.fromAsset, params.fromAmount, params.toAsset, params.toAmount);

        // transfer to user
        _safeTransferAsset(params.toAsset, params.toAddress, params.toAmount);

        quoteHash[quoteId] = keccak256(abi.encode(params));

        emit QuoteAccepted(msg.sender, quoteId, params);
    }

    function _settlement(address user, address fromToken, uint256 fromAmount, address toToken, uint256 toAmount) internal {
        uint256 toBefore;
        if (toToken == BNB) {
            toBefore = address(this).balance;
        } else {
            toBefore = IERC20Upgradeable(toToken).balanceOf(address(this));
        }

        // settlement
        if (fromToken == BNB) {
            vault.onchainSettlement{value: fromAmount}(fromToken, fromAmount, toToken, toAmount);   // dex -> vault
        } else {
            IERC20Upgradeable(fromToken).safeTransferFrom(user, address(this), fromAmount);       // user -> dex
            IERC20Upgradeable(fromToken).safeIncreaseAllowance(address(vault), fromAmount);       // dex -> vault approve
            vault.onchainSettlement(fromToken, fromAmount, toToken, toAmount);                    // dex -> vault
        }

        uint256 toAfter;
        if (toToken == BNB) {
            toAfter = address(this).balance;
        } else {
            toAfter = IERC20Upgradeable(toToken).balanceOf(address(this));
        }

        require(toAfter - toBefore == toAmount, "Onchain: settlement toAmount wrong");
    }


    function _source(bytes memory message, bytes memory signature) internal pure returns (address) {
        bytes32 hash = keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", keccak256(message)));
        return ECDSA.recover(hash, signature);
    }
}"
