    "contracts/CryptoUnityTokenLocker.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";

contract CryptoUnityTokenLocker is AccessControlUpgradeable, PausableUpgradeable {
    enum LockType {
        LIQUIDITY,
        PROJECT,
        TREASURY,
        STRATEGIC_SALE_1,
        RESERVE,
        TEAM,
        PRIVATE_SALE,
        AIRDROP,
        STRATEGIC_SALE_2
    }

    struct LockInfo {
        uint128 amount;
        uint128 claimedAmount;
        uint64 claimedMonth;
        uint64 lockTimestamp;
        uint32 lockMonths;
        LockType lockType;
        bool rewardClaimable;
        uint256 pendingReward;
    }
    bytes32 public constant EXECUTOR_ROLE = keccak256("EXECUTOR_ROLE");

    address public token;
    mapping(address => LockInfo) public lockData;
    mapping(LockType => uint32) public vestingPeriod;

    uint private constant MULTIPLIER = 1e9;
    uint private constant ONE_MONTH = 365 days / 12;
    uint private rewardIndex;
    mapping(address => uint) private rewardIndexOf;

    uint256 private totalSupplyForReward;
    uint256 private totalSupply;
    uint256 private pendingReward;

    event AddVesting(
        address indexed user,
        uint128 amount,
        uint64 lockTimestamp,
        LockType lockType
    );
    event RemoveVesting(address indexed user);
    event ClaimVesting(address indexed user, uint256 available);
    event ClaimReward(address indexed user, uint256 reward);

    function initialize(address _token) public initializer {
        __Pausable_init_unchained();
        __AccessControl_init_unchained();
        __CryptoUnityTokenLocker_init_unchained(_token);
    }

    function __CryptoUnityTokenLocker_init_unchained(address _token)
        internal
        onlyInitializing
    {
        token = _token;
        vestingPeriod[LockType.LIQUIDITY] = 12;
        vestingPeriod[LockType.PROJECT] = 36;
        vestingPeriod[LockType.TREASURY] = 24;
        vestingPeriod[LockType.STRATEGIC_SALE_1] = 24;
        vestingPeriod[LockType.RESERVE] = 48;
        vestingPeriod[LockType.TEAM] = 48;
        vestingPeriod[LockType.PRIVATE_SALE] = 12;
        vestingPeriod[LockType.AIRDROP] = 10;
        vestingPeriod[LockType.STRATEGIC_SALE_2] = 20;
        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _grantRole(EXECUTOR_ROLE, _msgSender());
    }

    function getToken() external view returns (address) {
        return token;
    }

    function withdrawToken(
        address _tokenAddress,
        uint256 _amount
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_tokenAddress != address(0), "Token address is invalid");
        IERC20Upgradeable(_tokenAddress).transfer(_msgSender(), _amount);
    }

    function pauseVesting() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    function resumeVesting() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    function setTotalSupply(uint256 _totalSupply, uint256 _totalSupplyForReward) external onlyRole(DEFAULT_ADMIN_ROLE) {
        totalSupply = _totalSupply;
        totalSupplyForReward = _totalSupplyForReward;
    }

    function getTotalSupply() external view returns(uint256, uint256){
        return (totalSupply, totalSupplyForReward);
    }

    function setVestingPeriod(
        LockType _lockType,
        uint32 _period
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_period > 0, "Invalid Period");
        vestingPeriod[_lockType] = _period;
    }

    function getLockData(
        address _user
    ) external view returns (uint128, uint128, uint64, uint64, uint32) {
        require(_user != address(0), "User address is invalid");
        LockInfo storage _lockInfo = lockData[_user];
        return (
            _lockInfo.amount,
            _lockInfo.claimedAmount,
            _lockInfo.claimedMonth,
            _lockInfo.lockTimestamp,
            _lockInfo.lockMonths
        );
    }

    function addVestingMany(
        address[] calldata _users,
        uint128[] calldata _amounts,
        uint64[] calldata _startTime,
        LockType _lockType
    ) external onlyRole(EXECUTOR_ROLE) {
        require(_users.length == _amounts.length, "array length not eq");
        require(_users.length == _startTime.length, "array length not eq");
        for (uint256 j = 0; j < _users.length; j++) {
            addVesting(_users[j], _amounts[j], _startTime[j], _lockType);
        }
    }

    function addVesting(
        address _user,
        uint128 _amount,
        uint64 _lockTimestamp,
        LockType _lockType
    ) public onlyRole(EXECUTOR_ROLE) {
        require(_amount > 0, "amount can not zero");
        require(_lockTimestamp > 0, "lock timestamp need more than zero");
        require(lockData[_user].amount == 0, "this address has already locked");

        bool _rewardClaimable = _lockType != LockType.AIRDROP && _lockType != LockType.STRATEGIC_SALE_2;
        LockInfo memory lockinfo = LockInfo({
            amount: _amount,
            lockTimestamp: _lockTimestamp,
            lockMonths: vestingPeriod[_lockType],
            claimedMonth: 0,
            claimedAmount: 0,
            lockType: _lockType,
            rewardClaimable: _rewardClaimable,
            pendingReward: 0
        });
        lockData[_user] = lockinfo;

        if (_rewardClaimable) {
            totalSupplyForReward += _amount;
        }
        totalSupply += _amount;
        emit AddVesting(_user, _amount, _lockTimestamp, _lockType);
    }

    function removeVesting(address _user) external onlyRole(EXECUTOR_ROLE) {
        totalSupply -= lockData[_user].amount;
        if(lockData[_user].rewardClaimable) {
            totalSupplyForReward -= lockData[_user].amount;
        }
        delete lockData[_user];
        emit RemoveVesting(_user);
    }

    function claimVesting(
        address _user
    ) external whenNotPaused returns (uint256 available) {
        _updateRewards(_msgSender());

        LockInfo storage _lockInfo = lockData[_user];
        require(_lockInfo.lockTimestamp <= block.timestamp, "Vesting time is not started");
        require(_lockInfo.amount > 0, "No lock token to claim");

        uint256 passedMonth = (block.timestamp - _lockInfo.lockTimestamp) / ONE_MONTH;
        require(passedMonth > _lockInfo.claimedMonth, "Not ready for claiming");

        passedMonth = passedMonth < _lockInfo.lockMonths ? passedMonth : _lockInfo.lockMonths;
        uint256 passedSec = passedMonth < _lockInfo.lockMonths ? (block.timestamp - _lockInfo.lockTimestamp) % ONE_MONTH : 0;
        if (_lockInfo.lockType == LockType.PRIVATE_SALE) {
            if (passedMonth == 1 || passedMonth == 2) {
                available = (uint256(_lockInfo.amount) / 20) * passedMonth;
                available += (uint256(_lockInfo.amount) / 20) * passedSec / ONE_MONTH;
            } else {
                available = (uint256(_lockInfo.amount) / 10) + (((uint256(_lockInfo.amount) * 9) / 100) * (passedMonth - 2));
                available += (uint256(_lockInfo.amount) * 9 / 100) * passedSec / ONE_MONTH;
            }
        } else {
            // Other Vesting
            available = (uint256(_lockInfo.amount) / uint256(_lockInfo.lockMonths)) * passedMonth;
            available += (uint256(_lockInfo.amount) / uint256(_lockInfo.lockMonths)) * passedSec / ONE_MONTH;
        }

        available = available - _lockInfo.claimedAmount;
        require(available > 0, "not available claim");

        _lockInfo.claimedAmount = uint128(uint256(_lockInfo.claimedAmount) + available);
        _lockInfo.claimedMonth = uint64(passedMonth);
        
        totalSupplyForReward = totalSupplyForReward - available;
        totalSupply = totalSupply - available;
        IERC20Upgradeable(token).transfer(_user, available);
        emit ClaimVesting(_user, available);
    }

    function claimRewardForTeam() external onlyRole(EXECUTOR_ROLE) {
        uint256 reward = _calculateRewards(address(this));
    
        require(reward > 0, "No rewards to claim");
        // Transfer the reward tokens from the contract to the user
        require(IERC20Upgradeable(token).transfer(_msgSender(), reward), "Token transfer failed");
        rewardIndexOf[address(this)] = rewardIndex;
        pendingReward -= reward;

        emit ClaimReward(address(this), reward);
    }

    function claimReward() public {
        _updateRewards(_msgSender());

        uint256 reward = lockData[_msgSender()].pendingReward;
        require(reward > 0, "No rewards to claim");
        // Transfer the reward tokens from the contract to the user
        require(IERC20Upgradeable(token).transfer(_msgSender(), reward), "Token transfer failed");
        pendingReward -= reward;

        emit ClaimReward(_msgSender(), reward);
    }

    function _calculateRewards(address account) private view returns (uint) {
        uint shares = lockData[account].amount;
        if(account == address(this)) {
            shares = totalSupply - totalSupplyForReward;
        }
        return (shares * (rewardIndex - rewardIndexOf[account])) / MULTIPLIER;
    }

    function _updateRewards(address account) private {
        _updateRewardIndex();
        lockData[account].pendingReward += _calculateRewards(account);
        rewardIndexOf[account] = rewardIndex;
    }

    function _updateRewardIndex() internal {
        uint256 reward = IERC20Upgradeable(token).balanceOf(address(this)) - totalSupply - pendingReward;

        rewardIndex += (reward * MULTIPLIER) / totalSupplyForReward;

        pendingReward += reward;
    }

    function calculateRewardsEarned(address account) external view returns (uint) {
        return lockData[account].pendingReward + _calculateRewards(account);
    }
}
