    "contracts/CyberLegion.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

// Cyber Legion
import "./CyberLegionFactory.sol";

contract CyberLegion is CyberLegionFactory {
    // Library
    using SafeMath for uint256;
    using EnumerableSet for EnumerableSet.AddressSet;

    // Parameters
    string public constant name = "Cyber Legion";
    string public constant symbol = "SPQR";

    // Events
    event Airdrop(address winner, uint256 tokenId);
    event Purchase(address buyer, uint256 tokenId);
    event PurchaseTerminal(address terminal, uint256 tokenId);
    event Retrieve(address receiver, uint256 tokenId, uint256 amount);
    event Transfer(address receiver, uint256 tokenId, uint256 amount);

    // Functions
    function transfer(
        address _from,
        address _to,
        uint256 _id,
        uint256 _amount
    ) external onlyOwner {
        // Supply validation
        require(balanceOf(_from, _id) >= _amount, "Insufficient amount.");

        // Transfer
        _safeTransferFrom(_from, _to, _id, _amount, "");

        // Event
        emit Transfer(_to, _id, _amount);
    }

    function purchase() external payable {
        // Supply validation
        require(
            _balanceLegionariesSize > 0,
            "There are no more spots in the legion."
        );

        // Price validation
        uint256 priceInBNB = getLatestPriceInBNB(price);
        require(msg.value >= priceInBNB, "Insufficient funds.");

        // Transfer
        uint256 id = legionary(msg.sender);

        // Refund excess BNB if any
        if (msg.value > priceInBNB) {
            uint256 refundAmount = msg.value - priceInBNB;
            payable(msg.sender).transfer(refundAmount);
        }

        // Event
        emit Purchase(msg.sender, id);
    }

    function purchaseTerminal() external onlyTerminal {
        // Transfer
        uint256 id = legionary(provider);

        // Event
        emit PurchaseTerminal(terminal, id);
    }

    function retrieve(
        address _address,
        uint256 _id,
        uint256 _amount
    ) external onlyProvider {
        // Supply validation
        require(balanceOf(provider, _id) >= _amount, "Insufficient amount.");

        // Transfer
        _safeTransferFrom(provider, _address, _id, _amount, "");

        // Event
        emit Retrieve(_address, _id, _amount);
    }

    function airdrop(uint256 id) public onlyOwner {
        // Random number validation
        require(
            randomNumberReady,
            "The random number has not been provided yet."
        );

        // Supply validation
        require(balanceOf(owner(), id) > 0, "There are no more spots.");

        // Raffle
        uint256 numberLegionaries = _legionaries.length();
        uint256 index = randomNumber.mod(numberLegionaries);
        address winner = _legionaries.at(index);

        // Transfer
        _safeTransferFrom(owner(), winner, id, 1, "");

        // Set
        randomNumberReady = false;

        // Event
        emit Airdrop(winner, id);
    }

    function legionary(address _receiver) internal returns (uint256) {
        // Types
        uint256 randomNumber = internalRandomGenerator();
        uint256 index = randomNumber % _balanceLegionariesSize;
        uint256 id = _balanceLegionaries[index];

        // Transfer
        _safeTransferFrom(owner(), _receiver, id, 1, "");

        // Swap
        if (balanceOf(owner(), id) == 0) {
            uint8 boundary = _balanceLegionariesSize - 1;
            if (boundary != index) {
                uint8 temp = _balanceLegionaries[boundary];
                _balanceLegionaries[boundary] = _balanceLegionaries[index];
                _balanceLegionaries[index] = temp;
            }
            _balanceLegionariesSize--;
        }

        return id;
    }

    function centurion() external onlyOwner {
        airdrop(CENTURION);
    }
}
"
    },
    "contracts/CyberLegionBank.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

// OpenZeppelin
import "@openzeppelin/contracts/access/Ownable.sol";

contract CyberLegionBank is Ownable {
    // Parameters
    address internal terminal;
    address internal provider;

    // Modifiers
    modifier onlyTerminal() {
        require(
            msg.sender == terminal || msg.sender == owner(),
            "Not authorized."
        );
        _;
    }

    modifier onlyProvider() {
        require(
            msg.sender == provider || msg.sender == owner(),
            "Not authorized."
        );
        _;
    }

    constructor() {
        terminal = 0x0290DCef69887ad187b0DbFEcF43c76172112A17;
        provider = 0x43abD30fE2Ee3AC302838C4D861818119588033B;
    }

    // Functions
    function _transferTo(address receiver, uint256 amount) private {
        (bool success, ) = receiver.call{value: amount}("");
        require(success, "Transfer failed.");
    }

    function withdraw() external onlyOwner {
        _transferTo(msg.sender, address(this).balance);
    }

    function withdrawAmount(uint256 _amount) external onlyOwner {
        require(_amount <= address(this).balance, "Insufficient funds.");
        _transferTo(msg.sender, _amount);
    }

    function setTerminal(address _address) external onlyOwner {
        terminal = _address;
    }

    function setProvider(address _address) external onlyOwner {
        provider = _address;
    }
}
"
    },
    "contracts/CyberLegionFactory.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

// OpenZeppelin
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";

// Cyber Legion
import "./CyberLegionBank.sol";
import "./CyberLegionRandom.sol";
import "./CyberLegionFinance.sol";

contract CyberLegionFactory is
    ERC1155,
    CyberLegionBank,
    CyberLegionRandom,
    CyberLegionFinance
{
    // Library
    using Strings for uint256;
    using SafeMath for uint256;
    using EnumerableSet for EnumerableSet.AddressSet;

    // Holders
    mapping(uint256 => EnumerableSet.AddressSet) internal _holders;

    // Types
    uint256 internal _id = 1;
    mapping(bytes32 => uint256) internal _types;
    mapping(uint256 => uint256) internal _typesSupply;

    // Types Constants
    uint256 public constant LEGIONARY_TYPE_1 = 1;
    uint256 public constant LEGIONARY_TYPE_2 = 2;
    uint256 public constant LEGIONARY_TYPE_3 = 3;
    uint256 public constant LEGIONARY_TYPE_4 = 4;
    uint256 public constant LEGIONARY_TYPE_5 = 5;
    uint256 public constant LEGIONARY_TYPE_6 = 6;
    uint256 public constant LEGIONARY_TYPE_7 = 7;
    uint256 public constant LEGIONARY_TYPE_8 = 8;
    uint256 public constant CENTURION = 9;

    // Balance
    EnumerableSet.AddressSet internal _legionaries;
    uint8[] internal _balanceLegionaries;
    uint8 internal _balanceLegionariesSize;

    // Events
    event Add(uint256 tokenId, uint256 amount, uint256 price);
    event Remove(uint256 tokenId, uint256 amount);

    constructor() ERC1155("https://cyberlegion.bg/api/item/data/") {
        // Mint
        mintLegion();
    }

    function mintLegion() public onlyOwner {
        // Mint
        add("LEGIONARY_TYPE_1", 10, price);
        add("LEGIONARY_TYPE_2", 10, price);
        add("LEGIONARY_TYPE_3", 10, price);
        add("LEGIONARY_TYPE_4", 10, price);
        add("LEGIONARY_TYPE_5", 10, price);
        add("LEGIONARY_TYPE_6", 10, price);
        add("LEGIONARY_TYPE_7", 10, price);
        add("LEGIONARY_TYPE_8", 10, price);
        add("CENTURION", 1, 0);

        // Set
        uint8[8] memory temp = [1, 2, 3, 4, 5, 6, 7, 8];
        _balanceLegionaries = temp;
        _balanceLegionariesSize = 8;
    }

    // Add Token
    function add(
        string memory _key,
        uint256 _amount,
        uint256 _price
    ) public onlyOwner {
        // Parameter
        (uint256 id, ) = getType(_key);

        if (id == 0) {
            // Add Type
            (id, ) = setType(_key);

            // Mint
            _mint(owner(), id, _amount, "");

            // Set Supply
            _typesSupply[id] = _amount;
        } else {
            // Mint
            _mint(owner(), id, _amount, "");

            // Add Supply
            _typesSupply[id] = _typesSupply[id] + _amount;
        }

        // Set Price
        setPrices(id, _price);

        // Event
        emit Add(id, _amount, _price);
    }

    // Remove Token
    function remove(string memory _key, uint256 _amount) public onlyOwner {
        (uint256 id, bytes32 hash) = getType(_key);

        if (id != 0) {
            // Supply validation
            require(
                balanceOf(owner(), id) >= _amount,
                "Insufficient supply."
            );

            // Burn
            _burn(owner(), id, _amount);

            // Remove Supply
            _typesSupply[id] = _typesSupply[id].sub(_amount);

            if (_typesSupply[id] == 0) {
                // Remove Type
                delete _types[hash];

                // Remove Supply
                delete _typesSupply[id];

                // Remove Price
                delete prices[id];
            }

            // Event
            emit Remove(id, _amount);
        }
    }

    // Types
    function getType(
        string memory _key
    ) public view returns (uint256, bytes32) {
        bytes32 _hash = keccak256(abi.encodePacked(_key));
        return (_types[_hash], _hash);
    }

    function setType(
        string memory _key
    ) internal returns (uint256, bytes32) {
        bytes32 _hash = keccak256(abi.encodePacked(_key));
        _types[_hash] = _id;
        _id++;
        return (_types[_hash], _hash);
    }

    // Functions
    function uri(
        uint256 id
    ) public view virtual override returns (string memory) {
        return string(abi.encodePacked(super.uri(id), id.toString(), ".json"));
    }

    function isFoundation(address _address) internal view returns (bool) {
        bool A = _address == owner();
        bool B = _address == address(0);
        bool C = _address == terminal;
        bool D = _address == provider;
        return A || B || C || D;
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal virtual override {}

    function _afterTokenTransfer(
        address,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory,
        bytes memory
    ) internal virtual override {
        for (uint256 i = 0; i < ids.length; i++) {
            // Parameters
            uint256 id = ids[i];

            if (!isFoundation(from)) {
                uint256 balanceFrom = balanceOf(from, id);

                // If the sender has no more tokens with id
                if (balanceFrom == 0) {
                    _holders[id].remove(from);

                    if (id >= LEGIONARY_TYPE_1 && id <= LEGIONARY_TYPE_8) {
                        _legionaries.remove(from);
                    }
                }
            }

            if (!isFoundation(to)) {
                uint256 balanceTo = balanceOf(to, id);

                // If the recipient has any tokens with id
                if (balanceTo > 0) {
                    _holders[id].add(to);

                    if (id >= LEGIONARY_TYPE_1 && id <= LEGIONARY_TYPE_8) {
                        _legionaries.add(to);
                    }
                }
            }
        }
    }
}
"
    },
    "contracts/CyberLegionFinance.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

// OpenZeppelin
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/math/SafeCast.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

// Chainlink
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";

contract CyberLegionFinance is Ownable {
    // Library
    using SafeMath for uint256;

    // Price Oracle
    AggregatorV3Interface private _priceFeed;

    // Price
    uint256 public price = 125; // USD
    mapping(uint256 => uint256) public prices; // Token Id => Price Fiat USD
    uint256 public factor = (10 ** 8);

    constructor() {
        // Price Oracle
        _priceFeed = AggregatorV3Interface(
            0x0567F2323251f0Aab15c8dFb1967E4e8A7D42aeE
        );
    }

    // Functions
    function getLatestPrice() public view returns (int256) {
        (, int256 _price, , , ) = _priceFeed.latestRoundData();
        return _price;
    }

    function getLatestPriceInBNB(
        uint256 _amount
    ) public view returns (uint256) {
        uint256 latestPrice = SafeCast.toUint256(getLatestPrice());
        require(latestPrice > 0, "Invalid price feed.");

        // Convert USD to BNB
        uint256 temp = _amount.mul(factor);
        temp = temp.mul(1e18);
        uint256 _price = temp.div(latestPrice);

        return _price;
    }

    function setPrice(uint256 _price) public onlyOwner {
        price = _price;
    }

    function setPrices(uint256 _tokenId, uint256 _price) public onlyOwner {
        prices[_tokenId] = _price;
    }

    function setPriceFeed(address _address) external onlyOwner {
        _priceFeed = AggregatorV3Interface(_address);
    }
}
"
    },
    "contracts/CyberLegionRandom.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

// OpenZeppelin
import "@openzeppelin/contracts/access/Ownable.sol";

// Chainlink
import "@chainlink/contracts/src/v0.8/VRFV2WrapperConsumerBase.sol";

contract CyberLegionRandom is Ownable, VRFV2WrapperConsumerBase {
    // Random Oracle Parameters
    uint256 private _feeRandomFeed;

    // Random Oracle Variables
    uint256 internal randomNumber;
    bool internal randomNumberReady = false;
    uint256 private _randomNumberRequestId;

    // Parameters
    uint256 private _randomSeed = 0;

    // Events
    event ExternalRandomNumberGenerated(
        uint256 randomNumber,
        uint256 requestId
    );

    constructor()
        VRFV2WrapperConsumerBase(
            0x404460C6A5EdE2D891e8297795264fDe62ADBB75, // LINK Token
            0x721DFbc5Cfe53d32ab00A9bdFa605d3b8E1f3f42 // VRF Wrapper
        )
    {
        // Random Oracle
        _feeRandomFeed = 0.005 * 10 ** 18; // 0.005 LINK
    }

    // Functions
    function internalRandomGenerator() internal returns (uint256) {
        // Generate
        bytes32 hash = keccak256(
            abi.encodePacked(
                block.timestamp,
                block.prevrandao,
                msg.sender,
                _randomSeed
            )
        );

        // Increment
        _randomSeed++;

        return uint256(hash);
    }

    function externalRandomGenerator(uint32 _callbackGasLimit) external onlyOwner returns (uint256) {
        require(
            LINK.balanceOf(address(this)) >= _feeRandomFeed,
            "Insufficient LINK tokens amount."
        );

        return requestRandomness(_callbackGasLimit, 3, 1);
    }

    function fulfillRandomWords(
        uint256 _requestId,
        uint256[] memory _randomWords
    ) internal virtual override {
        // Set
        randomNumber = _randomWords[0];
        randomNumberReady = true;
        _randomNumberRequestId = _requestId;

        // Event
        emit ExternalRandomNumberGenerated(_randomWords[0], _requestId);
    }

    function setFeeRandomFeed(uint32 _fee) external onlyOwner {
        _feeRandomFeed = _fee;
    }
}
"
    }
  },
  "settings": {
    "optimizer": {
      "enabled": true,
      "runs": 1000
    },
    "outputSelection": {
      "*": {
        "*": [
          "evm.bytecode",
          "evm.deployedBytecode",
          "devdoc",
          "userdoc",
          "metadata",
          "abi"
        ]
      }
    },
    "libraries": {}
  }
