      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";

contract UserBind is Ownable {
    address public root;

    mapping(address => address) public userBinds;

    event BindInfo(address indexed user, address father);

    constructor() {
        root = address(this);
    }

    function addBind(address father) public payable {
        require(msg.value >= 0.0001 ether, "fee error");
        require(userBinds[father] != address(0), "father error");
        require(userBinds[msg.sender] == address(0), "binded");
        userBinds[msg.sender] = father;
        emit BindInfo(msg.sender, father);
    }

    function batchBind(
        address[] memory users,
        address[] memory fathers
    ) public onlyOwner {
        for (uint i = 0; i < users.length; i++) {
            userBinds[users[i]] = fathers[i];
        }
    }

    function bigPoint(address bigAddress) public onlyOwner {
        userBinds[bigAddress] = root;
        emit BindInfo(bigAddress, root);
    }

    function deleteBind(address user) public onlyOwner {
        delete userBinds[user];
        emit BindInfo(user, address(0));
    }

    function withdraw(address payable recipient) public onlyOwner {
        recipient.transfer(address(this).balance);
    }
}
