      "content": "pragma solidity 0.6.12;

import "./libs/EBEP20.sol";
import '@pancakeswap/pancake-swap-lib/contracts/access/Ownable.sol';
import '@pancakeswap/pancake-swap-lib/contracts/math/SafeMath.sol';
import "./libs/IUniswapV2Pair.sol";
import "./libs/IUniswapV2Factory.sol";
import "./libs/IUniswapV2Router.sol";

// Every 5% transaction, collet it in the token. Then when swap&liquify time, do it, but don't send to liquidity. send to marketing wallet.

contract XplayToken is EBEP20 {
    using SafeMath for uint256;
    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;
    uint256 public _totalSupply = 500000000000000000000000000; // 500 M tokens
    uint256 public swapTokensAtAmount = 2 * (10 ** 5) * (10**18); // 0.2m = Threshold to swap
    address public marketingWallet;
    uint256 public marketingFee = 2;
    mapping (address => bool) private _isExcludedFromFees;
    bool private swapping;
    mapping (address => bool) public automatedMarketMakerPairs;

    event UpdateUniswapV2Router(address indexed newAddress, address indexed oldAddress);
    event SendDividends(
    	uint256 tokensSwapped,
    	uint256 amount
    );
    event ExcludeFromFees(address indexed account, bool isExcluded);
    event MarketingWalletUpdated(address indexed newLiquidityWallet, address indexed oldLiquidityWallet);
    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);


    receive() external payable {

  	}

    constructor() EBEP20('Xenon Play', 'XPLAY') public {
        /******************************************************/
        /** TESTNET **/
        /******************************************************/
        // test router official address is: 0xD99D1c33F9fC3444f8101754aBC46c52416550D1
        // Pancake front end doesnt work with testnet. We are using 
        // https://pancake.kiemtienonline360.com which works with a different router address (0x9Ac64Cc6e4415144C455BD8E4837Fea55603e5c3) 

        // TODO - change this to production address
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(address(0x10ED43C718714eb63d5aA57B78B54704E256024E));
        // IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(address(0x9Ac64Cc6e4415144C455BD8E4837Fea55603e5c3));


        address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        uniswapV2Router = _uniswapV2Router;
        uniswapV2Pair = _uniswapV2Pair;

        _setAutomatedMarketMakerPair(_uniswapV2Pair, true);
        excludeFromFees(address(this), true);
        excludeFromFees(owner(), true);
        marketingWallet = owner();
        super._mint(owner(), _totalSupply);
    }

    function updateMarketingVariables(
        uint256 _marketingFee, 
        uint256 _swapTokensAtAmount
    ) public onlyOwner{
        marketingFee = _marketingFee;
        swapTokensAtAmount = _swapTokensAtAmount;
    }

    function updateUniswapV2Router(address newAddress) public onlyOwner {
        require(newAddress != address(uniswapV2Router), "XPLAY: The router already has that address");
        emit UpdateUniswapV2Router(newAddress, address(uniswapV2Router));
        uniswapV2Router = IUniswapV2Router02(newAddress);
    }

    function swapAndSendDividends(uint256 tokens) private {
        swapTokensForEth(tokens);
        uint256 dividends = address(this).balance;
        (bool success,) = address(marketingWallet).call{value: dividends}("");

        if(success) {
   	 		emit SendDividends(tokens, dividends);
        }
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();
        _approve(address(this), address(uniswapV2Router), tokenAmount);
        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        if(amount == 0) {
            super._transfer(from, to, 0);
            return;
        }
		uint256 contractTokenBalance = balanceOf(address(this));
        bool canSwap = contractTokenBalance >= swapTokensAtAmount;
        if(
            canSwap &&
            !swapping &&
            !automatedMarketMakerPairs[from] &&
            from != marketingWallet &&
            to != marketingWallet
        ) {
            swapping = true;
            uint256 sellTokens = balanceOf(address(this));
            swapAndSendDividends(sellTokens);
            swapping = false;
        }
        bool takeFee = !swapping;
        // if any account belongs to _isExcludedFromFee account then remove the fee

        if(_isExcludedFromFees[from] || _isExcludedFromFees[to]) {
            takeFee = false;
        }
        if(takeFee) {
        	uint256 fees = amount.mul(marketingFee).div(100);
        	amount = amount.sub(fees);
            super._transfer(from, address(this), fees);
        }
        super._transfer(from, to, amount);
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        // require(_isExcludedFromFees[account] != excluded, "XPLAY: Account is already the value of 'excluded'");
        _isExcludedFromFees[account] = excluded;

        emit ExcludeFromFees(account, excluded);
    }

    function setAutomatedMarketMakerPair(address pair, bool value) public onlyOwner {
        require(pair != uniswapV2Pair, "XPLAY: The PancakeSwap pair cannot be removed from automatedMarketMakerPairs");

        _setAutomatedMarketMakerPair(pair, value);
    }

    function _setAutomatedMarketMakerPair(address pair, bool value) private {
        require(automatedMarketMakerPairs[pair] != value, "XPLAY: Automated market maker pair is already set to that value");
        automatedMarketMakerPairs[pair] = value;
        emit SetAutomatedMarketMakerPair(pair, value);
    }


    function updatemarketingWallet(address newmarketingWallet) public onlyOwner {
        require(newmarketingWallet != marketingWallet, "XPLAY: The liquidity wallet is already this address");
        excludeFromFees(newmarketingWallet, true);
        emit MarketingWalletUpdated(newmarketingWallet, marketingWallet);
        marketingWallet = newmarketingWallet;
    }


}
