contract SwapRoute is ReentrancyGuard, Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    IERC20 public LRToken;

    IERC20 public usdtToken;

    ISwapFactory public factory;
    address public pair;

    uint256 public RATE_DENOMINATOR = 100;

    uint256 public buyFeeRate = 0;
    uint256 public sellFeeRate = 10;

    address public feeTo;
    address public burnFeeTo;

    event Buy(address indexed user, address indexed otherUser, uint256 amountIn, uint256 amountOut, uint256 fees, uint256 burnAmount);
    event Burn(address indexed user, address indexed otherUser, uint256 burnAmount, uint256 fees);
    event Sell(address indexed user, uint256 amountIn, uint256 amountOut, uint256 fees, uint256 userAmount);
    event SetFeeTo(address indexed feeTo,address indexed burnFeeTo);
    event SetFeeRate(uint256 buyFeeRate,uint256  sellFeeRate);
    event SetFactory(address indexed factory);


    constructor(address LRToken_, address usdtToken_, address factory_, address feeTo_, address burnFeeTo_) public {
        LRToken = IERC20(LRToken_);
        usdtToken = IERC20(usdtToken_);
        feeTo = feeTo_;
        burnFeeTo = burnFeeTo_;
        factory = ISwapFactory(factory_);
        pair = factory.getPair(LRToken_, usdtToken_);
    }

    function setFactory(address factory_) external onlyOwner {
        factory = ISwapFactory(factory_);
        pair = factory.getPair(address(LRToken), address(usdtToken));
        emit SetFactory(factory_);
    }

    function setFeeTo(address feeTo_,address burnFeeTo_) external onlyOwner {
        feeTo = feeTo_;
        burnFeeTo = burnFeeTo_;
        emit SetFeeTo(feeTo_,burnFeeTo_);
    }


    function setFeeRate(uint256  buyFeeRate_,uint256  sellFeeRate_) external onlyOwner {
        buyFeeRate = buyFeeRate_;
        sellFeeRate = sellFeeRate_;
        emit SetFeeRate(buyFeeRate_,sellFeeRate_);
    }

    modifier ensure(uint256 deadline)  {
        require(deadline >= block.timestamp, "Swap: EXPIRED");
        _;
    }

    function preAddLiquidity(
        address tokenA,
        address tokenB,
        uint amountADesired,
        uint amountBDesired,
        uint amountAMin,
        uint amountBMin
    ) public view returns (uint amountA, uint amountB) {
        (uint reserveA, uint reserveB) = factory.getReserves(tokenA, tokenB);
        if (reserveA == 0 && reserveB == 0) {
            (amountA, amountB) = (amountADesired, amountBDesired);
        } else {
            uint amountBOptimal = factory.quote(amountADesired, reserveA, reserveB);
            if (amountBOptimal <= amountBDesired) {
                require(amountBOptimal >= amountBMin, 'Router: INSUFFICIENT_B_AMOUNT');
                (amountA, amountB) = (amountADesired, amountBOptimal);
            } else {
                uint amountAOptimal = factory.quote(amountBDesired, reserveB, reserveA);
                assert(amountAOptimal <= amountADesired);
                require(amountAOptimal >= amountAMin, 'Router: INSUFFICIENT_A_AMOUNT');
                (amountA, amountB) = (amountAOptimal, amountBDesired);
            }
        }
    }

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint amountADesired,
        uint amountBDesired,
        uint amountAMin,
        uint amountBMin,
        address to
    ) public returns (uint amountA, uint amountB, uint liquidity) {
        require(ISwapFactory(factory).isWhiteList(msg.sender), "Router:invalid caller");
        (amountA, amountB) = preAddLiquidity(tokenA, tokenB, amountADesired, amountBDesired, amountAMin, amountBMin);
        IERC20(tokenA).safeTransferFrom(msg.sender,pair, amountA);
        IERC20(tokenB).safeTransferFrom(msg.sender,pair, amountB);
        liquidity = ISwapPair(pair).mint(to);
    }



    // function burn(
