    function burn(
        uint256 burnAmount,
        uint256 deadline,
        address otherUser
    ) external ensure(deadline) nonReentrant {
        require(burnAmount > 0, "Router: INSUFFICIENT_INPUT_AMOUNT");
        require (LRToken.balanceOf(0x000000000000000000000000000000000000dEaD) <= 979000000 * 10 ** 18);
        address[] memory path = new address[](2);
        path[0] = address(usdtToken);
        path[1] = address(LRToken);
        uint256[] memory amounts = factory.getAmountsOut(burnAmount, path);
        LRToken.safeTransferFrom(msg.sender, 0x000000000000000000000000000000000000dEaD, amounts[1]);

        emit Burn(msg.sender, otherUser, amounts[1], 0);
    }

    function buy(
        uint256 amountIn,
        uint256 amountOutMin,
        uint256 deadline,
        address otherUser
    ) external ensure(deadline) nonReentrant {
        address[] memory path = new address[](2);
        path[0] = address(usdtToken);
        path[1] = address(LRToken);

        uint256[] memory amounts = factory.getAmountsOut(amountIn, path);
        require(
            amounts[1] >= amountOutMin,
            "Router: INSUFFICIENT_OUTPUT_AMOUNT"
        );

 




        if (LRToken.balanceOf(0x000000000000000000000000000000000000dEaD) >= 979000000 * 10 ** 18) 
        {
              usdtToken.safeTransferFrom(msg.sender, feeTo, amounts[0]);
         }else
        {
            usdtToken.safeTransferFrom(msg.sender, pair, amounts[0]);
        }
      
        _swap(amounts, path, address(this));
        uint256 amountOut = amounts[1];
        uint256 fees = amountOut.mul(buyFeeRate).div(RATE_DENOMINATOR);
        if (fees > 0) {
            LRToken.safeTransfer(feeTo, fees);
        }
        uint256 burnAmount = amountOut.sub(fees);
        if (burnAmount > 0) {
            LRToken.safeTransfer(0x000000000000000000000000000000000000dEaD, burnAmount);
        }
        emit Buy(msg.sender, otherUser, amountIn, amountOut, fees, burnAmount);
    }


    function sell(
        uint256 amountIn,
        uint256 amountOutMin,
        uint256 deadline
    ) external ensure(deadline) nonReentrant {
        address[] memory path = new address[](2);
        path[0] = address(LRToken);
        path[1] = address(usdtToken);

        uint256[] memory amounts = factory.getAmountsOut(amountIn, path);
        require(
            amounts[1] >= amountOutMin,
            "Router: INSUFFICIENT_OUTPUT_AMOUNT"
        );


    if (LRToken.balanceOf(0x000000000000000000000000000000000000dEaD) >= 979000000 * 10 ** 18) 
        {
              LRToken.safeTransferFrom(msg.sender, feeTo, amounts[0]);
         }else
        {
        LRToken.safeTransferFrom(msg.sender, pair, amounts[0]);
        }
      

        // LRToken.safeTransferFrom(msg.sender, pair, amounts[0]);
        _swap(amounts, path, address(this));

        uint256 amountOut = amounts[1];
        ISwapPair(pair).burnLR(amounts[0]);


        uint256 fees = amountOut.mul(sellFeeRate).div(RATE_DENOMINATOR);
        if (fees > 0) {
            usdtToken.safeTransfer(feeTo, fees);
        }
        uint256 userAmount = amountOut.sub(fees);
        if (userAmount > 0) {
            usdtToken.safeTransfer(msg.sender, userAmount);
        }
        emit Sell(msg.sender, amounts[0], amountOut,fees, userAmount);
    }

    function _swap(
        uint256[] memory amounts,
        address[] memory path,
        address _to
    ) internal virtual {
        for (uint256 i; i < path.length - 1; i++) {
            (address input, address output) = (path[i], path[i + 1]);
            (address token0,) = factory.sortTokens(input, output);
            uint256 amountOut = amounts[i + 1];
            (uint256 amount0Out, uint256 amount1Out) =
                input == token0
                    ? (uint256(0), amountOut)
                    : (amountOut, uint256(0));
            address to =
                i < path.length - 2
                    ? factory.pairFor(output, path[i + 2])
                    : _to;
            ISwapPair(factory.pairFor(input, output)).swap(amount0Out, amount1Out, to, new bytes(0));
        }
    }



