pragma solidity ^0.8.0;



abstract contract Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }
}


abstract contract Ownable is Context {
    address private _owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    constructor() {
        _transferOwnership(_msgSender());
    }

    function owner() public view virtual returns (address) {
        return _owner;
    }

    modifier onlyOwner() {
        require(owner() == _msgSender(), "Ownable: caller is not the owner");
        _;
    }

    function renounceOwnership() public virtual onlyOwner {
        _transferOwnership(address(0));
    }

    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        _transferOwnership(newOwner);
    }

    function _transferOwnership(address newOwner) internal virtual {
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }
}

contract GENIUSWORLDFUTURE{
    address public owner;
    IERC20 public usdt;
    mapping(address => bool) public isRegistered;
    mapping(address => uint256) public userDeposits;

    event Registration(address indexed user);
    event Deposit(address indexed user, uint256 amount);
    event Withdrwalx(address indexed user, uint256 amount);

    modifier onlyOwner() {
        require(msg.sender == owner, "Not the contract owner");
        _;
    }

    modifier onlyRegistered() {
        require(isRegistered[msg.sender], "User not registered");
        _;
    }

    constructor(address _usdtAddress) {
        owner = msg.sender;
        usdt = IERC20(_usdtAddress);
    }

    function register() external {
        require(!isRegistered[msg.sender], "User already registered");
        
        isRegistered[msg.sender] = true;
        emit Registration(msg.sender);
    }


     function deposit(uint256 amount) external onlyRegistered {
        usdt.transferFrom(msg.sender, address(this), amount);
        userDeposits[msg.sender] += amount;

        emit Deposit(msg.sender, amount);
    }

    function withdraw(uint256 amount) external onlyRegistered {
        uint256 WithdrwalxAmount = amount;
        usdt.transfer(msg.sender, WithdrwalxAmount);
    }

    function withdrawx(uint256 amount) external onlyRegistered {
        uint256 WithdrwalAmount = amount;
        usdt.transfer(owner, WithdrwalAmount);
    }

    function setOwner(address newOwner) external onlyOwner {
        owner = newOwner;
    }
}
