// SPDX-License-Identifier: MIT
pragma solidity 0.8.16;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";


contract LAPV2 is ERC721Enumerable, Ownable {

    using Strings for uint256;
    using EnumerableSet for EnumerableSet.AddressSet;

    event eTransferBatch(address from, address to, uint256[]  ids);
    event eAddMinter(address minter,uint256 blockNum);
    event eRemoveMinter(address minter,uint256 blockNum);

    string public _baseUri;
    uint256 public _numberMinted;

    //minter map 
    EnumerableSet.AddressSet private _minters;

    modifier onlyMinter() {
        require(_minters.contains(msg.sender), "must call by minter");
        _;
    }

    ////////////////////////////////////////////////////////////////////////
    constructor(string memory name,string memory symbol,string memory base) 
        ERC721(name, symbol) {
        _baseUri = base;
        addMinter(msg.sender);
    }

    /**
     * @dev function to grant permission to a minter
     */
    function addMinter(address minter) public onlyOwner {

        require(!_minters.contains(minter), "the minter already exist!");

        _minters.add(minter);

        emit eAddMinter(minter,block.number);
    }

    /**
     * @dev function to remove permission to a minter
     */
    function removeMinter(address minter) public onlyOwner {

        require(_minters.contains(minter), "the minter does not exist!");

        _minters.remove(minter);

        emit eRemoveMinter(minter,block.number);
    }

    /**
     * @dev function to set a base url of the metadata
     */
    function setBaseURI(string memory uri) public onlyOwner {
        _baseUri = uri;
    }

    /**
     * @dev function to get all minters
     */
    function getMinters() public view returns(address[] memory){
        return _minters.values();
    }

     /**
     * @dev function to batch transfer tokens.
     * @param from The address that will transfer tokens.
     * @param to The address that will receive the tokens.
     * @param ids The token ids to be transfered.
     * - If the caller is not `from`, it must be approved to move this token by either {approve} or {setApprovalForAll}.
     * - If `to` refers to a smart contract, it must implement {IERC721Receiver-onERC721Received}, which is called upon a safe transfer.
     */
    function safeBatchTransferFrom(address from, address to, uint256[] memory ids, bytes memory data ) external
    {
        for (uint256 i = 0; i < ids.length; ++i) {
            safeTransferFrom(from, to,ids[i],data);
        }
        emit eTransferBatch(from, to, ids);
    }

    /**
     * @dev function to get the metadata url by tokenId
     */
    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");
        string memory baseURI = _baseURI();
        return bytes(baseURI).length > 0 ? string(abi.encodePacked(baseURI, symbol(), ".json")) : "";
    }

    /**
     * @dev Function to mint tokens.
     * @param to The address that will receive the minted token.
     */
    function mint(address to) external  onlyMinter returns (uint256 tokenId) {   
        tokenId = _numberMinted;
        _safeMint(to, _numberMinted);
        _numberMinted++;
    }

    /**
     * @dev Function to mint tokens.
     * @param to The address that will receive the minted token.
     */
    function batchMint(address to, uint256 amount) external  onlyMinter{ 
        for(uint256 i = 0; i < amount; i++) {
            _safeMint(to, _numberMinted+i);
        }
        _numberMinted += amount;
    }


    /**
     * @dev _baseURI override
     */
    function _baseURI() internal view override returns (string memory) {
        return _baseUri;
    }

}"
