      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "../interface/IEvent.sol";
import "../interface/IUSDB.sol";
import "../interface/ITuna.sol";
import "../interface/IPledgedMining.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract Tuna is ITuna, ERC20, Ownable{

    using ECDSA for bytes32;

    address public _usdbAddress;
    address public _signer;
    address public _boraNFTAddr;
    address public _pledgedMiningAddress;
    address public _eventAddress;
    uint256 public _dailyTotalTuna;
    mapping(uint256 => uint256) public _claimedTotalTuna;// total claimed tuna by everyone in a day
    mapping(address => uint256) public lastClaimTuna;
    mapping(uint256 => bool) public claimTunaSignId;
    mapping(uint256 => bool) public claimBnbSignId;

    constructor (
        address usdbAddress,
        address signer,
        address eventAddress,
        uint256 dailyTotalTuna
    ) ERC20("Tuna","Tuna") {
        _usdbAddress = usdbAddress;
        _signer = signer;
        _eventAddress = eventAddress;
        _dailyTotalTuna = dailyTotalTuna;
        _mint(_msgSender(), 100000000e18);
    }

    function claim(
        uint256 id,
        uint256 burnUsdbAmount,
        uint256 claimTunaAmount,
        bool isPledged,
        bytes memory sign
        ) public {

        require(claimTunaSignId[id] == false, "Tuna_claim: this id is used");
        claimTunaSignId[id] = true;

        bytes32 hash = keccak256(abi.encodePacked("Tuna_claim", id, _msgSender(), burnUsdbAmount, claimTunaAmount));
        address signer = hash.toEthSignedMessageHash().recover(sign);
        require(signer == _signer, "Tuna_claim: invalid signature");

        uint256 today = getDay(block.timestamp);
        uint256 lastday = lastClaimTuna[_msgSender()];
        require(lastday < today,"duplicate claim");
        lastClaimTuna[_msgSender()] = today;

        _claimedTotalTuna[today] += claimTunaAmount;
        require(_claimedTotalTuna[today] <= _dailyTotalTuna, "Tuna_claim: dailyTotalTuna is beyong the limit");

        IUSDB(_usdbAddress).burnByClaimTuna(_msgSender(), burnUsdbAmount);

        _mint(_msgSender(), claimTunaAmount);

        if(isPledged){
            require(
                IPledgedMining(_pledgedMiningAddress).stakeByTunaClaim(_msgSender(), claimTunaAmount),
                "Tuna_claim: stakeByTunaClaim is failed."
            );
        }

        bytes memory log = abi.encode(_msgSender(), burnUsdbAmount, claimTunaAmount, address(this));
        IEvent(_eventAddress).eventOut(1, log);
    }

    function claimBnb(uint256 id, uint256 clearDay, uint256 amount, bytes memory signature) public {
        require(clearDay == getDay(block.timestamp), "Tuna_claimBnb: today is not claim day.");
        require(claimBnbSignId[id] == false, "Tuna_claimBnb: this id is used");
        claimBnbSignId[id] = true;

        address signer = keccak256(abi.encodePacked("Tuna_claimBnb", id, _msgSender(), clearDay, amount))
        .toEthSignedMessageHash().recover(signature);
        require(signer == _signer, "Tuna_claimBnb: invalid signature");

        payable(_msgSender()).transfer(amount);

        bytes memory log = abi.encode(_msgSender(), amount, address(this));
        IEvent(_eventAddress).eventOut(2, log);

    }

    function burnThroughUpdateBoraNFT(address owner, uint256 amount) external {
        require(_msgSender() == _boraNFTAddr, "Tuna_burnTUBN: only boraNFT can call.");
        _burn(owner, amount);

        bytes memory log = abi.encode(owner, amount, address(this));
        IEvent(_eventAddress).eventOut(3, log);

    }

    function setBoraNFTAddr(address boraNFTAddr) external onlyOwner returns(bool) {
        require(boraNFTAddr != address(0), "Tuna_setBoraNFTAddr: invalid address.");
        _boraNFTAddr = boraNFTAddr;
        return true;
    }

    function setPledgedMiningAddress(address PledgedMiningAddress) public onlyOwner returns(bool){
        require(PledgedMiningAddress != address(0), "Tuna_setPledgedMiningAddress: invalid address.");
        _pledgedMiningAddress = PledgedMiningAddress;
        return true;
    }

    function setSigner(address signer) public onlyOwner returns(bool){
        _signer = signer;
        return true;
    }

    function setDailyTotalTuna(uint256 newDailyTotalTuna) public onlyOwner returns(bool){
        _dailyTotalTuna = newDailyTotalTuna;
        return true;
    }
    function setUsdb(address newUsdb) public onlyOwner {
        _usdbAddress = newUsdb;
    }
    function setEvent(address newEvent) public onlyOwner{
        _eventAddress = newEvent;
    }
    function getDay(uint256 timestamp) public pure returns(uint256){
        return timestamp / 86400;
    }

    receive() external payable {

    }
}
"
