// https://docs.synthetix.io/contracts/source/contracts/pausable
contract Pausable is Owned {
    uint public lastPauseTime;
    bool public paused;

    constructor() internal {
        // This contract is abstract, and thus cannot be instantiated directly
        require(owner != address(0), "Owner must be set");
        // Paused will be false, and lastPauseTime will be 0 upon initialisation
    }

    /**
     * @notice Change the paused state of the contract
     * @dev Only the contract owner may call this.
     */
    function setPaused(bool _paused) external onlyOwner {
        // Ensure we're actually changing the state before we do anything
        if (_paused == paused) {
            return;
        }

        // Set our paused state.
        paused = _paused;

        // If applicable, set the last pause time.
        if (paused) {
            lastPauseTime = now;
        }

        // Let everyone know that our pause state has changed.
        emit PauseChanged(paused);
    }

    event PauseChanged(bool isPaused);

    modifier notPaused {
        require(!paused, "This action cannot be performed while the contract is paused");
        _;
    }
}

pragma solidity ^0.5.16;

// Inheritance

contract MeowStakingRewards is ReentrancyGuard, Pausable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    /* ========== STATE VARIABLES ========== */

    IERC20 public rewardsToken;
    IERC20 public stakingToken;
    uint256 public periodStart;
    uint256 public periodFinish;
    uint256 public rewardsDuration;    
    uint256 public fixedAPY;
    uint256 public maxStakeAmount;
    uint256 public maxStake;
    uint256 public totalStake;

    
    struct TimedStake {
      mapping(uint256 => uint256) stakes;
      uint256[] stakeTimes;
    }

    mapping(address => TimedStake) timeStakeInfo;
    mapping(address => bool) walletAdded;

    uint256 private _totalSupply;
    mapping(address => uint256) private _balances;

    
    constructor(
        address _owner,
        address _rewardsToken,
        address _stakingToken
    ) public Owned(_owner) {
        rewardsToken = IERC20(_rewardsToken);
        stakingToken = IERC20(_stakingToken);
        
    }

    function totalSupply() external view returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) external view returns (uint256) {
        return _balances[account];
    }

    function earned(address account) public view returns (uint256) {
        if(now > periodFinish)
            return  (_balances[account].mul(fixedAPY).div(100)).mul(rewardsDuration).div(365 days);
        
        return  (_balances[account].mul(fixedAPY).div(100)).mul(now - periodStart).div(365 days);
    }

    function stake(uint256 amount) external nonReentrant notPaused{
        require(amount > 0, "Cannot stake 0");
        require(periodFinish != 0, "staking is not start");

        require(_balances[msg.sender] + amount <= maxStakeAmount, "limit reached");
        require(totalStake < maxStake, "max stake reached");

        if(walletAdded[msg.sender] == false) {
           totalStake = totalStake + 1;
           walletAdded[msg.sender] = true;
        }
        _totalSupply = _totalSupply.add(amount);
        _balances[msg.sender] = _balances[msg.sender].add(amount);
        stakingToken.safeTransferFrom(msg.sender, address(this), amount);

        TimedStake storage _timedStake = timeStakeInfo[msg.sender];
        _timedStake.stakes[now] = amount;
        _timedStake.stakeTimes.push(now);
        timeStakeInfo[msg.sender] = _timedStake;
        emit Staked(msg.sender, amount);
    }

    function dealwithLockdown(uint256 amount,address account) internal returns(uint256){
        uint256 _total = amount;
        uint256 reward;
        TimedStake storage _timedStake = timeStakeInfo[account];
         for (uint8 index = 0; index < _timedStake.stakeTimes.length; index++) {
           if (_total > 0) {
                uint256 key = _timedStake.stakeTimes[index];
                if(_total >= _timedStake.stakes[key]){
                    _total = _total.sub(_timedStake.stakes[key]);
                    reward = reward.add((_timedStake.stakes[key].mul(fixedAPY).div(100)).mul(rewardsDuration).div(365 days));
                    delete( _timedStake.stakes[key]);
                    delete( _timedStake.stakeTimes[index]);
                }else{
                    _timedStake.stakes[key] = _timedStake.stakes[key].sub(_total);
                    _total = 0;
                    break;
                }
            
           }
        }
        return reward;
    }

    function exit() external {
        withdraw(_balances[msg.sender]);
    }

    function withdraw(uint256 amount) public nonReentrant {
        require(amount > 0, "Cannot withdraw 0");
        require(now > periodFinish, "period not finnish");
        
        _totalSupply = _totalSupply.sub(amount);
        _balances[msg.sender] = _balances[msg.sender].sub(amount);
        uint256 reward = dealwithLockdown(amount, msg.sender);
        stakingToken.safeTransfer(msg.sender, amount);
        rewardsToken.safeTransfer(msg.sender, reward);
        
        emit Withdrawn(msg.sender, amount);
        emit RewardPaid(msg.sender, reward);
    }

    function emergencyWithdraw(uint256 amount) public nonReentrant {
        require(amount > 0, "Cannot withdraw 0");
        require(now > periodFinish, "period not finnish");

        _totalSupply = _totalSupply.sub(amount);
        _balances[msg.sender] = _balances[msg.sender].sub(amount);

        stakingToken.safeTransfer(msg.sender, amount);
       
        emit Withdrawn(msg.sender, amount);
    }

    /* ========== RESTRICTED FUNCTIONS ========== */

    function init(
        uint256 _rewardsDuration,
        uint256 _fixedAPY,
        uint256 _maxStakeAmount,
        uint256 _maxStake) external onlyOwner {
        require(periodFinish == 0, "reward is set");
        
        fixedAPY = _fixedAPY;
        maxStakeAmount = _maxStakeAmount;
        maxStake = _maxStake;
        rewardsDuration = _rewardsDuration;
        periodStart = now;
        periodFinish = now + rewardsDuration;
    }

    // Added to support recovering LP Rewards from other systems such as BAL to be distributed to holders
    function recoverERC20(address tokenAddress, uint256 tokenAmount) external onlyOwner {
        IERC20(tokenAddress).safeTransfer(owner, tokenAmount);
        emit Recovered(tokenAddress, tokenAmount);
    }

    function setRewardsDuration(uint256 _rewardsDuration) external onlyOwner {
        require(
            block.timestamp > periodFinish,
            "Previous rewards period must be complete before changing the duration for the new period"
        );
        rewardsDuration = _rewardsDuration;
        emit RewardsDurationUpdated(rewardsDuration);
    }

    
    /* ========== EVENTS ========== */

    event RewardAdded(uint256 reward);
    event Staked(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);
    event RewardPaid(address indexed user, uint256 reward);
    event RewardsDurationUpdated(uint256 newDuration);
    event Recovered(address token, uint256 amount);

}
