        );
    }

    // **** SWAP ****
    // requires the initial amount to have already been sent to the first pair
    function _swap(
        uint256[] memory amounts,
        address[] memory path,
        address _to,
        address _final
    ) internal virtual {
        for (uint256 i; i < path.length - 1; i++) {
            (address input, address output) = (path[i], path[i + 1]);
            (address token0, ) = POLv2Library.sortTokens(input, output);
            uint256 amountOut = amounts[i + 1];
            (uint256 amount0Out, uint256 amount1Out) = input == token0
                ? (uint256(0), amountOut)
                : (amountOut, uint256(0));
            address to = i < path.length - 2
                ? IPOLv2Factory(factory).getPair(output, path[i + 2])
                : _to;
            IPOLv2Pair(IPOLv2Factory(factory).getPair(input, output)).swap(
                amount0Out,
                amount1Out,
                to,
                _final
            );
        }
    }

    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    )
        external
        virtual
        override
        ensure(deadline)
        returns (uint256[] memory amounts)
    {
        amounts = POLv2Library.getAmountsOut(factory, amountIn, path);
        require(
            amounts[amounts.length - 1] >= amountOutMin,
            "AmmRouter: INSUFFICIENT_OUTPUT_AMOUNT"
        );
        address pair = IPOLv2Factory(factory).getPair(path[0], path[1]);
        _safeTransferFromWithPairBuyFee(
            pair,
            IERC20(path[0]),
            pair,
            amounts[0],
            isExempt[msg.sender]
        );
        _swap(amounts, path, to, to);
    }

    function swapTokensForExactTokens(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    )
        external
        virtual
        override
        ensure(deadline)
        returns (uint256[] memory amounts)
    {
        amounts = POLv2Library.getAmountsIn(factory, amountOut, path);
        require(amounts[0] <= amountInMax, "AmmRouter: EXCESSIVE_INPUT_AMOUNT");
        address pair = IPOLv2Factory(factory).getPair(path[0], path[1]);
        _safeTransferFromWithPairBuyFee(
            pair,
            IERC20(path[0]),
            pair,
            amounts[0],
            isExempt[msg.sender]
        );
        _swap(amounts, path, to, to);
    }

