    function swapETHForExactTokens(
        uint256 amountOut,
        address[] calldata path,
        address to,
        uint256 deadline
    )
        external
        payable
        virtual
        override
        ensure(deadline)
        returns (uint256[] memory amounts)
    {
        require(path[0] == WETH, "AmmRouter: INVALID_PATH");
        amounts = POLv2Library.getAmountsIn(factory, amountOut, path);
        require(amounts[0] <= msg.value, "AmmRouter: EXCESSIVE_INPUT_AMOUNT");
        IWETH(WETH).deposit{value: amounts[0]}();
        address pair = IPOLv2Factory(factory).getPair(path[0], path[1]);
        _safeTransferWithPairBuyFee(
            pair,
            IERC20(WETH),
            pair,
            amounts[0],
            isExempt[msg.sender]
        );
        _swap(amounts, path, to, to);
        // refund dust eth, if any
        if (msg.value > amounts[0])
            payable(msg.sender).sendValue(msg.value - amounts[0]);
    }

    // **** SWAP (supporting fee-on-transfer tokens) ****
    // requires the initial amount to have already been sent to the first pair
    function _swapSupportingFeeOnTransferTokens(
        address[] memory path,
        address _to,
        address _final
    ) internal virtual {
        for (uint256 i; i < path.length - 1; i++) {
            (address input, address output) = (path[i], path[i + 1]);
            (address token0, ) = POLv2Library.sortTokens(input, output);
            IPOLv2Pair pair = IPOLv2Pair(
                IPOLv2Factory(factory).getPair(input, output)
            );
            uint256 amountInput;
            uint256 amountOutput;
            {
                // scope to avoid stack too deep errors
                (uint256 reserve0, uint256 reserve1, ) = pair.getReserves();
                (uint256 reserveInput, uint256 reserveOutput) = input == token0
                    ? (reserve0, reserve1)
                    : (reserve1, reserve0);
                amountInput = IERC20(input).balanceOf(address(pair)).sub(
                    reserveInput
                );
                amountOutput = POLv2Library.getAmountOut(
                    amountInput,
                    reserveInput,
                    reserveOutput
                );
            }
            (uint256 amount0Out, uint256 amount1Out) = input == token0
                ? (uint256(0), amountOutput)
                : (amountOutput, uint256(0));
            address to = i < path.length - 2
                ? IPOLv2Factory(factory).getPair(output, path[i + 2])
                : _to;
            pair.swap(amount0Out, amount1Out, to, _final);
        }
    }

    function swapExactTokensForTokensSupportingFeeOnTransferTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external virtual override ensure(deadline) {
        address pair = IPOLv2Factory(factory).getPair(path[0], path[1]);
        _safeTransferFromWithPairBuyFee(
            pair,
            IERC20(path[0]),
            pair,
            amountIn,
            isExempt[msg.sender]
        );
        uint256 balanceBefore = IERC20(path[path.length - 1]).balanceOf(to);
        _swapSupportingFeeOnTransferTokens(path, to, to);
        require(
            IERC20(path[path.length - 1]).balanceOf(to).sub(balanceBefore) >=
                amountOutMin,
            "AmmRouter: INSUFFICIENT_OUTPUT_AMOUNT"
        );
    }

    function swapExactETHForTokensSupportingFeeOnTransferTokens(
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable virtual override ensure(deadline) {
        require(path[0] == WETH, "AmmRouter: INVALID_PATH");
        uint256 amountIn = msg.value;
        IWETH(WETH).deposit{value: amountIn}();
        address pair = IPOLv2Factory(factory).getPair(path[0], path[1]);
        _safeTransferWithPairBuyFee(
            pair,
            IERC20(WETH),
            pair,
            amountIn,
            isExempt[msg.sender]
        );
        uint256 balanceBefore = IERC20(path[path.length - 1]).balanceOf(to);
        _swapSupportingFeeOnTransferTokens(path, to, to);
        require(
            IERC20(path[path.length - 1]).balanceOf(to).sub(balanceBefore) >=
                amountOutMin,
            "AmmRouter: INSUFFICIENT_OUTPUT_AMOUNT"
        );
    }

    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external virtual override ensure(deadline) {
        require(path[path.length - 1] == WETH, "AmmRouter: INVALID_PATH");
        address pair = IPOLv2Factory(factory).getPair(path[0], path[1]);
        _safeTransferFromWithPairBuyFee(
            pair,
            IERC20(path[0]),
            pair,
            amountIn,
            isExempt[msg.sender]
        );
        _swapSupportingFeeOnTransferTokens(path, address(this), to);
        uint256 amountOut = IERC20(WETH).balanceOf(address(this));
        require(
            amountOut >= amountOutMin,
            "AmmRouter: INSUFFICIENT_OUTPUT_AMOUNT"
        );
        IWETH(WETH).withdraw(amountOut);
        payable(to).sendValue(amountOut);
    }

