    function getSells(
        IERC20 token,
        address trader
    ) external view returns (uint256 amountSold, uint256 lastSell) {
        return (
            sellTracker[token][trader].amountSold,
            sellTracker[token][trader].lastSell
        );
    }

    function getFees(
        address pair
    ) external view returns (uint16 buyFee, uint16 sellFee, address takeToken) {
        return (
            pairFees[pair].buyFee,
            pairFees[pair].sellFee,
            pairFees[pair].tokenToTake
        );
    }

    function setPairFees(
        address token0,
        address token1,
        uint16 buyFee,
        uint16 sellFee,
        bool takeToken
    ) external {
        address pair = POLv2Library.pairFor(factory, token0, token1);
        if (msg.sender != IPOLv2Factory(factory).pairFeeSetter(pair))
            revert POLv2__InvalidFeeSetter();
        if (buyFee > MAX_FEE || sellFee > MAX_FEE) revert POLv2__NoScamFee();
        address tokenToTake = takeToken ? token0 : token1;
        _setPairFees(pair, buyFee, sellFee, tokenToTake);
    }

    function setIsExempt(address sender, bool to) external {
        require(
            msg.sender == IPOLv2Factory(factory).feeSetter(),
            "Not feetosetter"
        );
        isExempt[sender] = to;
    }

    function _setPairFees(
        address pairAddress,
        uint16 buyFee,
        uint16 sellFee,
        address tokenToTake
    ) internal {
        pairFees[pairAddress].buyFee = buyFee;
        pairFees[pairAddress].sellFee = sellFee;
        pairFees[pairAddress].tokenToTake = tokenToTake;
    }

    function _safeTransferWithPairBuyFee(
        address pair,
        IERC20 token,
        address receiver,
        uint amount,
        bool exempted
    ) internal {
        PairFees storage fees = pairFees[pair];
        uint16 buyFee = fees.buyFee;
        if (exempted || buyFee == 0 || address(token) != fees.tokenToTake) {
            return token.safeTransferFrom(msg.sender, receiver, amount);
        }
        uint256 fee = (amount * buyFee) / MAX_PERCENT;
        address feeRecipient = IPOLv2Factory(factory).pairFeeReceiver(pair);
        amount -= fee;
        token.safeTransfer(feeRecipient, fee);
        token.safeTransfer(receiver, amount);
    }

    function _safeTransferFromWithPairBuyFee(
        address pair,
        IERC20 token,
        address receiver,
        uint amount,
        bool exempted
    ) internal {
        PairFees storage fees = pairFees[pair];
        uint16 buyFee = fees.buyFee;
        if (exempted || buyFee == 0 || address(token) != fees.tokenToTake) {
            return token.safeTransferFrom(msg.sender, receiver, amount);
        }
        uint256 fee = (amount * buyFee) / MAX_PERCENT;
        address feeRecipient = IPOLv2Factory(factory).pairFeeReceiver(pair);
        amount -= fee;
        token.safeTransferFrom(msg.sender, feeRecipient, fee);
        token.safeTransferFrom(msg.sender, receiver, amount);
    }
}
