library NFTSVG {
    using Strings for uint256;

    function generateSVG(uint256 tokenId, uint round, address collection) internal pure returns (string memory svg) {
   
        svg = string(
            abi.encodePacked(
        '<svg width="290" height="500" viewBox="0 0 290 500" xmlns="http://www.w3.org/2000/svg" ',
        "xmlns:xlink='http://www.w3.org/1999/xlink'>",
        generateSVGDefs(tokenId, collection),
        generateSVGBorders(),
        generateBody(),
        generateSVGTokenID(tokenId, round),
        '</svg>'
            )
        );
            
    }

    function isRare(uint256 tokenId, address collection) internal pure returns (bool) {
        bytes32 h = keccak256(abi.encodePacked(tokenId, collection));
        return uint256(h) < type(uint256).max / (1 + BitMath.mostSignificantBit(tokenId) * 2);
    }

    function generateSVGDefs(uint256 tokenId, address collection) private pure returns (string memory svg) {

        string[38] memory defs = [
