    function generateSVGGradientStops(uint256 tokenId, address collection) private pure returns (string memory svg) {

        //we don't want traverse the hue spectrum linearly
        //we hash to pick a random spot on the rainbow
        uint256 h = uint256(keccak256(abi.encodePacked(tokenId, collection)));

        uint256 hue = h % 360;

        uint256 sat = 50 + (h % 50);

        uint256 secondOffset = 5 + (h % 60);

        uint256 lumin1 = h % 30;

        uint256 lumin2 = lumin1 * 2;

        if (isRare(tokenId, collection)) {
            svg = string(
                abi.encodePacked(
                    '<stop offset="0%" stop-color="hsl(0,0%,',
                    lumin1.toString(),
                    '%)" />'
                    '<stop offset="',
                    secondOffset.toString(),
                    '%" stop-color="hsl(0,0%,',
                    lumin2.toString(),
                    '%)" />',
                    '<stop offset="100%" stop-color="hsl(0,0%,0%)" />'
                )
            );
        } else {
            svg = string(
                abi.encodePacked(
                    '<stop offset="0%" stop-color="hsl(',
                    hue.toString(),
                    ',100%,50%)" />'
                    '<stop offset="',
                    secondOffset.toString(),
                    '%" stop-color="hsl(',
                    hue.toString(),
                    ',',
                    sat.toString(),
                    '%,60%)" />',
                    '<stop offset="100%" stop-color="hsl(',
                    hue.toString(),
                    ',100%,0%)" />'
                )
            );
        }
    } 

    function generateSVGTokenID(
        uint tokenId, uint round
    ) private pure returns (string memory svg) {
        string memory sequence = string(abi.encodePacked(tokenId.toString(), unicode' • ', round.toString()));
        uint256 str1length = bytes(sequence).length + 4;
        uint256 rectWidth = 7 * (str1length + 4);
        uint256 xLoc = 290 - 32 - rectWidth; //290 total width, 32 padding

        svg = string(
            abi.encodePacked(
                '<g style="transform:translate(',
        xLoc.toString(),
        'px, 430px)">',
            '<rect width="',
            rectWidth.toString(),
            'px" height="26px" rx="8px" ry="8px" fill="rgba(0,0,0,0.6)" /><text x="12px" y="17px" ',
                'font-family="\'Courier New\', monospace" font-size="12px" fill="white">',
                '<tspan fill="rgba(255,255,255,0.6)">ID: </tspan>',
                sequence,
            '</text></g>'
            )
        );
    }

    function packStrings(string[] memory segments) private pure returns (string memory result) {

        result = "";
        
        for (uint i = 0; i < segments.length; i++) {
            result = string(abi.encodePacked(result, segments[i]));
        }
    }

}
