/// @custom:security-contact contact@elephant.money
contract ElephantMoneyUnlimitedNFT is ERC721Royalty, ERC721Enumerable, AccessControl {
    using Counters for Counters.Counter;
    using Strings for uint256;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    Counters.Counter private _tokenIdCounter;

    uint256 public constant ROUND_SIZE = 10000;
    uint256 public constant BASE_PRICE = 1 ether;
    uint256 public constant OTC_MAX = 1000;

    constructor(address nftTreasury) ERC721("Elephant Money Unlimited NFT-V1", "ELEPHANT-NFT-V1") {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setDefaultRoyalty(nftTreasury, 3000); //30% suggested royalty
        _tokenIdCounter.increment(); //start count at 1
    }

    //OTC distribution is for the creator, custody, volunteers, and partners
    //This function is provided to mint in batches while managing gas cost while in the launch phase, pre release
    function mintOTC(uint batchSize) external {
        
        uint256 tokenId = _tokenIdCounter.current();
        uint256 current = 0;

        //run access control so anyone can verify OTC distribution is complete
        require(tokenId <= OTC_MAX, "OTC distribution is complete.");
        _checkRole(DEFAULT_ADMIN_ROLE);

        while (current < batchSize && tokenId <= OTC_MAX) {
            _safeMint(msg.sender, tokenId); //mint

            _tokenIdCounter.increment(); //next
            current++;
            tokenId = _tokenIdCounter.current();
        }

    }

    function price() external view returns (uint256) {
        return BASE_PRICE * (2 ** (totalSupply() / ROUND_SIZE));
    }

    function round() external view returns (uint256) {
        return (totalSupply() / ROUND_SIZE) + 1;
    }

    function roundOf(uint256 tokenId) public pure returns (uint256) {
        require(tokenId > 0, "tokenId must be non-zero");
        return ((tokenId - 1) / ROUND_SIZE) + 1; 
    }


    function safeMint(address to) public onlyRole(MINTER_ROLE) {
        uint256 tokenId = _tokenIdCounter.current();
        _tokenIdCounter.increment();
        _safeMint(to, tokenId);
    }

    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721)
        returns (string memory)
    {
        _requireMinted(tokenId);
        return _constructTokenURI(tokenId, address(this));
    }

    function tokensOfOwner(address owner) public view returns (uint[] memory tokenIds) {
        uint balance = balanceOf(owner);
        require(balance > 0, "tokensOfOwner - zero balance");

        tokenIds = new uint[](balance);
        for (uint i=0; i < balance; i++) {
            tokenIds[i] = tokenOfOwnerByIndex(owner, i);
        }
    }

    function _constructTokenURI(uint256 tokenId, address collection) internal pure returns (string memory svg) {
        
        string memory image = NFTSVG.generateSVG(tokenId, roundOf(tokenId), collection);

        image = Base64.encode(bytes(image));

        svg =
            string(
                abi.encodePacked(
                    'data:application/json;base64,',
                    Base64.encode(
                        bytes(
                            abi.encodePacked(
                                '{"name":"',
                                'Elephant Money Unlimited #',
                                tokenId.toString(),
                                '", "description":"',
                                'This NFT represents a percentage share in the Elephant Money Treasury paid out as annual yield. ',
                                "The owner of this NFT can resell or stake it for yield. If sold, a 30% royalty will be paid to the treasury. ",
                                "Created by Bankteller for the Elephant Money Herd."
                                '", "image": "',
                                'data:image/svg+xml;base64,',
                                image,
                                '","attributes":[{"trait_type":"round","value":"',
                                roundOf(tokenId).toString(),
                                '"},{"trait_type":"id","value":"',
                                tokenId.toString(),
                                '"},{"trait_type":"rarity","value":"',
                                NFTSVG.isRare(tokenId, collection) ? 'rare' : 'common',   
                                '"}]}'
                            )
                        )
                    )
                )
            );
    }


    // The following functions are overrides required by Solidity.

    function _beforeTokenTransfer(address from, address to, uint256 tokenId, uint256 batchSize)
        internal
        override(ERC721, ERC721Enumerable)
    {
        super._beforeTokenTransfer(from, to, tokenId, batchSize);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721Royalty, ERC721Enumerable, AccessControl)
        returns (bool)
    {
        return  interfaceId == type(ERC721Enumerable).interfaceId || interfaceId == type(AccessControl).interfaceId || interfaceId == type(ERC721Royalty).interfaceId || super.supportsInterface(interfaceId);
    }

