    function setEndTime(address _user, uint256 _endtime) external;
}

contract SevenCToken is IERC20, Ownable {
    using SafeMath for uint256;
    using Address for address;

    string private _name = "SevenCToken";
    string private _symbol = "SEVENC";
    uint8 private _decimals = 18;
    uint256 private _tTotal = 1e7 ether;
    
    uint256 public minTrans = 1e15;
    uint256 public minBal = 20 ether;
    address public sevenLaucnh;

    mapping(address => uint256) private _tOwned;
    mapping(address => mapping(address => uint256)) private _allowances;
    mapping(address => address) public inviter;
    mapping(address => address[]) public mapRefers;
    mapping(address => bool) public isValidMint;

    constructor(address _user1,address _user2) {
        _tOwned[_user1] = _tTotal.mul(8).div(10);
        _tOwned[_user2] = _tTotal.mul(2).div(10);
        
        inviter[_user1] = address(this);
        inviter[_user2] = address(this);

    }

    function setSevenLaunch(address _sevenLaucnh) public onlyOwner{
        sevenLaucnh = _sevenLaucnh;
    }

    function setMinTrans(uint256 _minTrans) public onlyOwner {
        minTrans = _minTrans;
    }

    function setMinBal(uint256 _minBal) public onlyOwner {
        minBal = _minBal;
    }

    function viewReferLen(address _user) public view returns (uint256) {
        return mapRefers[_user].length;
    }
    //=======================

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint256) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _tTotal;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _tOwned[account];
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            msg.sender,
            _allowances[sender][msg.sender].sub(amount, "ERC20: transfer amount exceeds allowance")
        );
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(msg.sender, spender, _allowances[msg.sender][spender].add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        _approve(
            msg.sender,
            spender,
            _allowances[msg.sender][spender].sub(subtractedValue, "ERC20: decreased allowance below zero")
        );
        return true;
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        _tokenTransfer(from, to, amount);
        if (inviter[to] == address(0) && amount >= minTrans && !from.isContract() && !to.isContract() ) {
            inviter[to] = from;
            mapRefers[from].push(to);
        }
        if (inviter[to] != address(0) && balanceOf(to) >= minBal) {
            isValidMint[to] = true;
        }
        if (inviter[from] != address(0) && balanceOf(from) >= minBal) {
            isValidMint[from] = true;
        }

        if (sevenLaucnh != address(0)) {
            if (_tOwned[from] < minBal) {
                ISevenLaunch(sevenLaucnh).setEndTime(from, block.timestamp);
            } else {
                ISevenLaunch(sevenLaucnh).setEndTime(from, 0);
            }
            if (_tOwned[to] < minBal) {
                ISevenLaunch(sevenLaucnh).setEndTime(to, block.timestamp);
            } else {
                ISevenLaunch(sevenLaucnh).setEndTime(to, 0);
            }
        }
    }

    function _tokenTransfer(
        address sender,
        address recipient,
        uint256 amount
    ) private {
        _tOwned[sender] = _tOwned[sender].sub(amount);
        _tOwned[recipient] = _tOwned[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
    }
}
