    "contracts/SocialExchange.sol": {
      "content": "// SPDX-License-Identifier: Unlicensed
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./interfaces/IERC20Detailed.sol";
import "./interfaces/IERC1155Detailed.sol";

/// @custom:security-contact support@xcadnetwork.com
contract SocialExchange is Pausable, AccessControl, ReentrancyGuard {
	using Address for address payable;
	using SafeERC20 for IERC20;

	bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
	bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
	bytes32 public constant FUNDS_ROLE = keccak256("FUNDS_ROLE");
	bytes32 public constant CREATOR_ROLE = keccak256("CREATOR_ROLE");

	event Created(address indexed sender, address creator, address referrer);
	event Buy(
		address indexed sender,
		address creator,
		uint256 amount,
		uint256 price,
		uint256 fee,
		address referrer,
		uint256 supply,
		uint256 balance
	);
	event Sell(
		address indexed sender,
		address creator,
		uint256 amount,
		uint256 price,
		uint256 fee,
		address referrer,
		uint256 supply,
		uint256 balance
	);
	event FeeChanged(uint256 creatorFee, uint256 treasuryFee, uint256 referralFee);
	event BurnFeeChanged(uint256 creatorBurnFee, uint256 treasuryBurnFee);
	event TreasuryChanged(address treasurer);
	event BurnableAddressChanged(address creator, bool enabled);
	event TreasuryFeesClaimed(address sender, address treasury, uint256 amount);
	event BurnableFeesClaimed(address sender, address creator, uint256 amount);
	event FeesClaimed(address sender, uint256 amount);

	mapping(address => uint256) public burnableFees;
	mapping(address => uint256) public addressFees;

	mapping(address => bool) public burnableAddresses;
	mapping(address => address) public referralAddresses;

	address public treasuryAddress;
	uint256 public treasuryFees;

	IERC1155Detailed public immutable keyTokenAddress;
	address public immutable paymentTokenAddress;
	uint8 public immutable paymentTokenDecimals;

	uint256 public constant keyTokenDenominator = 1_0;
	uint256 private constant _feeDenominator = 100_00;

	uint256 public totalFeePercent = 10_00;

	uint256 public referralFeePercent = 10_00;
	uint256 public creatorFeePercent = 50_00;
	uint256 public treasuryFeePercent = 50_00;

	uint256 public creatorBurnFeePercent = 50_00;
	uint256 public treasuryBurnFeePercent = 50_00;

	constructor(
		address _keyTokenAddress,
		address _paymentTokenAddress,
		address _treasuryAddress
	) {
		require(_keyTokenAddress != address(0), "Invalid Key Token Address");
		require(
			_paymentTokenAddress != address(0),
			"Invalid Payment Token Address"
		);
		require(_treasuryAddress != address(0), "Invalid Treasury Address");

		_grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
		_grantRole(PAUSER_ROLE, msg.sender);
		_grantRole(ADMIN_ROLE, msg.sender);
		_grantRole(FUNDS_ROLE, msg.sender);
		_grantRole(CREATOR_ROLE, msg.sender);

		keyTokenAddress = IERC1155Detailed(_keyTokenAddress);
		paymentTokenAddress = _paymentTokenAddress;
		paymentTokenDecimals = IERC20Detailed(paymentTokenAddress).decimals();
		treasuryAddress = _treasuryAddress;
	}

	function pause() external onlyRole(PAUSER_ROLE) {
		_pause();
	}

	function unpause() external onlyRole(PAUSER_ROLE) {
		_unpause();
	}

	/**
	 * Get Key Balance
	 *
	 * This will return the total amount of keys belonging to an address.
	 * Note: This returns the whole number of keys along with the fractional amount as an integer.
	 */
	function balanceOf(
		address _account,
		address _keyAddress
	) public view returns (uint256) {
		uint256 id = _getKeyIdentifier(_keyAddress);
		return keyTokenAddress.balanceOf(_account, id);
	}

	/**
	 * Get Key Supply
	 *
	 * This will return the total supply of a key.
	 * Note: This returns the whole number of keys along with the fractional amount as an integer.
	 */
	function totalSupply(address _keyAddress) public view returns (uint256) {
		uint256 id = _getKeyIdentifier(_keyAddress);
		return keyTokenAddress.totalSupply(id);
	}

	/**
	 * Check Key Exists
	 *
	 * This will return if a key exists.
	 */
	function exists(address _keyAddress) public view returns (bool) {
		uint256 id = _getKeyIdentifier(_keyAddress);
		return keyTokenAddress.exists(id);
	}

	function setTreasuryAddress(
		address _address
	) external onlyRole(ADMIN_ROLE) {
		require(_address != address(0), "Invalid Address");
		treasuryAddress = _address;

		emit TreasuryChanged(treasuryAddress);
	}

	function claimTreasuryFees() external onlyRole(ADMIN_ROLE) {
		uint256 amount = treasuryFees;
		require(amount > 0, "Insufficient Fees");

		treasuryFees = 0;
		IERC20(paymentTokenAddress).safeTransfer(treasuryAddress, amount);

		emit TreasuryFeesClaimed(msg.sender, treasuryAddress, amount);
	}

	/**
	 * Claim Burnable Fees
	 *
	 * These are "burnable" creator fees are set to be burnt by the address claiming.
	 */
	function claimBurnableFees(address _address) external onlyRole(FUNDS_ROLE) {
		uint256 amount = burnableFees[_address];
		require(amount > 0, "Insufficient Fees");

		burnableFees[_address] = 0;
		IERC20(paymentTokenAddress).safeTransfer(msg.sender, amount);

		emit BurnableFeesClaimed(msg.sender, _address, amount);
	}

	/**
	 * Claim Fees
	 *
	 * These are creator (key owner) and referral fees that can be claimed by the user.
	 */
	function claimFees() external {
		uint256 amount = addressFees[msg.sender];
		require(amount > 0, "Insufficient Fees");

		addressFees[msg.sender] = 0;
		IERC20(paymentTokenAddress).safeTransfer(msg.sender, amount);

		emit FeesClaimed(msg.sender, amount);
	}

	function setBurnableAddress(
		address _address,
		bool _active
	) external onlyRole(CREATOR_ROLE) {
		burnableAddresses[_address] = _active;

		emit BurnableAddressChanged(_address, _active);
	}

	/**
	 * Set Fee Distributions
	 *
	 * Fees are distributed across the key owner (creator), creator referrer and to the treasury.
	 * The treasury fee is the remainder of the fee after the key owner and referrer fees.
	 */
	function setFeePercent(
		uint256 _creatorFeePercent,
		uint256 _referrelFeePercent,
		uint256 _treasuryFeePercent
	) external onlyRole(ADMIN_ROLE) {
		require(
			_creatorFeePercent + _treasuryFeePercent == _feeDenominator,
			"Invalid Creator and Treasury Fees"
		);
		require(
			_referrelFeePercent <= _treasuryFeePercent,
			"Invalid Referral Fee"
		);

		creatorFeePercent = _creatorFeePercent;
		referralFeePercent = _referrelFeePercent;
		treasuryFeePercent = _treasuryFeePercent;

		emit FeeChanged(creatorFeePercent, treasuryFeePercent, referralFeePercent);
	}

	/**
	 * Set Burn Distribution
	 *
	 * The burn amounts are the percentages of the fees that are burnt.
	 * Portions of both the key owner (creator) fee and treasury fee may be burnt.
	 */
	function setBurnPercent(
		uint256 _creatorBurnFeePercent,
		uint256 _treasuryBurnFeePercent
	) external onlyRole(ADMIN_ROLE) {
		require(
			_creatorBurnFeePercent <= _feeDenominator,
			"Invalid Creator Burn Fee"
		);
		require(
			_treasuryBurnFeePercent <= _feeDenominator,
			"Invalid Treasury Burn Fee"
		);

		creatorBurnFeePercent = _creatorBurnFeePercent;
		treasuryBurnFeePercent = _treasuryBurnFeePercent;

		emit BurnFeeChanged(creatorBurnFeePercent, treasuryBurnFeePercent);
	}

	/**
	 * Get Referral Fee
	 *
	 * The referral fee is only applied if a valid referral address was provided when creating the key.
	 */
	function getReferralFee(
		address _referralAddress,
		uint256 _amount
	) public view returns (uint256) {
		if (_referralAddress == address(0)) {
			return 0;
		}
		return (_amount * referralFeePercent) / _feeDenominator;
	}

	/**
	 * Creator Fee
	 *
	 * Calculate the creator fee portion from the total fee.
	 * The creator fee is a fee directly for the key owner (key creator).
	 */
	function getCreatorFee(uint256 _amount) public view returns (uint256) {
		return (_amount * creatorFeePercent) / _feeDenominator;
	}

	/**
	 * Creator Burn Fee
	 *
	 * Calculate the creator burn portion from the creator fee.
	 * The creator burn amount is the amount of the creator fee that is burnt.
	 */
	function getCreatorBurnFee(
		address _keyAddress,
		uint256 _amount
	) public view returns (uint256) {
		if (!burnableAddresses[_keyAddress]) {
			return 0;
		}
		return (_amount * creatorBurnFeePercent) / _feeDenominator;
	}

	/**
	 * Treasury Fee
	 *
	 * Calculate the treasury fee portion from the total fee.
	 * The treasury fee is a fee for the protocol owner.
	 */
	function getTreasuryFee(uint256 _amount) public view returns (uint256) {
		return (_amount * treasuryFeePercent) / _feeDenominator;
	}

	/**
	 * Treasury Burn Fee
	 *
	 * Calculate the treasury burn portion from the treasury fee.
	 * The treasury burn amount is the amount of the payment token that is burnt from the treasury fee.
	 */
	function getTreasuryBurnFee(uint256 _amount) public view returns (uint256) {
		return (_amount * treasuryBurnFeePercent) / _feeDenominator;
	}

	/**
	 * Fractional Price Calculation
	 */
	function getXPrice(
		uint256 supply,
		uint256 amount
	) public view returns (uint256) {
		uint256 k1 = supply / keyTokenDenominator;
		uint256 k2 = (supply + amount) / keyTokenDenominator;

		uint256 p1 = _getPrice(k1, 1);
		if (k1 == k2) return (p1 * amount) / keyTokenDenominator;

		uint256 r1 = (keyTokenDenominator - (supply % keyTokenDenominator)) %
			keyTokenDenominator;
		uint256 r2 = (supply + amount) % keyTokenDenominator;

		uint256 p2 = _getPrice(k2, 1);
		uint256 p3 = _getPrice(
			((supply + keyTokenDenominator - 1) / keyTokenDenominator),
			(amount - r1 - r2) / keyTokenDenominator
		);

		uint256 f1 = (p1 * r1) / keyTokenDenominator;
		uint256 f2 = (p2 * r2) / keyTokenDenominator;

		return p3 + f1 + f2;
	}

	/**
	 * Key Price Calculation
	 */
	function _getPrice(
		uint256 supply,
		uint256 amount
	) internal view returns (uint256) {
		uint256 sum1 = supply == 0
			? 0
			: ((supply - 1) * (supply) * (2 * (supply - 1) + 1)) / 6;
		uint256 sum2 = supply == 0 && amount == 1
			? 0
			: ((supply + amount - 1) *
				(supply + amount) *
				(2 * (supply + amount - 1) + 1)) / 6;
		uint256 summation = sum2 - sum1;
		uint256 decimals = (10 ** paymentTokenDecimals);
		return (summation * decimals * 10) / 125;
	}

	function getFee(uint256 _price) public view returns (uint256) {
		return (_price * totalFeePercent) / _feeDenominator;
	}

	function getBuyPrice(
		uint256 id,
		uint256 amount
	) public view returns (uint256) {
		uint256 supply = keyTokenAddress.totalSupply(id);
		return getXPrice(supply, amount);
	}

	function getSellPrice(
		uint256 id,
		uint256 amount
	) public view returns (uint256) {
		uint256 supply = keyTokenAddress.totalSupply(id);
		uint256 remainingSupply = (supply > amount) ? (supply - amount) : 0;

		require(remainingSupply >= keyTokenDenominator, "Invalid Amount");

		return getXPrice(remainingSupply, amount);
	}

	function getBuyPriceAfterFee(
		uint256 id,
		uint256 amount
	) public view returns (uint256) {
		uint256 price = getBuyPrice(id, amount);
		uint256 totalFee = getFee(price);

		return price + totalFee;
	}

	function getSellPriceAfterFee(
		uint256 id,
		uint256 amount
	) public view returns (uint256) {
		uint256 price = getSellPrice(id, amount);
		uint256 totalFee = getFee(price);

		return price - totalFee;
	}

	function _getKeyIdentifier(address key) internal pure returns (uint256) {
		return uint256(uint160(key));
	}

	function _takeFees(
		uint256 totalFee,
		address keyAddress,
		address referrerAddress
	) internal {
		uint256 referralFee = getReferralFee(referrerAddress, totalFee);
		uint256 creatorFee = getCreatorFee(totalFee);
		uint256 creatorBurnFee = getCreatorBurnFee(keyAddress, creatorFee);

		uint256 treasuryFee = getTreasuryFee(totalFee) - referralFee;
		uint256 treasuryBurnFee = getTreasuryBurnFee(treasuryFee);

		// Treasury Fees
		treasuryFees += treasuryFee;

		// Individual Fees
		addressFees[keyAddress] += creatorFee;
		addressFees[referrerAddress] += referralFee;

		// Burn Portions
		burnableFees[keyAddress] += creatorBurnFee;
		burnableFees[treasuryAddress] += treasuryBurnFee;
	}

	function createKey(address referrerAddress) external whenNotPaused nonReentrant {
		require(referrerAddress != msg.sender, "Invalid Referrer");
		require(!exists(msg.sender), "Key Already Exists");

		referralAddresses[msg.sender] = referrerAddress;

		uint256 id = _getKeyIdentifier(msg.sender);
		keyTokenAddress.mint(msg.sender, id, keyTokenDenominator, "");

		emit Created(msg.sender, msg.sender, referrerAddress);
	}

	function buyKey(
		address keyAddress,
		uint256 amount,
		uint256 maximumPrice
	) external whenNotPaused nonReentrant {
		require(exists(keyAddress), "Key Does Not Exist");

		uint256 id = _getKeyIdentifier(keyAddress);

		// Calculate Price
		uint256 price = getBuyPrice(id, amount);
		uint256 fee = getFee(price);
		uint256 totalPrice = price + fee;

		require(totalPrice <= maximumPrice, 'Invalid Price');

		// Distribute Fees
		address referrerAddress = referralAddresses[keyAddress];
		_takeFees(fee, keyAddress, referrerAddress);

		// Take Funds
		IERC20(paymentTokenAddress).safeTransferFrom(
			msg.sender,
			address(this),
			totalPrice
		);

		// Mint Keys
		keyTokenAddress.mint(msg.sender, id, amount, "");

		uint256 supply = totalSupply(keyAddress);
		uint256 balance = balanceOf(msg.sender, keyAddress);

		emit Buy(msg.sender, keyAddress, amount, price, fee, referrerAddress, supply, balance);
	}

	function sellKey(
		address keyAddress,
		uint256 amount,
		uint256 minimumPrice
	) external whenNotPaused nonReentrant {
		require(
			balanceOf(msg.sender, keyAddress) >= amount,
			"Insufficient Amount"
		);

		uint256 id = _getKeyIdentifier(keyAddress);

		// Calculate Price
		uint256 price = getSellPrice(id, amount);
		uint256 fee = getFee(price);
		uint256 totalPrice = price - fee;

		require(totalPrice >= minimumPrice, 'Invalid Price');

		address referrerAddress = referralAddresses[keyAddress];
		_takeFees(fee, keyAddress, referrerAddress);

		// Burn Keys
		keyTokenAddress.burn(msg.sender, id, amount);

		// Give Funds
		IERC20(paymentTokenAddress).safeTransfer(msg.sender, totalPrice);

		uint256 supply = totalSupply(keyAddress);
		uint256 balance = balanceOf(msg.sender, keyAddress);

		emit Sell(msg.sender, keyAddress, amount, price, fee, referrerAddress, supply, balance);
	}
}"
    }
  },
