    "contracts/CCRController.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "hardhat/console.sol";

contract CCRController is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    IERC20 public tokenAddress;
    
    mapping(uint256 => bool) private _nonces;

    address signatureAddress;    
    address teamAddress;
    
    event ClaimCCR(address indexed to, uint256 indexed amount, uint256 indexed nonce);


    constructor(        
        address _tokenAddress,
        address _signatureAddress,
        address _teamAddress
    ) {        
        tokenAddress = IERC20(_tokenAddress);
        signatureAddress = address(_signatureAddress);
        teamAddress = address(_teamAddress);
    }

    function claimCCR(uint256 _amount, uint256 _nonce, bytes memory _signature) public 
        _validSignature(msg.sender, _amount, _nonce, _signature){
        
        require(!_nonces[_nonce], "Already withdraw");

        _nonces[_nonce] = true;
        //Send 95% of amount to requested
        tokenAddress.transfer(msg.sender, _amount - _amount/20);
        //Send 5% to team address
        tokenAddress.transfer(teamAddress, _amount/20);
        emit ClaimCCR(msg.sender, _amount, _nonce);
    }

    modifier _validSignature(address _to, uint256 _amount, uint256 _nonce, bytes memory _signature){
        bytes32 message = keccak256(abi.encodePacked(_to, _amount, _nonce));
        assert(ECDSA.recover(message, _signature) == signatureAddress);
        _;
    }

    function setSignatureAddress(address _address) external onlyOwner{
        signatureAddress = _address;
    }

    function setTeamAddress(address _address) external onlyOwner{
        teamAddress = _address;
    }

    function setTokenAddress(address _address) external onlyOwner{
        tokenAddress = IERC20(_address);
    }

}"
    },
