    "contracts/controllers/Controllable.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity >=0.8.4;

import "@openzeppelin/contracts/access/Ownable.sol";
import "../interfaces/IControllable.sol";

abstract contract Controllable is Ownable, IControllable {
    // A map of addresses that are authorised to register and renew names.
    mapping(address => bool) public controllers;

    modifier onlyController() {
        require(
            controllers[msg.sender],
            "Controllable: Caller is not a controller"
        );
        _;
    }

    function isController(address controller)
        public
        view
        virtual
        override
        returns (bool)
    {
        return controllers[controller];
    }

    // Authorises a controller, who can register and renew domains.
    function addController(address controller)
        public
        virtual
        override
        onlyOwner
    {
        controllers[controller] = true;
        emit ControllerAdded(controller);
    }

    // Revoke controller permission for an address.
    function removeController(address controller)
        public
        virtual
        override
        onlyOwner
    {
        controllers[controller] = false;
        emit ControllerRemoved(controller);
    }
}
"
