contract ONLY1TOKEN is Context, IERC20, Ownable {
    using SafeMath for uint256;
    using Address for address;

    mapping (address => uint256) private _rOwned;
    mapping (address => uint256) private _tOwned;
    mapping (address => mapping (address => uint256)) private _allowances;

    mapping (address => bool) private _isExcludedFromFee;
    mapping (address => bool) private _isExcludedFromTxLimit;

    mapping (address => bool) private _isExcluded;
    address[] private _excluded;

    // hard coded immutable multisig wallet used for gathering the marketing/listing fee
    address public immutable _multiSigEvolutionWalletAddress = 0x84e5307408768C1645B847e205F44c2189bf6325;

    uint256 private constant MAX = ~uint256(0);
    uint256 private _tTotal = 1  * 10**18;
    uint256 private _rTotal = (MAX - (MAX % _tTotal));
    uint256 private _tFeeTotal;

    string private _name = "Only 1 Token";
    string private _symbol = "O1T";
    uint8 private _decimals = 18;

    // reflection tax in promille (4.9%)
    uint256 public _taxFee = 49;
    uint256 private _previousTaxFee = _taxFee;

    // marketing/listing fee in promille (0.2%)
    uint256 public _evolutionFee = 2;
    uint256 private _previousEvolutionFee = _evolutionFee;

    // liquidity fee in promille (1.9%)
    uint256 public _liquidityFee = 19;
    uint256 private _previousLiquidityFee = _liquidityFee;

    // on each buy transaction, this fraction of the transaction value in O1T will be sent from the contract to the liquidity pool directly if available in the contract.
    uint256 public _liquidityFarmDivider = 40;
    // enable or disable releasing tokens from the contract to the liquidity pool
    bool public releaseTokensToLiquidityEnabled = true;
    // enable or disable fees on liquidity add/remove, to be used in combination with our own liquidity add function.
    bool public feelessLiquidityTransferEnabled = false;

    IPancakeRouter02 public immutable pancakeswapV2Router;
    address public immutable pancakeswapV2Pair;

    uint256 public _maxTxAmount = 1  * 10**16;

    event ReleaseTokensToLiquidityEnabledUpdated(bool enabled);
    event FeelessLiquidityTransferEnabledUpdated(bool enabled);

    constructor () {
        _rOwned[owner()] = _rTotal;

        IPancakeRouter02 _pancakeswapV2Router = IPancakeRouter02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
         // Create a pancakeswap pair for this new token
        pancakeswapV2Pair = IPancakeFactory(_pancakeswapV2Router.factory())
            .createPair(address(this), _pancakeswapV2Router.WETH());

        // set the rest of the contract variables
        pancakeswapV2Router = _pancakeswapV2Router;

        //exclude owner and this contract from fee
        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[address(this)] = true;

        excludeFromReward(address(this));

        emit Transfer(address(0), owner(), _tTotal);
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _tTotal;
    }

    function balanceOf(address account) public view override returns (uint256) {
        if (_isExcluded[account]) return _tOwned[account];
        return tokenFromReflection(_rOwned[account]);
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount, true);
        return true;
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount, true);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "ERC20: transfer amount exceeds allowance"));
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].sub(subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }

    function isExcludedFromReward(address account) public view returns (bool) {
        return _isExcluded[account];
    }

    function totalFees() public view returns (uint256) {
        return _tFeeTotal;
    }

    function deliver(uint256 tAmount) public {
        address sender = _msgSender();
        require(!_isExcluded[sender], "Excluded addresses cannot call this function");
        (uint256 rAmount,,,,,,) = _getValues(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rTotal = _rTotal.sub(rAmount);
        _tFeeTotal = _tFeeTotal.add(tAmount);
    }

    function getBalance() public view returns(uint) {
        return address(this).balance;
    }
    //This function allows BNB to be retrieved from the contract so we can use the contract address as a donation wallet. No O1T tokens or liquidity is touched here.
    function rescueBalance() public onlyOwner() {
        address payable to = payable(msg.sender);
        to.transfer(getBalance());
    }

    function reflectionFromToken(uint256 tAmount, bool deductTransferFee) public view returns(uint256) {
        require(tAmount <= _tTotal, "Amount must be less than supply");
        if (!deductTransferFee) {
            (uint256 rAmount,,,,,,) = _getValues(tAmount);
            return rAmount;
        } else {
            (,uint256 rTransferAmount,,,,,) = _getValues(tAmount);
            return rTransferAmount;
        }
    }

    function tokenFromReflection(uint256 rAmount) public view returns(uint256) {
        require(rAmount <= _rTotal, "Amount must be less than total reflections");
        uint256 currentRate =  _getRate();
        return rAmount.div(currentRate);
    }

    function excludeFromReward(address account) public onlyOwner() {
        // require(account != 0x10ed43c718714eb63d5aa57b78b54704e256024e, 'We can not exclude pancakeswap router.');
        require(!_isExcluded[account], "Account is already excluded");
        if(_rOwned[account] > 0) {
            _tOwned[account] = tokenFromReflection(_rOwned[account]);
        }
        _isExcluded[account] = true;
        _excluded.push(account);
    }

    function includeInReward(address account) external onlyOwner() {
        require(_isExcluded[account], "Account is already included");
        for (uint256 i = 0; i < _excluded.length; i++) { //Better use EnumerableSet but since the amount of excluded addresses will be small, this is ok.
            if (_excluded[i] == account) {
                _excluded[i] = _excluded[_excluded.length - 1];
                _tOwned[account] = 0;
                _isExcluded[account] = false;
                _excluded.pop();
                break;
            }
        }
    }
        function _transferBothExcluded(address sender, address recipient, uint256 tAmount) private {
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 tTransferAmount, uint256 tFee, uint256 tLiquidity, uint256 tEvolution) = _getValues(tAmount);
        _tOwned[sender] = _tOwned[sender].sub(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _tOwned[recipient] = _tOwned[recipient].add(tTransferAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);
        _takeLiquidity(tLiquidity);
        _takeEvolution(tEvolution);
        _reflectFee(rFee, tFee);
        emit Transfer(sender, recipient, tTransferAmount);
    }

    function excludeFromFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = true;
    }

    function excludeFromTxLimit(address account) public onlyOwner {
        _isExcludedFromTxLimit[account] = true;
    }

    function includeInFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = false;
    }

    function includeInTxLimit(address account) public onlyOwner {
        _isExcludedFromTxLimit[account] = false;
    }

    // All taxes/fees can only be reduced (not increased) to. Our intent is to reduce liquidity fees to 0 as soon as liquidity has grown enough.
    // Since we have
    function reduceTaxFeePercent(uint256 reduceTaxFee) external onlyOwner() {
        _taxFee = _taxFee.sub(reduceTaxFee);
    }

    function reduceEvolutionFeePercent(uint256 reduceEvolutionFee) external onlyOwner() {
        _evolutionFee = _evolutionFee.sub(reduceEvolutionFee);
    }

    function reduceLiquidityFeePercent(uint256 reduceLiquidityFee) external onlyOwner() {
        _liquidityFee = _liquidityFee.sub(reduceLiquidityFee);
    }

    // The rate at which tokens can be released from the contract into the liquidity pool. Capped at 1/10th of any buy transaction, provided enough tokens exist in the contract
    function setLiquidityAddPercent(uint256 liquidityFarmDivider) external onlyOwner() {
        require(liquidityFarmDivider >= 20);
        _liquidityFarmDivider = liquidityFarmDivider;
    }

    // Enable or disable releasing tokens from the contract to the liquidity pool
    function setReleaseTokensToLiquidityEnabled(bool _enabled) public onlyOwner {
        releaseTokensToLiquidityEnabled = _enabled;
        emit ReleaseTokensToLiquidityEnabledUpdated(_enabled);
    }
    // Enable or disable fees on adding/removing liquidity. To be used with our own addLiquidity function
    function setFeelessLiquidityTransferEnabled(bool _enabled) public onlyOwner {
        feelessLiquidityTransferEnabled = _enabled;
        emit FeelessLiquidityTransferEnabledUpdated(_enabled);
    }
     //to recieve BNB from pancakeswapV2Router when swaping
    receive() external payable {}

    function _reflectFee(uint256 rFee, uint256 tFee) private {
        _rTotal = _rTotal.sub(rFee);
        _tFeeTotal = _tFeeTotal.add(tFee);
    }

    function _getValues(uint256 tAmount) private view returns (uint256, uint256, uint256, uint256, uint256, uint256, uint256) {
        (uint256 tTransferAmount, uint256 tFee, uint256 tLiquidity, uint256 tEvolution) = _getTValues(tAmount);
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee) = _getRValues(tAmount, tFee, tLiquidity, tEvolution, _getRate());
        return (rAmount, rTransferAmount, rFee, tTransferAmount, tFee, tLiquidity, tEvolution);
    }

    function _getTValues(uint256 tAmount) private view returns (uint256, uint256, uint256, uint256) {
        uint256 tFee = calculateTaxFee(tAmount);
        uint256 tLiquidity = calculateLiquidityFee(tAmount);
        uint256 tEvolution = calculateEvolutionFee(tAmount);
        uint256 tTransferAmount = tAmount.sub(tFee).sub(tLiquidity).sub(tEvolution);
        return (tTransferAmount, tFee, tLiquidity, tEvolution);
    }

    function _getRValues(uint256 tAmount, uint256 tFee, uint256 tLiquidity, uint256 tEvolution, uint256 currentRate) private pure returns (uint256, uint256, uint256) {
        uint256 rAmount = tAmount.mul(currentRate);
        uint256 rFee = tFee.mul(currentRate);
        uint256 rLiquidity = tLiquidity.mul(currentRate);
        uint256 rEvolution = tEvolution.mul(currentRate);
        uint256 rTransferAmount = rAmount.sub(rFee).sub(rLiquidity).sub(rEvolution);
        return (rAmount, rTransferAmount, rFee);
    }

    function _getRate() private view returns(uint256) {
        (uint256 rSupply, uint256 tSupply) = _getCurrentSupply();
        return rSupply.div(tSupply);
    }

    function _getCurrentSupply() private view returns(uint256, uint256) {
        uint256 rSupply = _rTotal;
        uint256 tSupply = _tTotal;
        for (uint256 i = 0; i < _excluded.length; i++) { //Better use EnumerableSet but since the amount of excluded addresses will be small, this is ok.
            if (_rOwned[_excluded[i]] > rSupply || _tOwned[_excluded[i]] > tSupply) return (_rTotal, _tTotal);
            rSupply = rSupply.sub(_rOwned[_excluded[i]]);
            tSupply = tSupply.sub(_tOwned[_excluded[i]]);
        }
        if (rSupply < _rTotal.div(_tTotal)) return (_rTotal, _tTotal);
        return (rSupply, tSupply);
    }

    function _takeLiquidity(uint256 tLiquidity) private {
        uint256 currentRate =  _getRate();
        uint256 rLiquidity = tLiquidity.mul(currentRate);
        _rOwned[address(this)] = _rOwned[address(this)].add(rLiquidity);
        if(_isExcluded[address(this)])
            _tOwned[address(this)] = _tOwned[address(this)].add(tLiquidity);
    }

    function _takeEvolution(uint256 tEvolution) private {
        uint256 currentRate =  _getRate();
        uint256 rEvolution = tEvolution.mul(currentRate);
        _rOwned[_multiSigEvolutionWalletAddress] = _rOwned[_multiSigEvolutionWalletAddress].add(rEvolution);
        if(_isExcluded[_multiSigEvolutionWalletAddress])
            _tOwned[_multiSigEvolutionWalletAddress] = _tOwned[_multiSigEvolutionWalletAddress].add(tEvolution);
    }

    function calculateTaxFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_taxFee).div(
            10**3
        );
    }

    function calculateEvolutionFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_evolutionFee).div(
            10**3
        );
    }

    function calculateLiquidityFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_liquidityFee).div(
            10**3
        );
    }

    function removeAllFee() private {
        if(_taxFee == 0 && _liquidityFee == 0) return;

        _previousTaxFee = _taxFee;
        _previousEvolutionFee = _evolutionFee;
        _previousLiquidityFee = _liquidityFee;

        _taxFee = 0;
        _evolutionFee = 0;
        _liquidityFee = 0;
    }

    function restoreAllFee() private {
        _taxFee = _previousTaxFee;
        _evolutionFee = _previousEvolutionFee;
        _liquidityFee = _previousLiquidityFee;
    }

    function isExcludedFromFee(address account) public view returns(bool) {
        return _isExcludedFromFee[account];
    }

    function isExcludedFromTxLimit(address account) public view returns(bool) {
        return _isExcludedFromTxLimit[account];
    }

    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount,
        bool allowLiquidityEvent // Prevent infinite recursion since a sell and a send of tokens to the pancakeswapV2Pair can't be distinguished
    ) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        if(from != owner() && to != owner() && !_isExcludedFromTxLimit[from] && !_isExcludedFromTxLimit[to])
            require(amount <= _maxTxAmount, "Transfer amount exceeds the maxTxAmount.");

        uint256 contractTokenBalance = balanceOf(address(this));

        // Add 1/_liquidityFarmDivider % of tokens
        uint256 toLiquidity = amount.div(_liquidityFarmDivider);
        bool canTransfer = toLiquidity < contractTokenBalance;

        if (canTransfer && releaseTokensToLiquidityEnabled && allowLiquidityEvent && (from == pancakeswapV2Pair || to == pancakeswapV2Pair)) // Has enough tokens && is a buy or sell && token release enabled
        {
            // Send tokens to LP directly. This will distribute extra tokens to the people owning liquidity pool tokens proportionally to their share.
            // The price impact is minor (Buys will increase the price 5% less than normal effectively) but liquidity holders are rewarded.
            _transfer(address(this), address(pancakeswapV2Pair), toLiquidity, false);
        }

        //indicates if fee should be deducted from transfer
        bool takeFee = true;

        //if any account belongs to _isExcludedFromFee account then remove the fee
        if(_isExcludedFromFee[from] || _isExcludedFromFee[to]){
            takeFee = false;
        }

        //transfer amount, it will take tax, evolution and liquidity fee.

        _tokenTransfer(from,to,amount,takeFee);
    }

    // Function to airdrop tokens to all holders. This is used for the migration of the previous contract to the current contract twice.
    // The first time before liquidity migration we send a miniscule amount to ensure that the balances are updated well with reflection
    // The second time we use this function is after liquidity migration where we will airdrop holder balances 1:1 of the current contract
    function bulkTransfer(address[] calldata _receivers, uint256[] calldata _amounts) external onlyOwner {
		require(_receivers.length == _amounts.length);
		for (uint256 i = 0; i < _receivers.length; i++) {
			_transfer(msg.sender, _receivers[i], _amounts[i], false);
		}
	}

    //this method is responsible for taking all fee, if takeFee is true
    function _tokenTransfer(address sender, address recipient, uint256 amount,bool takeFee) private {
        if(!takeFee)
            removeAllFee();

        if (_isExcluded[sender] && !_isExcluded[recipient]) {
            _transferFromExcluded(sender, recipient, amount);
        } else if (!_isExcluded[sender] && _isExcluded[recipient]) {
            _transferToExcluded(sender, recipient, amount);
        } else if (!_isExcluded[sender] && !_isExcluded[recipient]) {
            _transferStandard(sender, recipient, amount);
        } else if (_isExcluded[sender] && _isExcluded[recipient]) {
            _transferBothExcluded(sender, recipient, amount);
        } else {
            _transferStandard(sender, recipient, amount);
        }

        if(!takeFee)
            restoreAllFee();
    }

    function _transferStandard(address sender, address recipient, uint256 tAmount) private {
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 tTransferAmount, uint256 tFee, uint256 tLiquidity, uint256 tEvolution) = _getValues(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);
        _takeLiquidity(tLiquidity);
        _takeEvolution(tEvolution);
        _reflectFee(rFee, tFee);
        emit Transfer(sender, recipient, tTransferAmount);
    }

    function _transferToExcluded(address sender, address recipient, uint256 tAmount) private {
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 tTransferAmount, uint256 tFee, uint256 tLiquidity, uint256 tEvolution) = _getValues(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _tOwned[recipient] = _tOwned[recipient].add(tTransferAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);
        _takeLiquidity(tLiquidity);
        _takeEvolution(tEvolution);
        _reflectFee(rFee, tFee);
        emit Transfer(sender, recipient, tTransferAmount);
    }

    function _transferFromExcluded(address sender, address recipient, uint256 tAmount) private {
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 tTransferAmount, uint256 tFee, uint256 tLiquidity, uint256 tEvolution) = _getValues(tAmount);
        _tOwned[sender] = _tOwned[sender].sub(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);
        _takeLiquidity(tLiquidity);
        _takeEvolution(tEvolution);
        _reflectFee(rFee, tFee);
        emit Transfer(sender, recipient, tTransferAmount);
    }

}
