pragma solidity ^0.8.19;







contract ERC20Token is ERC20, Ownable {
    using SafeERC20 for IERC20;
    using Address for address;

    uint256 constant MUL_CONSTANT = 10000;
    uint256 public immutable TRADING_FEE_CAP;

    IUniswapV2Router02 public immutable uniswapV2Router;
    address public uniswapV2Pair;

    bool private swapping;

    address public marketingFeeWallet;
    address public serviceFeeWallet;
    uint256 public maxTx;
    uint256 public maxWallet;
    uint256 public swapTokensAtAmount;

    bool public swapEnabled = false;
    bool public tradingActive = false;

    // 1% = 100, 100% = 10000
    uint256 public buyTotalFees;
    uint256 public buyMarketingFee;
    uint256 public buyServiceFee;
    uint256 public buyLiquidityFee;

    uint256 public sellTotalFees;
    uint256 public sellMarketingFee;
    uint256 public sellServiceFee;
    uint256 public sellLiquidityFee;

    uint256 public tokensForMarketingFee;
    uint256 public tokensForServiceFee;
    uint256 public tokensForLiquidity;
    /******************/

    address autoLP_collector = address(0xdead);

    // exlcude from fees and max transaction amount
    mapping(address => bool) private _isExcludedFromFees;
    mapping(address => bool) public _isExcludedMaxTransactionAmount;

    // store addresses that a automatic market maker pairs. Any transfer *to* these addresses
    // could be subject to a maximum transfer amount
    mapping(address => bool) public automatedMarketMakerPairs;

    struct CreationParams {
        string name;
        string symbol;
        uint256 totalSupply;
        address owner;
        address uniswapV2Router;
        address marketingFeeWallet;
        address serviceFeeWallet;
        uint256 buyMarketingFee;
        uint256 buyServiceFee;
        uint256 buyLiquidityFee;
        uint256 sellMarketingFee;
        uint256 sellServiceFee;
        uint256 sellLiquidityFee;
        uint256 maxTx;
        uint256 maxWallet;
    }

    event UpdateUniswapV2Router(
        address indexed newAddress,
        address indexed oldAddress
    );

    event ExcludeFromFees(address indexed account, bool isExcluded);

    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);

    event MarketingWalletUpdated(
        address indexed newWallet,
        address indexed oldWallet
    );

    event ServiceWalletUpdated(
        address indexed newWallet,
        address indexed oldWallet
    );

    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiquidity
    );

    constructor(
        CreationParams memory params,
        uint256 tradingFeeCap
    ) ERC20(params.name, params.symbol) {
        TRADING_FEE_CAP = tradingFeeCap;

        uniswapV2Router = IUniswapV2Router02(params.uniswapV2Router);

        excludeFromMaxTransaction(params.uniswapV2Router, true);

        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory()).createPair(
            address(this),
            uniswapV2Router.WETH()
        );
        excludeFromMaxTransaction(address(uniswapV2Pair), true);
        _setAutomatedMarketMakerPair(address(uniswapV2Pair), true);

        maxTx = params.maxTx;
        maxWallet = params.maxWallet;

        buyMarketingFee = params.buyMarketingFee;
        buyServiceFee = params.buyServiceFee;
        buyLiquidityFee = params.buyLiquidityFee;
        buyTotalFees = buyMarketingFee + buyServiceFee + buyLiquidityFee;

        sellMarketingFee = params.sellMarketingFee;
        sellServiceFee = params.sellServiceFee;
        sellLiquidityFee = params.sellLiquidityFee;
        sellTotalFees = sellMarketingFee + sellServiceFee + sellLiquidityFee;

        marketingFeeWallet = params.marketingFeeWallet;
        serviceFeeWallet = params.serviceFeeWallet;
        swapTokensAtAmount = 1000 * 1e18;

        // exclude from paying fees or having max transaction amount
        excludeFromFees(params.owner, true);
        excludeFromFees(address(this), true);

        excludeFromMaxTransaction(params.owner, true);
        excludeFromMaxTransaction(address(this), true);
        excludeFromMaxTransaction(address(0xdead), true);
        excludeFromMaxTransaction(address(0), true);

        _mint(params.owner, params.totalSupply);

        transferOwnership(params.owner);
    }

    receive() external payable {}

    // Enable Trading
    function enableTrading() external onlyOwner {
        tradingActive = true;
        swapEnabled = true;
    }

    // Disable Trading
    function disableTrading() external onlyOwner {
        tradingActive = false;
        swapEnabled = false;
    }

    function setMaxTransactionAmount(uint256 newNumInEther) external onlyOwner {
        maxTx = newNumInEther * (10 ** 18);
    }

    function setMaxWalletAmount(uint256 newNumInEther) external onlyOwner {
        maxWallet = newNumInEther * (10 ** 18);
    }

    function setSwapTokensAtAmount(
        uint256 newAmountInWei
    ) external onlyOwner returns (bool) {
        swapTokensAtAmount = newAmountInWei;
        return true;
    }

    function excludeFromMaxTransaction(
        address updAds,
        bool isEx
    ) public onlyOwner {
        _isExcludedMaxTransactionAmount[updAds] = isEx;
    }

    // only use to disable contract sales if absolutely necessary (emergency use only)
    function updateSwapEnabled(bool enabled) external onlyOwner {
        swapEnabled = enabled;
    }

    function setBuyFees(
        uint256 _marketingFee,
        uint256 _liquidityFee
    ) external onlyOwner {
        buyMarketingFee = _marketingFee;
        buyLiquidityFee = _liquidityFee;
        buyTotalFees = buyMarketingFee + buyServiceFee + buyLiquidityFee;

        require(
            (buyMarketingFee + buyLiquidityFee) <= TRADING_FEE_CAP,
            "Total fees exceeds max fee"
        );
    }

    function setSellFees(
        uint256 _marketingFee,
        uint256 _liquidityFee
    ) external onlyOwner {
        sellMarketingFee = _marketingFee;
        sellLiquidityFee = _liquidityFee;
        sellTotalFees = sellMarketingFee + sellServiceFee + sellLiquidityFee;
        require(
            (sellMarketingFee + sellLiquidityFee) <= TRADING_FEE_CAP,
            "Total fees exceeds max fee"
        );
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        _isExcludedFromFees[account] = excluded;
        emit ExcludeFromFees(account, excluded);
    }

    function setAutomatedMarketMakerPair(
        address pair,
        bool value
    ) public onlyOwner {
        require(
            pair != uniswapV2Pair,
            "The pair cannot be removed from automatedMarketMakerPairs"
        );

        _setAutomatedMarketMakerPair(pair, value);
    }

    function _setAutomatedMarketMakerPair(address pair, bool value) internal {
        automatedMarketMakerPairs[pair] = value;

        emit SetAutomatedMarketMakerPair(pair, value);
    }

    function setMarketingWallet(address newMarketingWallet) external onlyOwner {
        emit MarketingWalletUpdated(newMarketingWallet, marketingFeeWallet);
        marketingFeeWallet = newMarketingWallet;
        excludeFromFees(marketingFeeWallet, true);
    }

    function clearStuckBNBBalance(address addr) external onlyOwner {
        (bool sent, ) = payable(addr).call{value: (address(this).balance)}("");
        require(sent);
    }

    function setOwner(address _newOwner) external onlyOwner {
        transferOwnership(_newOwner);
        excludeFromFees(_newOwner, true);
    }

    function clearStuckTokenBalance(
        address addr,
        address tokenAddress
    ) external onlyOwner {
        uint256 _bal = IERC20(tokenAddress).balanceOf(address(this));
        IERC20(tokenAddress).safeTransfer(addr, _bal);
    }

    function isExcludedFromFees(address account) public view returns (bool) {
        return _isExcludedFromFees[account];
    }

    event BoughtEarly(address indexed sniper);

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        if (amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        if (
            from != owner() &&
            to != owner() &&
            to != address(0) &&
            to != address(0xdead) &&
            !swapping
        ) {
            if (!tradingActive) {
                require(
                    _isExcludedFromFees[from] || _isExcludedFromFees[to],
                    "Trading is not active."
                );
            }

            //when buy
            if (
                automatedMarketMakerPairs[from] &&
                !_isExcludedMaxTransactionAmount[to]
            ) {
                require(
                    amount <= maxTx,
                    "Buy transfer amount exceeds the maxTx."
                );
                require(
                    amount + balanceOf(to) <= maxWallet,
                    "Max wallet exceeded"
                );
            }
            //when sell
            else if (
                automatedMarketMakerPairs[to] &&
                !_isExcludedMaxTransactionAmount[from]
            ) {
                require(
                    amount <= maxTx,
                    "Sell transfer amount exceeds the maxTx."
                );
            } else {
                if (!_isExcludedMaxTransactionAmount[to]) {
                    require(
                        amount + balanceOf(to) <= maxWallet,
                        "Max wallet exceeded"
                    );
                }
            }
        }

        uint256 contractTokenBalance = balanceOf(address(this));

        bool canSwap = contractTokenBalance >= swapTokensAtAmount;

        if (
            canSwap &&
            swapEnabled &&
            !swapping &&
            !automatedMarketMakerPairs[from] &&
            !_isExcludedFromFees[from] &&
            !_isExcludedFromFees[to] &&
            sellTotalFees > 0 &&
            buyTotalFees > 0
        ) {
            swapping = true;

            swapBack();

            swapping = false;
        }

        bool takeFee = !swapping;

        // if any account belongs to _isExcludedFromFee account then remove the fee
        if (_isExcludedFromFees[from] || _isExcludedFromFees[to]) {
            takeFee = false;
        }

        uint256 fees = 0;
        uint256 marketingFee = 0;
        uint256 liquidityFee = 0;
        // only take fees on buys/sells, do not take on wallet transfers
        if (takeFee) {
            // on sell
            if (automatedMarketMakerPairs[to] && sellTotalFees > 0) {
                fees = (amount * sellTotalFees) / MUL_CONSTANT;
                marketingFee = (fees * sellMarketingFee) / sellTotalFees;
                liquidityFee = (fees * sellLiquidityFee) / sellTotalFees;
                tokensForMarketingFee += marketingFee;
                tokensForLiquidity += liquidityFee;
                unchecked {
                    tokensForServiceFee += (fees - marketingFee - liquidityFee);
                }
            }
            // on buy
            else if (automatedMarketMakerPairs[from] && buyTotalFees > 0) {
                fees = (amount * buyTotalFees) / MUL_CONSTANT;
                marketingFee = (fees * buyMarketingFee) / buyTotalFees;
                liquidityFee = (fees * buyLiquidityFee) / buyTotalFees;
                tokensForMarketingFee += marketingFee;
                tokensForLiquidity += liquidityFee;
                unchecked {
                    tokensForServiceFee += (fees - marketingFee - liquidityFee);
                }
            }

            if (fees > 0) {
                super._transfer(from, address(this), fees);
            }

            amount -= fees;
        }

        super._transfer(from, to, amount);
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            autoLP_collector,
            block.timestamp
        );
    }

    function swapBack() private {
        uint256 contractBalance = balanceOf(address(this));
        uint256 totalTokensToSwap = tokensForLiquidity +
            tokensForMarketingFee +
            tokensForServiceFee;

        if (contractBalance == 0 || totalTokensToSwap == 0) {
            return;
        }

        // Halve the amount of liquidity tokens
        uint256 liquidityTokens = (contractBalance * tokensForLiquidity) /
            totalTokensToSwap /
            2;
        uint256 amountToSwapForETH = 0;

        unchecked {
            amountToSwapForETH = contractBalance - liquidityTokens;
        }

        uint256 initialETHBalance = address(this).balance;

        swapTokensForEth(amountToSwapForETH);

        uint256 ethBalance = 0;

        unchecked {
            ethBalance = address(this).balance - initialETHBalance;
        }

        uint256 ethForMarketing = (ethBalance * tokensForMarketingFee) /
            totalTokensToSwap;
        uint256 ethForService = (ethBalance * tokensForServiceFee) /
            totalTokensToSwap;

        uint256 ethForLiquidity = 0;

        unchecked {
            ethForLiquidity = ethBalance - ethForMarketing - ethForService;
        }

        tokensForLiquidity = 0;
        tokensForMarketingFee = 0;
        tokensForServiceFee = 0;

        (bool sentForMarketing, ) = address(marketingFeeWallet).call{
            value: ethForMarketing
        }("");
        (bool sentForService, ) = address(serviceFeeWallet).call{
            value: ethForService
        }("");

        if (liquidityTokens > 0 && ethForLiquidity > 0) {
            addLiquidity(liquidityTokens, ethForLiquidity);
            emit SwapAndLiquify(
                amountToSwapForETH,
                ethForLiquidity,
                tokensForLiquidity
            );
        }
        require(sentForMarketing && sentForService, "send failed");
    }
}
