pragma solidity ^0.8.18;


interface IBEP20 {
  function totalSupply() external view returns (uint256);
  function decimals() external view returns (uint8);
  function symbol() external view returns (string memory);
  function name() external view returns (string memory);
  function balanceOf(address account) external view returns (uint256);
  function transfer(address recipient, uint256 amount) external returns (bool);
  function allowance(address _owner, address spender) external view returns (uint256);
  function approve(address spender, uint256 amount) external returns (bool);
  function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
  event Transfer(address indexed from, address indexed to, uint256 value);
  event Approval(address indexed owner, address indexed spender, uint256 value);
}

abstract contract Ownable {
    address private _owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    /**
     * @dev Initializes the contract setting the deployer as the initial owner.
     */
    constructor () {
        address msgSender = msg.sender;
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }

    /**
     * @dev Returns the address of the current owner.
     */
    function owner() public view returns (address) {
        return _owner;
    }

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(owner() == msg.sender, "Ownable: caller is not the owner");
        _;
    }

    /**
     * @dev Leaves the contract without owner. It will not be possible to call
     * `onlyOwner` functions anymore. Can only be called by the current owner.
     *
     * NOTE: Renouncing ownership will leave the contract without an owner,
     * thereby removing any functionality that is only available to the owner.
     */
    function renounceOwnership() public onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current owner.
     */
    function transferOwnership(address newOwner) public onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        emit OwnershipTransferred(_owner, newOwner);
        _owner = newOwner;
    }
}

contract WrappedBurnfolio is IBEP20, Ownable
{
    mapping (address => uint) public  balanceOf;
    mapping (address => mapping (address => uint)) public allowance;
    mapping(address => bool) public excludedFromFees;
    mapping(address=>bool) public isAMM;
    string public constant name = 'Burnfolio';
    string public constant symbol = 'WBF';
    uint8 public constant decimals = 9;
    uint public totalSupply=0;
    IBEP20 Burnfolio=IBEP20(0xf26E13b60bc629dcD47b2362Eb538115D4807359);

    constructor () {
        excludedFromFees[msg.sender]=true;
        excludedFromFees[address(this)]=true;
    }
    
    function _transfer(address sender, address recipient, uint amount) private{
        require(sender != address(0), "Transfer from zero");
        require(recipient != address(0), "Transfer to zero");

        if(excludedFromFees[sender] || excludedFromFees[recipient])
            _feelessTransfer(sender, recipient, amount);
        else{ 
            require(block.timestamp>LaunchTimestamp,"trading not yet enabled");
            _taxedTransfer(sender,recipient,amount);                  
        }
    }
    function _taxedTransfer(address sender, address recipient, uint amount) private{
        uint senderBalance = balanceOf[sender];
        require(senderBalance >= amount, "Transfer exceeds balance");
        unchecked
        {
            balanceOf[sender]-=amount;
            if(isAMM[sender]||isAMM[recipient]) 
            {
                uint BurnAmount=amount/100;
                amount-=BurnAmount;
                balanceOf[address(0xdead)]+=BurnAmount;
            }
            balanceOf[recipient]+=amount;
        }

        
        emit Transfer(sender,recipient,amount);
    }


    function _feelessTransfer(address sender, address recipient, uint amount) private{
        uint senderBalance = balanceOf[sender];
        require(senderBalance >= amount, "Transfer exceeds balance");
        unchecked{
            balanceOf[sender]-=amount;
            balanceOf[recipient]+=amount; 
        }
     
        emit Transfer(sender,recipient,amount);
    }
    event OnSetAMM(address AMM, bool flag);
    function setAMM(address AMM, bool Add) public onlyOwner{
        isAMM[AMM]=Add;
        emit OnSetAMM(AMM,Add);

    }
    
    event OnExcludeAccount(address account, bool exclude);
    function excludeAccountFromFees(address account, bool exclude) public onlyOwner{
        require(account!=address(this),"can't Include the contract");
        excludedFromFees[account]=exclude;
        emit OnExcludeAccount(account,exclude);
    }
    event OnEnableTrading(uint timestamp);
    uint public LaunchTimestamp=type(uint).max;
    function enableTrading() external{
        setLaunchTimestamp(block.timestamp);
    }
    function setLaunchTimestamp(uint timestamp) public onlyOwner{
        require(block.timestamp<LaunchTimestamp,"AlreadyLaunched");
        LaunchTimestamp=timestamp;
        emit OnEnableTrading(timestamp);

    }
    bool _locked;
    modifier lock{
        require(!_locked);
        _locked=true;
        _;
        _locked=false;
    }
    function wrap(uint amount) external lock{
        address sender=msg.sender;
        uint prevBF=Burnfolio.balanceOf(address(this));
        Burnfolio.transferFrom(msg.sender,address(this),amount);
        uint wrappedAmount=Burnfolio.balanceOf(address(this))-prevBF;
        balanceOf[sender]+=amount;
        totalSupply+=amount;
        emit Transfer(address(0),sender,wrappedAmount);
    }
    function unwrap(uint amount) external lock{
        address sender=msg.sender;
        balanceOf[sender]-=amount;
        totalSupply-=amount;
        emit Transfer(msg.sender,address(0),amount);
        Burnfolio.transfer(sender,amount);
    }

    function transfer(address recipient, uint amount) external override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function approve(address spender, uint amount) external override returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }
    function _approve(address owner, address spender, uint amount) private {
        require(owner != address(0), "Approve from zero");
        require(spender != address(0), "Approve to zero");

        allowance[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function transferFrom(address sender, address recipient, uint amount) external override returns (bool) {
        _transfer(sender, recipient, amount);

        uint currentAllowance = allowance[sender][msg.sender];
        require(currentAllowance >= amount, "Transfer > allowance");

        _approve(sender, msg.sender, currentAllowance - amount);
        return true;
    }
}
