        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external returns (uint amountETH);

    function swapExactTokensForTokensSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
    function swapExactETHForTokensSupportingFeeOnTransferTokens(
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external payable;
    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
}

interface ISwapFactory {
    event PairCreated(address indexed token0, address indexed token1, address pair, uint);

    function feeTo() external view returns (address);
    function feeToSetter() external view returns (address);

    function getPair(address tokenA, address tokenB) external view returns (address pair);
    function allPairs(uint) external view returns (address pair);
    function allPairsLength() external view returns (uint);

    function createPair(address tokenA, address tokenB) external returns (address pair);

    function setFeeTo(address) external;
    function setFeeToSetter(address) external;

    function INIT_CODE_PAIR_HASH() external view returns (bytes32);
}

contract MNGTOKEN is Context, IERC20, Ownable {
  using SafeMath for uint256;
  using EnumerableSet for EnumerableSet.AddressSet;
  AutoSwap public autoSwap;
  AutoSwap public botSwap;
  mapping (address => uint256) balances;
  mapping (address => mapping (address => uint256)) allowances;
  uint256 public totalSupply;
  uint8 public decimals;
  string public symbol;
  string public name;
  address public erc20TokenUSDT;
  address public erc20TokenMND;
  address public market1Address;
  address public market2Address;
  address public lpHoldAddress;
  
  mapping(address => bool) public excludeFromFees;
  mapping(address => bool) public automatedMarketMakerPairs;
  mapping(address => bool) public blackList;
  ISwapRouterV2 swapRouter;
  address public mainPair;
  bool inSwap;
  TokenDividend public tokenDividend;

  uint256 public RATIO_MARKET_MAIN=20;
  uint256 public RATIO_MARKET_SLAVE=10;
  uint256 public RATIO_MND_BURN=30;
  uint256 public RATIO_LP_SHARE=30;
  uint256 public RATIO_ADD_LP=10;
  uint256 public RATE_TOTAL=50;
  uint256 swapCount;
  bool  begainTransfer;
  modifier swapping(){
      inSwap=true;
      _;
      inSwap=false;
  }

  constructor() {
    name = "MNG TOKEN";
    symbol = "MNG";
    decimals = 18;
    totalSupply = 10000000e18;
    balances[msg.sender] = totalSupply;
    
    erc20TokenUSDT=0x55d398326f99059fF775485246999027B3197955;
    erc20TokenMND=0x9d97e1b22a4a8C8075Fc3b4dAED6700a59F9C869;
    market1Address=0xA6a648d4bC2C937977159b374305779DBa396116;
    market2Address=0x519C813DF7d7363c87946DE2cee99D79FEb5A5F8;
    lpHoldAddress=0xA6a648d4bC2C937977159b374305779DBa396116;
    swapRouter=ISwapRouterV2(0x10ED43C718714eb63d5aA57B78B54704E256024E);
    
    
    mainPair=ISwapFactory(swapRouter.factory()).createPair(erc20TokenUSDT,address(this));
    automatedMarketMakerPairs[mainPair]=true;
    excludeFromFees[msg.sender]=true;
    autoSwap=new AutoSwap(address(this));
    tokenDividend=new TokenDividend(IERC20(erc20TokenUSDT),IERC20(mainPair),msg.sender);
    emit Transfer(address(0), msg.sender, totalSupply);
  }

  function batchSetBlackList(address[] calldata users,bool flag)external onlyOwner{
      
    for(uint i=0;i<users.length;i++){
       blackList[users[i]]=flag;
    }
  }
  function setAutomatedMarketMakerPairs(address pair,bool flag) external onlyOwner{
      automatedMarketMakerPairs[pair]=flag;
  }

    function setExcludeFromFees(address user,bool flag) public onlyOwner {
    excludeFromFees[user]=flag;
  }

  function batchSetExcludeFromFees(address[] calldata users,bool flag) public onlyOwner {
    for(uint i=0;i<users.length;i++){
        excludeFromFees[users[i]]=flag;
    }
  }

  function setAddress(uint8 _type,address _addr) external onlyOwner {
      if(_type==1){
        lpHoldAddress=_addr;
      }else if(_type==2){
        market1Address=_addr;
      }else if(_type==3){
        market2Address=_addr;
      }
  }

  function updateSwapRouter(ISwapRouterV2 _swapRouter) external onlyOwner {
      swapRouter=_swapRouter;
  }

  /**
   * @dev See {ERC20-allowance}.
   */
  function allowance(address owner, address spender) external view returns (uint256) {
    return allowances[owner][spender];
  }

  /**
   * @dev See {ERC20-balanceOf}.
   */
  function balanceOf(address account) external view returns (uint256) {
    return balances[account];
  }


  /**
   * @dev See {ERC20-transfer}.
   *
   * Requirements:
   *
   * - `recipient` cannot be the zero address.
   * - the caller must have a balance of at least `amount`.
   */
  function transfer(address recipient, uint256 amount) external returns (bool) {
    _transfer(_msgSender(), recipient, amount);
    return true;
  }

  /**
   * @dev See {ERC20-approve}.
   *
   * Requirements:
   *
   * - `spender` cannot be the zero address.
   */
  function approve(address spender, uint256 amount) external returns (bool) {
    _approve(_msgSender(), spender, amount);
    return true;
  }

  /**
   * @dev See {ERC20-transferFrom}.
   *
   * Emits an {Approval} event indicating the updated allowance. This is not
   * required by the EIP. See the note at the beginning of {ERC20};
   *
   * Requirements:
   * - `sender` and `recipient` cannot be the zero address.
   * - `sender` must have a balance of at least `amount`.
   * - the caller must have allowance for `sender`'s tokens of at least
   * `amount`.
   */
  function transferFrom(address sender, address recipient, uint256 amount) external returns (bool) {
    _transfer(sender, recipient, amount);
    _approve(sender, _msgSender(), allowances[sender][_msgSender()].sub(amount, "ERC20: transfer amount exceeds allowance"));
    return true;
  }

  /**
   * @dev Atomically increases the allowance granted to `spender` by the caller.
   *
   * This is an alternative to {approve} that can be used as a mitigation for
   * problems described in {ERC20-approve}.
   *
   * Emits an {Approval} event indicating the updated allowance.
   *
   * Requirements:
   *
   * - `spender` cannot be the zero address.
   */
  function increaseAllowance(address spender, uint256 addedValue) public returns (bool) {
    _approve(_msgSender(), spender, allowances[_msgSender()][spender].add(addedValue));
    return true;
  }

  /**
   * @dev Atomically decreases the allowance granted to `spender` by the caller.
   *
   * This is an alternative to {approve} that can be used as a mitigation for
   * problems described in {ERC20-approve}.
   *
   * Emits an {Approval} event indicating the updated allowance.
   *
   * Requirements:
   *
   * - `spender` cannot be the zero address.
   * - `spender` must have allowance for the caller of at least
   * `subtractedValue`.
   */
  function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool) {
    _approve(_msgSender(), spender, allowances[_msgSender()][spender].sub(subtractedValue, "ERC20: decreased allowance below zero"));
    return true;
  }

  function setBegainTransfer(bool enabled) external onlyOwner{
      begainTransfer=enabled;
  }

