   * @dev Sets `amount` as the allowance of `spender` over the `owner`s tokens.
   *
   * This is internal function is equivalent to `approve`, and can be used to
   * e.g. set automatic allowances for certain subsystems, etc.
   *
   * Emits an {Approval} event.
   *
   * Requirements:
   *
   * - `owner` cannot be the zero address.
   * - `spender` cannot be the zero address.
   */
  function _approve(address owner, address spender, uint256 amount) internal {
    require(owner != address(0), "ERC20: approve from the zero address");
    require(spender != address(0), "ERC20: approve to the zero address");

    allowances[owner][spender] = amount;
    emit Approval(owner, spender, amount);
  }

  function baseTransfer(address sender, address recipient, uint256 amount) internal{
    balances[sender] = balances[sender].sub(amount, "ERC20: transfer amount exceeds balance");
    balances[recipient] = balances[recipient].add(amount);
    emit Transfer(sender, recipient, amount);
  }

  EnumerableSet.AddressSet lpHolder;
  uint256 currentIndex;
  function isLPHolder(address user) external view returns (bool){
     return lpHolder.contains(user);
  }

  function process() internal {
      uint256 shareholderCount = lpHolder.length();

        if (shareholderCount == 0) return;

        uint256 iterations = 0;
        uint index = currentIndex;
        uint256 gasUsed = 0;
        uint256 gasLeft = gasleft();
        while (gasUsed<500000&&iterations<shareholderCount) {
            if (index >= shareholderCount) {
                index = 0;
            }

            address shareholder = lpHolder.at(index);
            tokenDividend.withdraw(shareholder);
            gasUsed = gasUsed + (gasLeft - gasleft());
            gasLeft = gasleft();
            index ++;
            iterations++;
        }
        currentIndex = index;
        
  }
  /**
   * @dev Moves tokens `amount` from `sender` to `recipient`.
   *
   * This is internal function is equivalent to {transfer}, and can be used to
   * e.g. implement automatic token fees, slashing mechanisms, etc.
   *
   * Emits a {Transfer} event.
   *
   * Requirements:
   *
   * - `sender` cannot be the zero address.
   * - `recipient` cannot be the zero address.
   * - `sender` must have a balance of at least `amount`.
   */
  function _transfer(address sender, address recipient, uint256 amount) internal {
    
    if(inSwap){
      baseTransfer(sender,recipient,amount);
      return;
    }

    require(!blackList[sender] && !blackList[recipient]);
    
    if(recipient==mainPair&&sender.code.length==0){
        lpHolder.add(sender);
    }

    if(excludeFromFees[sender]||excludeFromFees[recipient]){
      baseTransfer(sender,recipient,amount);
      return;
    }

    require(begainTransfer);
    if(!automatedMarketMakerPairs[sender]&&!automatedMarketMakerPairs[recipient]){
        baseTransfer(sender,recipient,amount);
        return;
    }

    uint256 feeAmount=amount.mul(1000-RATE_TOTAL).div(1000);
    baseTransfer(sender,address(this),feeAmount);
    
    swapCount++;
    if(swapCount>20&&!automatedMarketMakerPairs[sender]){
        swapAndAddLiquidity();
        swapCount=0;
    }else{
        process();
    }
    
    uint256 newAmount=amount.sub(feeAmount);
    baseTransfer(sender,recipient,newAmount);
    
  }

  function swapAndAddLiquidity() internal swapping {
    
    uint256 tokenMNGBalance=balances[address(this)];
    if(tokenMNGBalance==0){
        return;
    }

    IERC20 tokenUSDT=IERC20(erc20TokenUSDT);
    
    // MNG to USDTs
    uint256 tokenMNGAmount=tokenMNGBalance.mul(100-RATIO_ADD_LP/2).div(100);
    swapTokenATokenB(address(this),erc20TokenUSDT,tokenMNGAmount,0,address(autoSwap));
    autoSwap.withdraw(erc20TokenUSDT);


    //auto add LP
    uint256 tokenUSDTBalance=tokenUSDT.balanceOf(address(this));
    uint256 lpAAmount=tokenMNGBalance.sub(tokenMNGAmount);
    tokenUSDT.approve(address(swapRouter),tokenUSDTBalance);
    _approve(address(this), address(swapRouter), lpAAmount);
    (, uint amountB, )=swapRouter.addLiquidity(address(this), erc20TokenUSDT, lpAAmount, tokenUSDTBalance, 0 , 0, lpHoldAddress, block.timestamp);

    tokenUSDTBalance=tokenUSDTBalance.sub(amountB);
    uint256 ratio=100-RATIO_ADD_LP;

    //lpShare
    uint256 lpShareAmount=tokenUSDTBalance.mul(RATIO_LP_SHARE).div(ratio);
    tokenUSDT.approve(address(tokenDividend),lpShareAmount);
    tokenDividend.fund(lpShareAmount);

    //burn
    uint256 burnAmount=tokenUSDTBalance.mul(RATIO_MND_BURN).div(ratio);
    swapTokenATokenB(erc20TokenUSDT,erc20TokenMND,burnAmount,0,address(0xDead));

    //market
    uint256 market1Amount=tokenUSDTBalance.mul(RATIO_MARKET_MAIN).div(ratio);
    tokenUSDT.transfer(market1Address,market1Amount);
    tokenUSDT.transfer(market2Address,tokenUSDT.balanceOf(address(this)));
  }

  

  function swapTokenATokenB(address tokenA,address tokenB,uint256 aAmount, uint256 bAmount,address to) internal {
    address[] memory path=new address[](2);
    path[0]=tokenA;
    path[1]=tokenB;
    IERC20(tokenA).approve(address(swapRouter),aAmount);
    swapRouter.swapExactTokensForTokensSupportingFeeOnTransferTokens(aAmount, bAmount, path, to, block.timestamp);
  }

  function setRadio(uint256 totalRadio,uint256  _RATIO_MARKET_MAIN,uint256 _RATIO_MARKET_SLAVE,uint256 _RATIO_MND_BURN,uint256 _RATIO_LP_SHARE,uint256 _RATIO_ADD_LP ) external onlyOwner{
    RATE_TOTAL=totalRadio;
    _RATIO_MARKET_MAIN=_RATIO_MARKET_MAIN;
    _RATIO_MARKET_SLAVE=_RATIO_MARKET_SLAVE;
    _RATIO_MND_BURN=_RATIO_MND_BURN;
    _RATIO_LP_SHARE=_RATIO_LP_SHARE;
    _RATIO_ADD_LP=_RATIO_ADD_LP;
  }

  function updateTokenDividend(TokenDividend _newDividend) external onlyOwner {
      tokenDividend=_newDividend;
  }
}

contract TokenDividend{
    using SafeMath for uint256;
    struct UserInfo {
        uint256 holdAmount;
        uint256 rewardDebt;
    }
    uint256 public accERC20PerShare;
    IERC20 accERC20;
    IERC20 holdERC20;
    address owner;
    mapping(address user => UserInfo) public userInfo;
    constructor(IERC20 _accERC20,IERC20 _holdERC20,address _owner) {
        accERC20=_accERC20;
        holdERC20=_holdERC20;
        owner=_owner;
    }

    function withdraw(address user) external {
        if(user.code.length>0||address(0)==user){
            return;
        }
        uint256 totalSupply=holdERC20.totalSupply();
        if(totalSupply==0){
            return;
        }
        UserInfo storage _userInfo=userInfo[user];
        
        if(_userInfo.holdAmount>0){
            uint256 accAmount=_userInfo.holdAmount.mul(accERC20PerShare).div(1e36).sub(_userInfo.rewardDebt);
            uint256 balance=accERC20.balanceOf(address(this));
            if(balance>0){
                if(balance<accAmount){
                    accERC20.transfer(user,balance);
                }else{
                    accERC20.transfer(user,accAmount);
                }
            }
            
        }

        uint256 _holdAmount=holdERC20.balanceOf(user);
        _userInfo.holdAmount=_holdAmount;
        _userInfo.rewardDebt=_holdAmount.mul(accERC20PerShare).div(1e36);
    }

    function fund(uint256 amount) external {
        accERC20.transferFrom(msg.sender, address(this), amount);
        uint256 totalSupply=holdERC20.totalSupply();
        accERC20PerShare = accERC20PerShare.add(amount.mul(1e36).div(totalSupply));
    }

    function withdraw(address to,uint256 amount) external {
        require(msg.sender==owner);
        accERC20.transfer(to,amount);
    }
}

contract AutoSwap {
    address public owner;

    constructor(address _owner) {
        owner = _owner;
    }

    function withdraw(address token) public {
        require(msg.sender == owner, "caller is not owner");
        uint256 balance = IERC20(token).balanceOf(address(this));
        if (balance > 0) {
            IERC20(token).transfer(msg.sender, balance);
        }
    }

    function withdraw(address token, uint256 amount) public {
        require(msg.sender == owner, "caller is not owner");
        uint256 balance = IERC20(token).balanceOf(address(this));
        require(amount > 0 && balance >= amount, "Illegal amount");
        IERC20(token).transfer(msg.sender, amount);
    }

    function withdraw(address token, address to) public {
        require(msg.sender == owner, "caller is not owner");
        uint256 balance = IERC20(token).balanceOf(address(this));
        if (balance > 0) {
            IERC20(token).transfer(to, balance);
        }
    }
}
