    "contracts/HAC/StakingHAC.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract StakingHAC is Ownable {
    string public name = "StakingHAC";
    string public version = "1.0";

    address public tokenAddress;

    struct Staking {
        address account;
        uint amount;
        uint lockDays;
        uint rate;
        uint startTimestamp;
        uint endTimestamp;
    }

    uint public pool = 1000_0000_000000000000000000;
    mapping(uint => uint) public rates;
    mapping(uint => Staking) public stakings;
    mapping(address => uint[]) public userStakings;

    event Stake(
        uint no,
        address account,
        uint amount,
        uint lockDays,
        uint rate,
        uint rewardPerDay,
        uint startTimestamp
    );
    event Unstake(uint no, uint endTimestamp);

    uint nonce;

    constructor(address _tokenAddress) {
        tokenAddress = _tokenAddress;
    }

    function setRate(uint _lockDays, uint _rate) external onlyOwner {
        rates[_lockDays] = _rate;
    }

    function calculate_reward(
        uint _amount,
        uint _lockDays
    ) public view returns (uint) {
        uint totalReward = (_amount * rates[_lockDays]) /
            (365 / _lockDays) /
            10000;

        return totalReward;
    }

    function stake(uint _amount, uint _lockDays) external {
        require(rates[_lockDays] != 0, "Invalid lockDays.");

        uint totalReward = calculate_reward(_amount, _lockDays);
        require(totalReward <= pool, "There is no balance in the pool.");
        pool -= totalReward;

        IERC20 token = IERC20(tokenAddress);
        token.transferFrom(msg.sender, address(this), _amount);

        uint no = nonce;
        userStakings[msg.sender].push(no);
        stakings[no] = Staking(
            msg.sender,
            _amount,
            _lockDays,
            rates[_lockDays],
            block.timestamp,
            0
        );

        uint rewardPerDay = totalReward / _lockDays;

        emit Stake(
            no,
            msg.sender,
            _amount,
            _lockDays,
            rates[_lockDays],
            rewardPerDay,
            block.timestamp
        );

        nonce++;
    }

    function unstake(uint _no) external {
        Staking storage staking = stakings[_no];

        require(staking.account == msg.sender, "You are not the owner");
        require(staking.endTimestamp == 0, "Invalid no.");
        require(
            staking.startTimestamp + staking.lockDays * 86400 < block.timestamp,
            "Unlock Time Not Reached."
        );

        staking.endTimestamp = block.timestamp;

        IERC20 token = IERC20(tokenAddress);
        token.transfer(msg.sender, staking.amount);

        emit Unstake(_no, block.timestamp);
    }
}
