    "contracts/DeGuardClaimer.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

contract DeGuardClaimer is Ownable, ReentrancyGuard {
    uint public claimPrice;
    uint public claimAmount;
    IERC20Metadata public token;
    mapping(address => bool) private claimed;

    /// Constructor
    /// @param _claimPrice uint  Price for claim
    /// @param _claimAmount uint  Claim amount
    /// @param _token IERC20  Claim token
    constructor(uint _claimPrice, uint _claimAmount, IERC20Metadata _token) {
        setClaimParams(_claimPrice, _claimAmount * 10 ** _token.decimals());
        token = _token;
    }

    receive() external payable {}

    fallback() external payable {}

    /// Set claim params
    /// @param _claimPrice uint  Price for claim
    /// @param _claimAmount uint  Claim amount
    function setClaimParams(
        uint _claimPrice,
        uint _claimAmount
    ) public onlyOwner {
        require(_claimAmount > 0, "DeGuardToken: _claimAmount must be valid");

        claimPrice = _claimPrice;
        claimAmount = _claimAmount;
    }

    /// Withdraw coins
    /// @param _target address  Target address
    /// @param _amount uint  Transfer amount
    function withdrawCoins(address _target, uint _amount) external onlyOwner {
        require(
            address(this).balance >= _amount,
            "DeGuardToken: balance not enough"
        );
        (bool success, ) = _target.call{value: _amount}("");
        require(success, "DeGuardToken: transfer failed");
    }

    /// Transfer tokens
    /// @param _token IERC20  Token address
    /// @param _target address  Destination address
    /// @param _amount uint  Transfer amount
    function withdrawTokens(
        IERC20 _token,
        address _target,
        uint _amount
    ) external onlyOwner {
        require(
            _token.balanceOf(address(this)) >= _amount,
            "DeGuardToken: not enough tokens"
        );
        require(
            _token.transfer(_target, _amount),
            "DeGuardToken: transfer request failed"
        );
    }

    /// Return claimer data
    /// @param _claimerAddress address  Claimer address
    /// @return bool  Claim flag
    function getClaimerData(
        address _claimerAddress
    ) external view returns (bool) {
        return claimed[_claimerAddress];
    }

    /// Chain tokens
    function claim() external payable nonReentrant {
        require(!claimed[msg.sender], "DeGuardToken: claimed already");
        require(msg.value >= claimPrice, "DeGuardToken: invalid value");
        if (msg.value > 0) {
            (bool successTransfer, ) = owner().call{value: msg.value}("");
            require(successTransfer, "DeGuardToken: transfer failed");
        }

        claimed[msg.sender] = true;
        require(
            token.transfer(msg.sender, claimAmount),
            "DeGuardToken: invalid transfer"
        );
    }
}
