      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

import "../interfaces/IGovernance.sol";


contract Launchpad is Context, ReentrancyGuard {
    using SafeERC20 for IERC20;
    using Address for address;
    struct BuyOrder {
        uint256 amount;
        address paymentToken;
        address buyer;
    }

    mapping(uint256 => bool) public isActive; // campaignId => active
    mapping(uint256 => BuyOrder) public getOrders; // orderId => BuyOrder
    mapping(uint256 => bool) public usedOrderIds; // orderId => isUsed

    // Included 
    // treasury for managing asset
    // verifier for verifing signature
    // manager for sensitive updating
    IGovernance public governance;

    event SetCampaign(uint256 indexed campaignId, bool active);
    event Buy(uint256 indexed campaignId, uint256 indexed orderId, uint256 amount, address paymentToken, address releaseToken, address buyer);

    constructor(address governance_) {
        governance = IGovernance(governance_);
    }

    modifier onlyManager() {
        require(_msgSender() == governance.manager(), "Caller has no privilege");
        _;
    }

    function updateGovernance(address newGovernance)
        external
        onlyManager
    {
        require(newGovernance != address(0), "Set zero address");
        require(newGovernance != address(governance), "Old governance address");
        governance = IGovernance(newGovernance);
    }

    function getBuyOrder(uint256 orderId) external view returns (BuyOrder memory buyOrder) {
        buyOrder = getOrders[orderId];
    }

    function registCampaign(
        uint256 campaignId
    ) external onlyManager {
        setCampaign(campaignId, true);
    }

    function deactivateCampaign(
        uint256 campaignId
    ) external onlyManager {
        setCampaign(campaignId, false);
    }

    function setCampaign(uint256 campaignId, bool _isActive) internal {
        isActive[campaignId] = _isActive;
        emit SetCampaign(campaignId, _isActive);
    }

    /**
     * Buy IDO by transfer user's asset to treasury and record buy order
     * orderId BE generated orderID
     * amount Amount of payment token
     * paymentToken ERC20 accepted token using for payment in this IDO
     * releaseToken ERC20 / ERC721 / ERC1155
     * signature SIGNATURE provided from BE by verifier
     */
    function buy(
        uint256 orderId,
        uint256 amount,
        address paymentToken,
        address releaseToken,
        bytes calldata signature
    ) external nonReentrant {
        uint256 campaignId = orderId / 10 ** 36;  
        require(isActive[campaignId], "Campaign is no longer active"); 

        _buy(orderId, amount, paymentToken, releaseToken, _msgSender(), signature);

        emit Buy(campaignId, orderId, amount, paymentToken, releaseToken, _msgSender());
    }

    function _buy(
        uint256 orderId,
        uint256 amount,
        address paymentToken,
        address releaseToken,
        address buyer,
        bytes calldata signature
    ) internal {
        // Using address(0) for those IDO havn't have any offical Token address
        require(releaseToken.isContract() || releaseToken == address(0), "Release Token is invalid");
        require(!usedOrderIds[orderId], "OrderId has already used");

        bytes32 hashToSign = toHash(orderId, amount, paymentToken, releaseToken, buyer);
        validateSignature(hashToSign, signature);
        payment(buyer, paymentToken, amount);

        getOrders[orderId] = BuyOrder(amount, paymentToken, buyer);
        usedOrderIds[orderId] = true;
    }

    // Only support for ERC20
    function payment(
        address buyer, 
        address paymentToken, 
        uint256 amount
    ) internal {
        address treasury = governance.treasury();

        require(governance.acceptedPayments(paymentToken), "Payment Token is invalid");
        require(msg.value == 0, "Redundant paying asset");

        uint256 before = IERC20(paymentToken).balanceOf(treasury);
        IERC20(paymentToken).safeTransferFrom(buyer, treasury, amount);

        require(IERC20(paymentToken).balanceOf(treasury) - before >= amount, "Insufficient fund");
    }

    function toHash(
        uint256 orderId,
        uint256 amount,
        address paymentToken,
        address releaseToken,
        address buyer
    ) internal pure returns (bytes32 hashToSign) {
        hashToSign = keccak256(
                abi.encodePacked(
                    orderId,
                    amount,
                    paymentToken,
                    releaseToken,
                    buyer
                )
            );
    }

    function validateSignature(
        bytes32 hashToSign, 
        bytes calldata signature
    ) private view {
        hashToSign = ECDSA.toEthSignedMessageHash(hashToSign);
        require(
            ECDSA.recover(hashToSign, signature) == governance.verifier(),
            "Invalid signature"
        );
    }

    receive() external payable {
        revert();
    }

    fallback() external payable {
        revert();
    }
}
"
