    struct Stake {
        uint8 class;
        uint256 initialAmount;
        uint256 finalAmount;
        uint256 timestamp;
        bool unstaked;
    }

    Stake[] public stakes;
    mapping(address => uint256[]) public stakesOf;
    mapping(uint256 => address) public ownerOf;


    struct StakeInfo {
        uint256 period; //3 - Deposit duration in days
        uint256 initialAmount; //4 - Deposit amount (18 decimals)
        uint256 finalAmount; //7 -  Deposit + profit in Pulse tokens (18 decimals)
        uint256 profit; //6 -  Profit in pulse tokens (18 decimals)
        uint256 rateAPY; //5+9 - APY. 2500 = 25%
        uint256 ratePerDay; //10 -  Rate per day
        uint256 pulsePerDay; //8 -  Daily profit in Pulse tokens
        string startDate; //11 -  Start date in string format
        string endDate; //2+12  - End date in string format
        string daysLeft; //13 -  Days left in string format
        bool isWithdrawEnabled; //14 -  Is withdrawal of this stake Enabled
        bool isUnstaked; // Is this stake was unstaked
        uint256 stakeIndex; // Index of stake
    }

    event Staked(address indexed sender, uint8 indexed class, uint256 amount, uint256 finalAmount);
    event Unstaked(address indexed sender, uint8 indexed class, uint256 amount);
    event IncreaseRewardsPool(address indexed adder, uint256 added, uint256 newSize);


    // Init
    function initialize(IERC20 _TKN) public initializer {

        __Pausable_init_unchained();
        __AccessControl_init_unchained();      

        TKN = _TKN;
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }


    // ------------------------ ADMIN FUNCTIONS ----------------------------

    function pause() public onlyRole(DEFAULT_ADMIN_ROLE){
        super._pause();
    }
   
   
    function unpause() public onlyRole(DEFAULT_ADMIN_ROLE){
        super._unpause();
    }


    function returnAccidentallySent(IERC20 _TKN, uint256 amount) public onlyRole(DEFAULT_ADMIN_ROLE){

        if (_TKN == TKN) {
            rewardsPool -= amount;
        }

        _TKN.transfer(msg.sender, amount);
    }


    function setRates(uint32[5] memory _newRates) external onlyRole(DEFAULT_ADMIN_ROLE) {
        rates = _newRates;
    }

 
    function setPeriods(uint256[5] memory _newPeriods) external onlyRole(DEFAULT_ADMIN_ROLE) {
        periods = _newPeriods;
    }

 
    function updateMaxStakes(uint256 _max) public onlyRole(DEFAULT_ADMIN_ROLE) {
        MAX_STAKES = _max;
    }


    // ------------------------ USER FUNCTIONS ----------------------------

    function stake(uint8 _class, uint256 _amount) public whenNotPaused{
        require((_class >= 0) && (_class < 5), "Wrong class"); // data valid
        require(myActiveStakesCount(msg.sender) < MAX_STAKES,"MAX_STAKES overflow"); // has space for new active stake
    
        uint256 _finalAmount = _amount + (_amount * rates[_class] * periods[_class] / 311040000000);

        require(rewardsPool >= _finalAmount - _amount, "Rewards pool is empty for now");

        uint256 balanceBefore = TKN.balanceOf(address(this));
        TKN.transferFrom(msg.sender, address(this), _amount);
        require(TKN.balanceOf(address(this)) - balanceBefore == _amount, "Pulse transfer error. Check allowance");

        rewardsPool -= _finalAmount - _amount;
    
        uint256 _index = stakes.length;
    
        stakesOf[msg.sender].push(_index);
        stakes.push(Stake({
            class: _class,
            initialAmount: _amount,
            finalAmount: _finalAmount,
            timestamp: block.timestamp,
            unstaked: false
        }));

        ownerOf[_index] = msg.sender;
        emit Staked(msg.sender, _class, _amount, _finalAmount);
    }



    function unstake(uint256 _index) public whenNotPaused{
        require(msg.sender == ownerOf[_index], "Not correct index");
        Stake storage _s = stakes[_index];
        require(!_s.unstaked, "Already unstaked"); // not unstaked yet
        require(block.timestamp >= _s.timestamp + periods[_s.class], "Staking period not finished"); // staking period finished

        TKN.transfer(msg.sender, _s.finalAmount);
  
        _s.unstaked = true;
        emit Unstaked(msg.sender, _s.class, _s.finalAmount);
    }


    function increaseRewardsPool(uint256 _amount) public whenNotPaused{
        TKN.transferFrom(msg.sender, address(this), _amount);
        rewardsPool += _amount;
        emit IncreaseRewardsPool(msg.sender, _amount, rewardsPool);
    }


    // ------------------------ VIEW FUNCTIONS ----------------------------

    function myStakes(address _me) public view returns (Stake[] memory s, uint256[] memory indexes){
        s = new Stake[](stakesOf[_me].length);
        indexes = new uint256[](stakesOf[_me].length);

        for (uint256 i = 0; i < stakesOf[_me].length; i++){
            indexes[i] = stakesOf[_me][i];
            s[i] = stakes[indexes[i]];
        }
    }


    function myActiveStakesCount(address _me) public view returns (uint256 l) {
        uint256[] storage _s = stakesOf[_me];
        for (uint256 i = 0; i < _s.length; i++) if (!stakes[_s[i]].unstaked) l++;
    }


    function getMyStakesInfo(address _userAddress) public view returns (StakeInfo[] memory){
        Stake[] memory s = new Stake[](stakesOf[_userAddress].length);
        uint256[] memory _indexes = new uint256[](stakesOf[_userAddress].length);

        (s,_indexes) = myStakes(_userAddress);
        StakeInfo[] memory _stakesInfo = new StakeInfo[](stakesOf[_userAddress].length);

        for (uint i = 0; i < stakesOf[_userAddress].length; i++){
            _stakesInfo[i].period = secondsToDays(periods[s[i].class]);
            _stakesInfo[i].initialAmount = s[i].initialAmount;
            _stakesInfo[i].finalAmount = s[i].finalAmount;
            _stakesInfo[i].profit = _stakesInfo[i].finalAmount - _stakesInfo[i].initialAmount;
            _stakesInfo[i].rateAPY = rates[s[i].class];
            _stakesInfo[i].ratePerDay = _stakesInfo[i].rateAPY / 360;

            if (_stakesInfo[i].period != 0){
                _stakesInfo[i].pulsePerDay = _stakesInfo[i].profit / _stakesInfo[i].period;
            } else {
                _stakesInfo[i].pulsePerDay = 0;
            }

            _stakesInfo[i].startDate = timestampToDate(s[i].timestamp);
            _stakesInfo[i].endDate = timestampToDate(s[i].timestamp + periods[s[i].class]);
            
            if (s[i].timestamp + periods[s[i].class] < block.timestamp){
                _stakesInfo[i].daysLeft = "0";
            } else {
            _stakesInfo[i].daysLeft = string(abi.encodePacked(StringsUpgradeable.toString(uint(secondsToDays(s[i].timestamp + periods[s[i].class] - block.timestamp)))));
            }
            
            _stakesInfo[i].isWithdrawEnabled = (block.timestamp >= s[i].timestamp + periods[s[i].class]) && (!s[i].unstaked);
            _stakesInfo[i].isUnstaked = s[i].unstaked;
            _stakesInfo[i].stakeIndex = _indexes[i];
        }
        return _stakesInfo;
    }


    function getStartEndDates(uint8 _class) public view returns (string memory, string memory){
        return (
            timestampToDate(block.timestamp),
            timestampToDate(block.timestamp + periods[_class])
        );
    }


    function getRates() public view returns(uint32[5] memory){
        return rates;
    }


    function getRate(uint8 _class) public view returns(uint32){
        return rates[_class];
    }


    function getPeriods() public view returns(uint256[5] memory){
        return periods;
    }


    function getPeriod(uint8 _class) public view returns(uint256){
        return periods[_class];
    }


    function getPeriodsInDays() public view returns(uint32[5] memory){
        uint32[5] memory _temp;

        for(uint i=0; i<5; i++){
            _temp[i] = uint32(secondsToDays(periods[i]));
        }
        return _temp;
    }

  // --------------------- INTERNAL FUNCTIONS -----------------------
  
    // seconds to days
    function secondsToDays(uint256 _seconds) internal pure returns (uint32){
        return uint32(_seconds/86400);
    }


    // timestampToDate
    function timestampToDate(uint256 _timestamp) internal pure returns (string memory) {
        int __days = int(_timestamp/86400);

        int L = __days + 68569 + 2440588;
        int N = 4 * L / 146097;
        L = L - (146097 * N + 3) / 4;
        int _year = 4000 * (L + 1) / 1461001;
        L = L - 1461 * _year / 4 + 31;
        int _month = 80 * L / 2447;
        int _day = L - 2447 * _month / 80;
        L = _month / 11;
        _month = _month + 2 - 12 * L;
        _year = 100 * (N - 49) + _year + L;

        return string(abi.encodePacked(StringsUpgradeable.toString(uint(_day)),".", StringsUpgradeable.toString(uint(_month)),".",StringsUpgradeable.toString(uint(_year))));
    }   

}
