    "contracts/WorldLeagueLivePassNFT.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

// WLLADPFraction is a erc1155 contract that represents the fraction of the WLL NFT

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/utils/cryptography/draft-EIP712.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "./WLLCard.sol";

error TransferNotAllowed();
error SignerNotSet();
error InvalidSignature();
error NotAuthorizedToMint();
error AlreadyClaimed();

contract WorldLeagueLivePassNFT is ERC1155, EIP712, Ownable, Pausable {
    using SafeERC20 for IERC20;

    uint256 public constant SILVER = 0;
    uint256 public constant GOLD = 1;
    bytes32 internal constant EIP712_TYPE_HASH =
        keccak256("claim(address to,uint256 tokenId)");

    address private _signer;
    // ERC1155
    mapping(uint256 => string) private tokenURIs;

    // mint roles mapping, reserve for further use
    mapping(address => bool) public mintRoles;
    address public skinNFTAddress;

    // compaign id for pirate nft
    uint256 public pirateCampaignId = 0;

    constructor() ERC1155("") EIP712("WorldLeagueLivePassNFT", "1") {}

    function setSkinNFTAddress(address _skinNFTAddress) external onlyOwner {
        skinNFTAddress = _skinNFTAddress;
    }

    // airdrop to mutiple address in one transaction
    function batchAirdopWithSkin(
        address[] calldata accounts,
        uint256[] calldata tokenIds
    ) external {
        if (!mintRoles[msg.sender]) {
            revert NotAuthorizedToMint();
        }

        for (uint256 i = 0; i < accounts.length; i++) {
            if (balanceOf(accounts[i], tokenIds[i]) > 0) {
                // if user already claimed, we don't fire error here, just skip
                continue;
            }

            _mint(accounts[i], tokenIds[i], 1, "");

            // mint skin nft as well
            if (
                skinNFTAddress != address(0) &&
                WLLCard(payable(skinNFTAddress)).balanceOf(accounts[i]) == 0
            ) {
                WLLCard(payable(skinNFTAddress)).mintLegendaryCampaign(
                    accounts[i],
                    pirateCampaignId,
                    1
                );
            }
        }
    }

    function airdrop(address account, uint256 tokenId) external onlyOwner {
        _mint(account, tokenId, 1, "");

        // mint skin nft as well
        if (
            skinNFTAddress != address(0) &&
            WLLCard(payable(skinNFTAddress)).balanceOf(msg.sender) == 0
        ) {
            WLLCard(payable(skinNFTAddress)).mintLegendaryCampaign(
                account,
                pirateCampaignId,
                1
            );
        }
    }

    /// @notice user can claim nft by providing the signature
    function claim(
        uint256 tokenId,
        bytes memory signature
    ) external whenNotPaused {
        if (balanceOf(msg.sender, tokenId) > 0) {
            revert AlreadyClaimed();
        }

        if (!verify(msg.sender, tokenId, signature)) {
            revert InvalidSignature();
        }

        _mint(msg.sender, tokenId, 1, "");

        // mint skin nft as well
        if (
            skinNFTAddress != address(0) &&
            WLLCard(payable(skinNFTAddress)).balanceOf(msg.sender) == 0
        ) {
            WLLCard(payable(skinNFTAddress)).mintLegendaryCampaign(
                msg.sender,
                pirateCampaignId,
                1
            );
        }
    }

    /// @notice only permit address can mint
    function mint(
        address account,
        uint256 tokenId,
        uint256 quantity
    ) external whenNotPaused {
        if (!mintRoles[msg.sender]) {
            revert NotAuthorizedToMint();
        }

        _mint(account, tokenId, quantity, "");
    }

    /// @notice returns the uri metadata. Used by marketplaces and wallets to show the NFT
    function uri(uint256 tokenId) public view override returns (string memory) {
        return tokenURIs[tokenId];
    }

    // set token uris
    function changeURIs(
        string[] calldata _tokenURIs,
        uint256[] calldata _tokenIds
    ) external onlyOwner {
        for (uint256 i = 0; i < _tokenURIs.length; i++) {
            tokenURIs[_tokenIds[i]] = _tokenURIs[i];
        }
    }

    function changeSigner(address signer) external onlyOwner {
        _signer = signer;
    }

    function changeMintRoles(
        address[] calldata _mintRoles,
        bool[] calldata _mintRolesStatus
    ) external onlyOwner {
        for (uint256 i = 0; i < _mintRoles.length; i++) {
            mintRoles[_mintRoles[i]] = _mintRolesStatus[i];
        }
    }

    function setPirateCampaignId(uint256 _pirateCampaignId) external onlyOwner {
        pirateCampaignId = _pirateCampaignId;
    }

    // check if the token can be transferred before approving
    function setApprovalForAll(
        address operator,
        bool approved
    ) public override {
        revert("WorldLeagueLivePassNFT: SBT");
    }

    // implement _beforeTokenTransfer to check if transfer is allowed
    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal override {
        if ((from != address(0) && to != address(0))) {
            revert TransferNotAllowed();
        }

        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }

    // verify signature
    function verify(
        address to,
        uint256 tokenId,
        bytes memory signature
    ) public view returns (bool) {
        if (_signer == address(0)) {
            revert SignerNotSet();
        }

        bytes32 digest = _hashTypedDataV4(
            keccak256(abi.encode(EIP712_TYPE_HASH, to, tokenId))
        );

        return ECDSA.recover(digest, signature) == _signer;
    }

    // query balance of silver and gold
    function queryBalance(
        address account
    ) external view returns (uint256, uint256) {
        return (balanceOf(account, SILVER), balanceOf(account, GOLD));
    }
}
"
    }
  },
  "settings": {
    "optimizer": {
      "enabled": true,
      "runs": 1000
    },
