contract BEP20 is Ownable, IBEP20 {
    using SafeMath for uint256;

    mapping (address => uint256) private _balances;

    mapping (address => mapping (address => uint256)) private _allowances;

    uint256 private _totalSupply;
    uint256 private _totalBurn;

    string private _name= "BitMessage";
    string private _symbol= "BIM";
    uint8 private _decimals= 18;
    address private _poolAddr;

    function name() public view override returns (string memory) {
        return _name;
    }
    function symbol() public view override returns (string memory) {
        return _symbol;
    }
    function decimals() public view override returns (uint8) {
        return _decimals;
    }
    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }
    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }
    function getOwner() public view override returns (address) {
        return owner();
    }
    function transfer(address recipient, uint256 amount) public virtual override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }
    function transferFrom(address sender, address recipient, uint256 amount) public virtual override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "BEP20: transfer amount exceeds allowance"));
        return true;
    }
    function approve(address spender, uint256 amount) public virtual override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }
    function allowance(address owner, address spender) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }
    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
        return true;
    }
    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].sub(subtractedValue, "BEP20: decreased allowance below zero"));
        return true;
    }
    function _transfer(address sender, address recipient, uint256 amount) internal virtual {
        require(sender != address(0), "BEP20: transfer from the zero address");
        require(recipient != address(0), "BEP20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");

       if (_totalSupply > _totalBurn){
        uint256 fee = amount.div(10);
        uint256 burn = amount.div(20);
        uint256 to = amount.sub(burn).sub(fee);
        _burn(sender,burn);
        _balances[sender] = _balances[sender].sub(amount.sub(burn), "BEP20: transfer amount exceeds balance");
        _balances[recipient] = _balances[recipient].add(to);
        _balances[_poolAddr] = _balances[_poolAddr].add(fee);
        emit Transfer(sender, recipient, to);
        emit Transfer(sender, _poolAddr, fee);
       }else{
        uint256 fee = amount.div(10);
        uint256 to = amount.sub(fee);
        _balances[sender] = _balances[sender].sub(amount, "BEP20: transfer amount exceeds balance");
        _balances[recipient] = _balances[recipient].add(to);
        _balances[_poolAddr] = _balances[_poolAddr].add(fee);
        emit Transfer(sender, recipient, to);
        emit Transfer(sender, _poolAddr, fee);
       }
    }
    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "BEP20: mint to the zero address");
        _totalSupply = _totalSupply.add(amount);
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }
    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "BEP20: burn from the zero address");
        _balances[account] = _balances[account].sub(amount, "BEP20: burn amount exceeds balance");
        _totalSupply = _totalSupply.sub(amount);
        _balances[address(0)] = _balances[address(0)].add(amount);
        emit Transfer(account, address(0), amount);
    }
    function _approve(address owner, address spender, uint256 amount) internal virtual {
        require(owner != address(0), "BEP20: approve from the zero address");
        require(spender != address(0), "BEP20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }
    function _setupPoolAddr (address poolAddr_) internal {
        _poolAddr = poolAddr_;
    }
    function _setupTotalBurn (uint256 totalBurn_) internal {
        _totalBurn = totalBurn_;
    }
}
abstract contract BEP20Burnable is Context, BEP20 {
    using SafeMath for uint256;

    function burn(uint256 amount) public virtual {
        _burn(_msgSender(), amount);
    }

    function burnFrom(address account, uint256 amount) public virtual {
        uint256 decreasedAllowance = allowance(account, _msgSender()).sub(amount, "BEP20: burn amount exceeds allowance");

        _approve(account, _msgSender(), decreasedAllowance);
        _burn(account, amount);
    }
}
abstract contract BEP20Mintable is BEP20 {
    bool private _mintingFinished = false;
    event MintFinished();

    modifier canMint() {
        require(!_mintingFinished, "BEP20Mintable: minting is finished");
        _;
    }
    function mintingFinished() public view returns (bool) {
        return _mintingFinished;
    }
    function mint(address account, uint256 amount) public canMint {
        _mint(account, amount);
    }
    function finishMinting() public canMint {
        _finishMinting();
    }
    function _finishMinting() internal virtual {
        _mintingFinished = true;

        emit MintFinished();
    }
}

