    address public constant backedLPAddress = address(0xf15A72B15fC4CAeD6FaDB1ba7347f6CCD1E0Aede); //TRUNK/BUSD LP
    address public constant routerAddress = address(0x10ED43C718714eb63d5aA57B78B54704E256024E); 
    address public constant flowDataAddress = address(0x4C64719E524383662232FDb50dfdaDEFB15c09D9); //FlowData 

}

interface IRaffle {
    
    function add(address participant, uint256 amount) external;

}

interface IReferralData {
    ///@dev Return the referral of a participant 
    function referrerOf(address participant) external view returns (address);
}

contract ElephantRouterProxy is Ownable {
    using SafeMath for uint256;
    using Address for address;


    uint256 public processingFee = 85;
    uint256 public transferFee = 35;
    uint256 public constant referralFee = 5;
    uint256 public constant precision = 1000;

    IReferralData public referralData;
    
    AddressRegistry private registry;
    
    IERC20 private coreToken;
    IUniswapV2Router02 public  collateralRouter;

    event UpdateProcessingFee(uint prevFee, uint fee);
    event UpdateTransferFee(uint prevFee, uint fee );
    event UpdateCollateralRouter(address router);
    event UpdateReferralData(address indexed addr);

    //After construction the reserve needs to be added to the whitelist of the treasuries and the backedToken
    constructor () public Ownable() {

        //init reg
        registry = new AddressRegistry();

        //the collateral router can be upgraded in the future
        collateralRouter = IUniswapV2Router02(registry.routerAddress());

        //setup tokens
        coreToken = IERC20(registry.coreAddress());
      
    }

    receive() payable external {}

    function updateReferralData(address referralDataAddress) onlyOwner external {
    require(referralDataAddress != address(0), "Require valid non-zero addresses");

    referralData = IReferralData(referralDataAddress);

    emit UpdateReferralData(referralDataAddress);
  }



    //Update the processingFee
    function updateProcessingFee(uint fee) onlyOwner external {
        require(fee > 1 && fee <= 100, "Fee must be 1 - 100");

        emit UpdateProcessingFee(processingFee, fee);

        processingFee = fee;
    }

    //Update the transferFee
    function updateTransferFee(uint fee) onlyOwner external {
        require(fee > 1 && fee <= 100, "Fee must be 1 - 100");

        emit UpdateTransferFee(transferFee, fee);

        transferFee = fee;
    }

    //Core collateral liquidity can move from one contract location to another across major PCS releases
    function updateCollateralRouter(address _router) onlyOwner external {
        require(_router != address(0), "Router must be set");
        collateralRouter = IUniswapV2Router02(_router);

        emit UpdateCollateralRouter(_router);
        
    }


    function swapExactETH(
        uint amountOutMin,
        address to
    ) external payable returns (uint totalAmount, uint tradeAmount, uint feeAmount, uint referralAmount, uint treasuryAmount) {
    
        address[] memory path = new address[](2);
        path[0] = collateralRouter.WETH();
        path[1] = registry.coreAddress();
        

        uint initialBalance = coreToken.balanceOf(address(this));

        //auto slippage
        collateralRouter.swapExactETHForTokensSupportingFeeOnTransferTokens{value:msg.value}(amountOutMin, path, address(this), block.timestamp);

        //Process referral
        address _referrer = referralData.referrerOf(_msgSender());

        //adjust processingFee to incentivize getting a partner
        uint _processingFee = (_referrer != address(0)) ? processingFee : processingFee.add(referralFee);

        totalAmount = coreToken.balanceOf(address(this)).sub(initialBalance);
        feeAmount = totalAmount.mul(_processingFee).div(precision);
        tradeAmount  = totalAmount.sub(feeAmount);

        //Need to have an upline
        if (_referrer != address(0)){
            referralAmount = totalAmount.mul(referralFee).div(precision);
            treasuryAmount = feeAmount.sub(referralAmount);

            //referral transfer
            coreToken.transfer(_referrer, referralAmount);
        } else {
            treasuryAmount = feeAmount;
        }

        //fund treasury
        coreToken.transfer(registry.coreTreasuryAddress(), treasuryAmount);
        //send tokens
        coreToken.transfer(to, tradeAmount);

    }

    function swapExactTokens(
        uint amountIn,
        uint amountOutMin,
        address payable to
    ) external returns (uint tradeAmount, uint feeAmount, uint wethAmount, uint referralAmount, uint treasuryAmount) {
        address[] memory path = new address[](2);
        path[0] = address(coreToken);
        path[1] = collateralRouter.WETH();

        require(coreToken.transferFrom(_msgSender(),address(this), amountIn), "Core token must be approved and available");

        uint initialBalance = address(this).balance;

        //Process referral
        address _referrer = referralData.referrerOf(_msgSender());

        //adjust processingFee to incentivize getting a partner
        uint _processingFee = (_referrer != address(0)) ? processingFee : processingFee.add(referralFee);

        feeAmount = amountIn.mul(_processingFee).div(precision);

        tradeAmount = amountIn.sub(feeAmount);

        coreToken.approve(address(collateralRouter), tradeAmount);

        collateralRouter.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tradeAmount,
            amountOutMin,
            path,
            address(this),
            block.timestamp
        );   

        wethAmount = address(this).balance.sub(initialBalance);

        //Need to have an upline
        if (_referrer != address(0)){
            referralAmount = amountIn.mul(referralFee).div(precision);
            treasuryAmount = feeAmount.sub(referralAmount);

            //referral transfer
            coreToken.transfer(_referrer, referralAmount);
        } else {
            treasuryAmount = feeAmount;
        }


        //transfer to treasury
        coreToken.transfer(registry.coreTreasuryAddress(), treasuryAmount);

        //transfer weth
        to.transfer(wethAmount); 

    }

    function transferExactTokens(
        uint amount,
        address to
    ) external returns (uint transferAmount, uint feeAmount, uint referralAmount, uint treasuryAmount) {
       
        require(coreToken.transferFrom(_msgSender(),address(this), amount), "Core token must be approved and available");

        //Process referral
        address _referrer = referralData.referrerOf(_msgSender());

        //adjust processingFee to incentivize getting a partner
        uint _transferFee = (_referrer != address(0)) ? transferFee : transferFee.add(referralFee);


        feeAmount = amount.mul(_transferFee).div(precision);
        transferAmount = amount.sub(feeAmount);

        //Need to have an upline
        if (_referrer != address(0)){
            referralAmount = amount.mul(referralFee).div(precision);
            treasuryAmount = feeAmount.sub(referralAmount);

            //referral transfer
            coreToken.transfer(_referrer, referralAmount);
        } else {
            treasuryAmount = feeAmount;
        }

        //transfer to treasury
        coreToken.transfer(registry.coreTreasuryAddress(), treasuryAmount);

        //transfer remaining
        coreToken.transfer(to, transferAmount); 

    }

    // This function is sensitive to slippage and that isn't a bad thing...
    // Estimates the amount of  core tokens getting transfered to USD collateral tokens
    function estimateCoreToCollateral(uint coreAmount) private view returns (uint collateralAmount) {
         //Convert from core to WETH using the core's Oracle
        address[] memory path = new address[](3);
        path[0] = registry.coreAddress();
        path[1] = collateralRouter.WETH();
        path[2] = registry.collateralAddress();

        uint[] memory amounts = collateralRouter.getAmountsOut(coreAmount, path);
        
        collateralAmount = amounts[2];
    }

}
