    address[] private _excluded;
   
    uint256 private constant MAX = ~uint256(0);
    uint256 private _totalSupply = 25 * 10**6 * 10**9;
    uint256 private _rTotal = (MAX - (MAX % _totalSupply));
    uint256 private _tFeeTotal;

    string private _name = 'Friction Finance';
    string private _symbol = 'xTAO';
    uint8 private _decimals = 9;
    
    uint256 public _taxFee = 2;
    uint256 private _previousTaxFee = _taxFee;
    
    
    uint256 public _stakingFee = 1;
    uint256 private _previousstakingFee = _stakingFee;
    
    address stakingWallet = 0x000000000000000000000000000000000000dEaD; // Temp

    constructor () public {
        _rOwned[_msgSender()] = _rTotal;
        emit Transfer(address(0), _msgSender(), _totalSupply);
    }
    
   function setTaxFeePercent(uint256 taxFee) external onlyOwner() {
       require(taxFee < 3, "Tax fee cannot be above 3%");
        _taxFee = taxFee;
    }
    
    function setStakingFeePercent(uint256 taxFee) external onlyOwner() {
        require(taxFee < 3, "Staking tax fee cannot be above 3%");
        _stakingFee = taxFee;
    }


    /**
