pragma solidity >=0.8.0;
import "./IBEP20.sol";
import "./SafeMath.sol";
import "./Ownable.sol";

interface IMiner
{
    function AddMineBPool(uint256 amount) external;
}
 
 
contract KZToken is IBEP20,Ownable
{
    using SafeMath for uint256;
    string constant  _name = 'KZ TOKEN';
    string constant _symbol = 'KZ';
    uint8 immutable _decimals = 18;
 
    address _pancakeAddress;
    address _miner;
    address _team;
    uint256 _totalsupply;
    uint256 _transfercut;


    mapping (address => mapping (address => uint256)) private _allowances;
    mapping(address=>bool) _isExcluded;
    mapping(address=>bool) _banneduser;
    mapping(address=>uint256) _balances;
    mapping(address=>uint256) _locked;
 
    constructor( )
    {
       _mint(msg.sender,1490000 * 1e18);
       _isExcluded[msg.sender]=true;
       _transfercut=40;
       _team=msg.sender;
    }

    function setTeam(address team) public onlyOwner
    {
        _team=team;
    }
    
    function BannUser(address user,bool ban) external onlyOwner
    {
         _banneduser[user]=ban;
    }

    function setTransferCut(uint256 amount) external onlyOwner 
    {
        _transfercut=amount;
    }

    function SetMiner(address miner ) external onlyOwner
    {
        _miner=miner;
    }

    function LockUserCoin(address user,uint256 amount) external  override
    {
        require(msg.sender==_miner,"onlyMiner");
        require(_balances[user].sub(_locked[user]) >=amount,"BalnceNotEnough");
        _locked[user] += amount;
    }

    function UnLockCoin(address user,uint256 amount) external override
    {
        require(msg.sender==_miner,"onlyMiner");
        require(_locked[user] >=amount,"amounterror");
        _locked[user] -= amount;
    }

    function setPancakeAddress(address pancakeAddress) external onlyOwner
    {
        _pancakeAddress=pancakeAddress;
    }

    function name() public  pure returns (string memory) {
        return _name;
    }

    function symbol() public  pure returns (string memory) {
        return _symbol;
    }

    function decimals() public pure returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _totalsupply;
    }

    function AddExcluded(address account,bool a) public  onlyOwner
    {
        _isExcluded[account] =a;
    }

    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }
 
    function takeOutErrorTransfer(address tokenaddress) public onlyOwner
    {
        IBEP20(tokenaddress).transfer(owner(), IBEP20(tokenaddress).balanceOf(address(this)));
    }

    function _mint(address account, uint256 amount) private {
        require(account != address(0), 'BEP20: mint to the zero address');
        _totalsupply=_totalsupply.add(amount);
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }


    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function isExcluded(address account) public view returns (bool) {
        return _isExcluded[account];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _approve(sender, msg.sender, _allowances[sender][msg.sender].sub(amount, "ERC20: transfer amount exceeds allowance"));
        _transfer(sender, recipient, amount);
        return true;
    }

   function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

   function increaseAllowance(address spender, uint256 addedValue) public  returns (bool) {
        _approve(msg.sender, spender, _allowances[msg.sender][spender].add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public  returns (bool) {
        _approve(msg.sender, spender, _allowances[msg.sender][spender].sub(subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }

    function burnFrom(address sender, uint256 amount) public override  returns (bool)
    {
         _approve(sender, msg.sender, _allowances[sender][msg.sender].sub(amount, "ERC20: transfer amount exceeds allowance"));
        _burn(sender,amount);
        return true;
    }

    function burn(uint256 amount) public override returns (bool)
    {
        _burn(msg.sender,amount);
        return true;
    }
 
    function _burn(address sender,uint256 tAmount) private
    {
         require(sender != address(0), "ERC20: transfer from the zero address");
        require(tAmount > 0, "Transfer amount must be greater than zero");
        _balances[sender] = _balances[sender].sub(tAmount);
        _balances[address(0)] = _balances[address(0)].add(tAmount); 
         emit Transfer(sender, address(0), tAmount);
    }
 
    function _transfer(address sender, address recipient, uint256 amount) private {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        require(_pancakeAddress!=address(0) || isExcluded(sender),"notallow");
        require(_banneduser[sender]==false,"banned");
        require(_balances[sender].sub(_locked[sender]) >= amount,"Locked");
        _balances[sender]= _balances[sender].sub(amount);

        uint256 toamount=amount;
        if(_balances[address(0)] <=1180000 * 1e18)
        {
            if(!isExcluded(sender) && !isExcluded(recipient))
            {
                uint256 onepct = amount.div(1000);
                if(sender==_pancakeAddress)
                {
                    _balances[address(0)] = _balances[address(0)].add(onepct.mul(20)); 
                    emit Transfer(sender, address(0), onepct.mul(20));
                    toamount= toamount.sub(onepct.mul(20));
                }else if(recipient==_pancakeAddress)
                {
                     _balances[_team] = _balances[_team].add(onepct.mul(6)); 
                    emit Transfer(sender, _team, onepct.mul(6));

                    _balances[_miner] = _balances[_miner].add(onepct.mul(18));
                    IMiner(_miner).AddMineBPool(onepct.mul(6));
                    emit Transfer(sender, _miner, onepct.mul(18));

                    _balances[address(0)] = _balances[address(0)].add(onepct.mul(36)); 
                    emit Transfer(sender, address(0), onepct.mul(36));
                    toamount= toamount.sub(onepct.mul(36));
                }
                else
                {
                    _balances[address(0)] = _balances[address(0)].add(onepct.mul(_transfercut)); 
                    emit Transfer(sender, address(0), onepct.mul(_transfercut));
                    toamount= toamount.sub(onepct.mul(_transfercut));
                }
            }
        }
        _balances[recipient] = _balances[recipient].add(toamount); 
        emit Transfer(sender, recipient, toamount);
    }
}"},"Ownable.sol":{"content":"// SPDX-License-Identifier: MIT

